import heapq
def k_nearest_neighbors(points, query, k=3):

    def build_tree(points, level=0):
        if not points:
            return None
        axis = level % len(points[0])
        points = sorted(points, key=lambda x: x[axis])
        median_index = len(points) // 2
        node_point_index = median_index
        node = {'point_index': node_point_index, 'axis': axis, 'left': build_tree(points[:median_index], level + 1) if median_index > 0 else None, 'right': build_tree(points[median_index + 1:], level + 1) if median_index < len(points) - 1 else None}
        return node

    def squared_distance(point1, point2):
        return sum(((a - b) ** 2 for (a, b) in zip(point1, point2)))

    def knn_search(node, query, k, best):
        if node is None:
            return best
        point_index = node['point_index']
        point = points[point_index]
        dist = squared_distance(query, point)
        heapq.heappush(best, (dist, point_index))
        if len(best) > k:
            heapq.heappop(best)
        axis = node['axis']
        if query[axis] < points[node['point_index']][axis]:
            best = knn_search(node['left'], query, k, best)
            if len(best) < k or best[0][0] > abs(query[axis] - points[node['point_index']][axis]) ** 2:
                best = knn_search(node['right'], query, k, best)
        else:
            best = knn_search(node['right'], query, k, best)
            if len(best) < k or best[0][0] > abs(query[axis] - points[node['point_index']][axis]) ** 2:
                best = knn_search(node['left'], query, k, best)
        return best
    if not points:
        return []
    tree = build_tree(points)
    best = []
    best = knn_search(tree, query, k, best)
    return [x[1] for x in sorted(best)]

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    current_node = root
    while True:
        if hasattr(current_node, 'y'):
            return current_node.y
        elif task_type == 'classification':
            feature_value = features[current_node.label]
            matching_child = next((child for child in current_node.child if child.x == feature_value), None)
            if matching_child is not None:
                current_node = matching_child
            else:
                current_node = current_node.child[1]
        elif task_type == 'regression':
            feature_value = features[current_node.label]
            if feature_value <= current_node.s:
                current_node = current_node.child[0]
            else:
                current_node = current_node.child[1]

from collections import defaultdict
from typing import List, Tuple, Set
def naive_bayes_predict(train_data: List[Tuple[str, List[str]]], keywords: Set[str], test_samples: List[List[str]]) -> List[str]:
    """Build an unsmoothed Bernoulli Naive Bayes text classifier and predict labels.

    The classifier treats every word in *keywords* as a binary feature that is
    either present or absent in a document.  Words outside *keywords* are
    ignored.  Training follows the standard Bernoulli model *without* any form
    of smoothing, and *no* class prior is used when computing the likelihoods.

    Args:
        train_data: List of ``(label, tokens)`` pairs forming the training set.
        keywords:   Set of words that act as binary features.
        test_samples: List of token lists (one per unseen document).

    Returns:
        List of predicted labels for the documents in *test_samples* (same
        order as input).
    """
    keyword_counts = defaultdict(lambda : defaultdict(int))
    class_counts = defaultdict(int)
    for (label, tokens) in train_data:
        class_counts[label] += 1
        for token in tokens:
            if token in keywords:
                keyword_counts[label][token] += 1
    probabilities = {}
    for (label, count) in class_counts.items():
        probabilities[label] = {}
        for keyword in keywords:
            probabilities[label][keyword] = keyword_counts[label][keyword] / count
    predicted_labels = []
    for test_sample in test_samples:
        test_sample_keywords = set(test_sample) & keywords
        best_label = None
        best_likelihood = -1
        for (label, probs) in probabilities.items():
            likelihood = 1
            for keyword in keywords:
                if keyword in test_sample_keywords:
                    likelihood *= probs.get(keyword, 0)
                else:
                    likelihood *= 1 - probs.get(keyword, 0)
            if likelihood > best_likelihood:
                best_likelihood = likelihood
                best_label = label
        predicted_labels.append(best_label)
    return predicted_labels
