def best_split(feature, target):
    """Determine the numerical threshold that produces the highest information gain.

    Parameters
    ----------
    feature : list[int | float] | 1-D numpy.ndarray
        Numerical values of a single attribute.
    target  : list[int] | 1-D numpy.ndarray
        Corresponding class labels.

    Returns
    -------
    tuple
        (threshold, information_gain) where
        * threshold – float rounded to 4 decimals or None when no useful split exists;
        * information_gain – float rounded to 4 decimals.
    """
    if hasattr(feature, 'tolist'):
        feature = feature.tolist()
    if hasattr(target, 'tolist'):
        target = target.tolist()
    n = len(feature)
    if n == 0:
        return (None, 0.0)
    sorted_pairs = sorted(zip(feature, target), key=lambda x: x[0])
    (sorted_feature, sorted_target) = zip(*sorted_pairs)
    thresholds = []
    for i in range(n - 1):
        if sorted_feature[i] != sorted_feature[i + 1]:
            t = (sorted_feature[i] + sorted_feature[i + 1]) / 2
            thresholds.append(t)
    if not thresholds:
        return (None, 0.0)

    def entropy(labels):
        count = Counter(labels)
        total = len(labels)
        if total == 0:
            return 0.0
        ent = 0.0
        for cnt in count.values():
            p = cnt / total
            if p > 0:
                ent -= p * math.log2(p)
        return ent
    H_parent = entropy(target)
    if H_parent == 0:
        return (None, 0.0)
    max_IG = -1
    best_t = None
    for t in thresholds:
        left = []
        right = []
        for (x, y) in sorted_pairs:
            if x < t:
                left.append(y)
            else:
                right.append(y)
        H_left = entropy(left)
        H_right = entropy(right)
        IG = H_parent - len(left) / n * H_left - len(right) / n * H_right
        if IG > max_IG or (IG == max_IG and (best_t is None or t < best_t)):
            max_IG = IG
            best_t = t
    if max_IG <= 0:
        return (None, 0.0)
    return (round(best_t, 4), round(max_IG, 4))
