def inorder_keys(pairs):
    """Build a BST from (key, value) *pairs* and return keys from an in-order traversal.

    Args:
        pairs (list[tuple[Any, int | float]]): List of (key, numeric value) pairs.

    Returns:
        list: Keys in in-order (ascending by numeric value). Return an empty
        list if *pairs* is empty.
    """
    if not pairs:
        return []

    class Node:

        def __init__(self, key, val):
            self.key = key
            self.val = val
            self.left = None
            self.right = None

        def __lt__(self, other):
            return self.val < other.val

        def __le__(self, other):
            return self.val <= other.val

        def __gt__(self, other):
            return self.val > other.val

        def __ge__(self, other):
            return self.val >= other.val
    root = Node(pairs[0][0], pairs[0][1])

    def insert(node, new_node):
        current = node
        while True:
            if new_node < current:
                if current.left is None:
                    current.left = new_node
                    break
                else:
                    current = current.left
            elif current.right is None:
                current.right = new_node
                break
            else:
                current = current.right
    for (key, val) in pairs[1:]:
        new_node = Node(key, val)
        insert(root, new_node)

    def inorder_traversal(node, result):
        if node:
            inorder_traversal(node.left, result)
            result.append(node.key)
            inorder_traversal(node.right, result)
    keys_in_order = []
    inorder_traversal(root, keys_in_order)
    return keys_in_order
