def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    try:
        X_np = np.array(X, dtype=float)
        if X_np.ndim != 2:
            return -1
        (N, C) = X_np.shape
        if Y is None:
            Y_np = X_np
        else:
            Y_np = np.array(Y, dtype=float)
            if Y_np.ndim != 2:
                return -1
            if Y_np.shape[1] != C:
                return -1
        if sigma is None:
            sigma_val = np.sqrt(C / 2)
            if sigma_val <= 0:
                return -1
            inv_sigma_sq = 1 / sigma_val ** 2
        elif isinstance(sigma, (float, int)):
            sigma_val = float(sigma)
            if sigma_val <= 0:
                return -1
            inv_sigma_sq = 1 / sigma_val ** 2
        elif isinstance(sigma, list):
            if len(sigma) != C:
                return -1
            sigma_array = np.array(sigma, dtype=float)
            if np.any(sigma_array <= 0):
                return -1
            inv_sigma_sq = 1 / sigma_array ** 2
        else:
            return -1
        diff = X_np[:, np.newaxis, :] - Y_np[np.newaxis, :, :]
        diff_sq = diff ** 2
        if isinstance(inv_sigma_sq, np.ndarray):
            scaled_diff_sq = diff_sq * inv_sigma_sq
        else:
            scaled_diff_sq = diff_sq * inv_sigma_sq
        sum_scaled_diff_sq = np.sum(scaled_diff_sq, axis=2)
        K = np.exp(-0.5 * sum_scaled_diff_sq)
        K_rounded = np.round(K, 4)
        return K_rounded.tolist()
    except:
        return -1
