def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """

    class FPTreeNode:

        def __init__(self, item, count, parent):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = {}
            self.link = None

        def increment(self, count):
            self.count += count

    class FPTree:

        def __init__(self):
            self.root = FPTreeNode(None, 1, None)
            self.header_table = {}

        def add_tree(self, items, count):
            current_node = self.root
            for item in items:
                if item in current_node.children:
                    current_node.children[item].increment(count)
                else:
                    new_node = FPTreeNode(item, count, current_node)
                    current_node.children[item] = new_node
                    if item in self.header_table:
                        last_node = self.header_table[item]
                        while last_node.link:
                            last_node = last_node.link
                        last_node.link = new_node
                    else:
                        self.header_table[item] = new_node
                current_node = current_node.children[item]

    def build_fp_tree(transactions, min_sup):
        item_counts = Counter()
        for transaction in transactions:
            item_counts.update(transaction)
        freq_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
        if not freq_items:
            return (None, None)
        sorted_items = sorted(freq_items.items(), key=lambda x: (-x[1], x[0]))
        item_order = {item: idx for (idx, (item, _)) in enumerate(sorted_items)}
        tree = FPTree()
        for transaction in transactions:
            filtered = [item for item in transaction if item in freq_items]
            sorted_transaction = sorted(filtered, key=lambda item: (-freq_items[item], item))
            if sorted_transaction:
                tree.add_tree(sorted_transaction, 1)
        return (tree, freq_items)

    def ascend_tree(node):
        path = []
        while node.parent and node.parent.item is not None:
            node = node.parent
            path.append(node.item)
        return path

    def find_conditional_pattern_base(node):
        conditional_patterns = []
        while node:
            path = ascend_tree(node)
            if path:
                conditional_patterns.append((path, node.count))
            node = node.link
        return conditional_patterns

    def mine_tree(tree, prefix, frequent_itemsets, min_sup):
        items = sorted(tree.header_table.items(), key=lambda x: (freq_order[x[0]], x[0]))
        for (item, node) in items:
            new_prefix = prefix.copy()
            new_prefix.append(item)
            frequent_itemsets.append(tuple(sorted(new_prefix)))
            conditional_patterns = find_conditional_pattern_base(node)
            conditional_counts = Counter()
            for (path, count) in conditional_patterns:
                conditional_counts.update(dict.fromkeys(path, count))
            conditional_freq = {i: c for (i, c) in conditional_counts.items() if c >= min_sup}
            if not conditional_freq:
                continue
            sorted_conditional = sorted(conditional_freq.items(), key=lambda x: (-x[1], x[0]))
            ordered_conditional = {item: idx for (idx, (item, _)) in enumerate(sorted_conditional)}
            conditional_transactions = []
            for (path, count) in conditional_patterns:
                filtered = [i for i in path if i in conditional_freq]
                sorted_path = sorted(filtered, key=lambda x: (-conditional_freq[x], x))
                if sorted_path:
                    conditional_transactions.append((sorted_path, count))
            conditional_tree = FPTree()
            for (sorted_path, count) in conditional_transactions:
                conditional_tree.add_tree(sorted_path, count)
            mine_tree(conditional_tree, new_prefix, frequent_itemsets, min_sup)
    (tree, freq_items) = build_fp_tree(transactions, min_sup)
    if not tree or not tree.header_table:
        return []
    freq_order = {item: count for (item, count) in freq_items.items()}
    frequent_itemsets = [tuple([item]) for item in sorted(freq_items.keys())]
    mine_tree(tree, [], frequent_itemsets, min_sup)
    unique_itemsets = sorted(set(frequent_itemsets), key=lambda x: (len(x), x))
    return unique_itemsets
