import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    import math
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    if sample_size > n_samples:
        sample_size = n_samples
    if sample_size > 1:
        phi = 2 * math.log(sample_size - 1) - 2 * (sample_size - 1) / sample_size
    else:
        phi = 0

    def c(n):
        if n <= 1:
            return 0
        else:
            return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    class Tree:

        def __init__(self, data_subset, height_limit):
            self.height_limit = height_limit
            self.n_features = data_subset.shape[1]
            self.root = self.build_tree(data_subset, current_depth=0)

        def build_tree(self, data_subset, current_depth):
            if current_depth >= self.height_limit or data_subset.shape[0] <= 1:
                return {'leaf': True, 'size': data_subset.shape[0]}
            else:
                feature = np.random.randint(0, self.n_features)
                feature_values = data_subset[:, feature]
                min_val = feature_values.min()
                max_val = feature_values.max()
                if min_val == max_val:
                    return {'leaf': True, 'size': data_subset.shape[0]}
                threshold = np.random.uniform(min_val, max_val)
                left_indices = data_subset[:, feature] < threshold
                right_indices = data_subset[:, feature] >= threshold
                left = data_subset[left_indices]
                right = data_subset[right_indices]
                if left.shape[0] == 0 or right.shape[0] == 0:
                    return {'leaf': True, 'size': data_subset.shape[0]}
                return {'leaf': False, 'feature': feature, 'threshold': threshold, 'left': self.build_tree(left, current_depth + 1), 'right': self.build_tree(right, current_depth + 1)}

        def path_length(self, x):
            node = self.root
            path = 0
            while not node.get('leaf', False):
                path += 1
                feature = node['feature']
                threshold = node['threshold']
                if x[feature] < threshold:
                    node = node['left']
                else:
                    node = node['right']
            size = node['size']
            path += c(size)
            return path
    height_limit = math.ceil(math.log2(sample_size)) if sample_size > 0 else 0
    trees = []
    for _ in range(n_trees):
        if sample_size >= n_samples:
            sample_indices = np.arange(n_samples)
        else:
            sample_indices = np.random.choice(n_samples, size=sample_size, replace=False)
        data_subset = data[sample_indices]
        tree = Tree(data_subset, height_limit)
        trees.append(tree)
    path_lengths = np.zeros((n_samples, n_trees))
    for (tree_idx, tree) in enumerate(trees):
        for i in range(n_samples):
            x = data[i]
            h = tree.path_length(x)
            path_lengths[i, tree_idx] = h
    avg_h = path_lengths.mean(axis=1)
    with np.errstate(divide='ignore', invalid='ignore'):
        scores = np.power(2, -avg_h / phi) if phi != 0 else np.zeros(n_samples)
    if epsilon == 0:
        return []
    else:
        num_outliers = math.ceil(epsilon * n_samples)
        if num_outliers == 0:
            return []
        sorted_indices = np.argsort(-scores, kind='stable')
        top_indices = sorted_indices[:num_outliers]
        return sorted(top_indices.tolist())
