import math
from collections import Counter
from itertools import combinations
from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                      itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count. If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    N = len(transactions)
    min_support_count = math.ceil(minsup * N)
    transaction_list = [set(transaction) for transaction in transactions]
    frequent_itemsets = {}
    item_counts = Counter()
    for transaction in transaction_list:
        for item in transaction:
            item_counts[item] += 1
    current_frequent = {(item,): count for (item, count) in item_counts.items() if count >= min_support_count}
    frequent_itemsets.update(current_frequent)
    k = 2
    while current_frequent:
        candidates = set()
        items_prev_level = sorted([itemset for itemset in current_frequent.keys()])
        for i in range(len(items_prev_level)):
            for j in range(i + 1, len(items_prev_level)):
                itemset1 = items_prev_level[i]
                itemset2 = items_prev_level[j]
                if itemset1[:-1] == itemset2[:-1]:
                    new_candidate = tuple(sorted(set(itemset1) | set(itemset2)))
                    if len(new_candidate) == k:
                        candidates.add(new_candidate)
                else:
                    break
        candidate_counts = Counter()
        for transaction in transaction_list:
            for candidate in candidates:
                if set(candidate).issubset(transaction):
                    candidate_counts[candidate] += 1
        current_frequent = {candidate: count for (candidate, count) in candidate_counts.items() if count >= min_support_count}
        frequent_itemsets.update(current_frequent)
        k += 1
    return frequent_itemsets

def check_data(a, b):
    """Validate and convert the two inputs so that both are numeric.

    The function must accept integers, floats, or numeric strings. Any string
    should be converted with `float()`; existing numeric values stay as they
    are. The pair `(a, b)` must then be returned.
    """
    a = float(a) if isinstance(a, str) else a
    b = float(b) if isinstance(b, str) else b
    return (a, b)
def validate_input(function):

    def wrapper(a, b):
        (a, b) = check_data(a, b)
        return function(a, b)
    return wrapper
@validate_input
def sum_numbers(a, b):
    """Return the arithmetic sum of *a* and *b* after validation."""
    total = a + b
    return int(total) if isinstance(total, float) and total.is_integer() else total

from typing import List, Union
Number = Union[int, float]
def k_smallest(nums: List[Number], k: int) -> List[Number]:
    """Return the k smallest numbers contained in *nums*.

    The function should scan the input list once, maintaining a custom
    max-heap (size at most k) implemented with a Python list.  At the end,
    return the k smallest elements in ascending order.  If *k* is invalid
    (≤0 or larger than len(nums)) return -1.

    The use of the built-in ``heapq`` module is **not** allowed.
    """
    if k <= 0 or k > len(nums):
        return -1
    heap = []

    def _move_up(heap: List[Number], index: int) -> None:
        """Move the element at index up to maintain max-heap property."""
        while index > 0:
            parent = (index - 1) // 2
            if heap[index] > heap[parent]:
                (heap[index], heap[parent]) = (heap[parent], heap[index])
                index = parent
            else:
                break

    def _move_down(heap: List[Number], index: int, size: int) -> None:
        """Move the element at index down to maintain max-heap property."""
        while True:
            largest = index
            left = 2 * index + 1
            right = 2 * index + 2
            if left < size and heap[left] > heap[largest]:
                largest = left
            if right < size and heap[right] > heap[largest]:
                largest = right
            if largest != index:
                (heap[index], heap[largest]) = (heap[largest], heap[index])
                index = largest
            else:
                break
    for num in nums:
        if len(heap) < k:
            heap.append(num)
            _move_up(heap, len(heap) - 1)
        elif num < heap[0]:
            heap[0] = num
            _move_down(heap, 0, len(heap))
    return sorted(heap)

import numpy as np
def softplus(x, deriv: bool=False):
    """
    Compute the SoftPlus activation function or its derivative (sigmoid).

    Parameters:
    - x: A scalar, list, or NumPy array of real numbers.
    - deriv: Boolean flag indicating whether to compute the derivative (sigmoid).
             Defaults to False.

    Returns:
    - A list of rounded results to 4 decimal places, or a single float if input is scalar.
    """
    is_scalar = np.isscalar(x)
    x_array = np.array([x]) if is_scalar else np.array(x)
    if not deriv:
        softplus_vals = np.where(x_array > 0, x_array + np.log1p(np.exp(-x_array)), np.log1p(np.exp(x_array)))
        result = np.round(softplus_vals, 4)
    else:
        sigmoid_vals = np.where(x_array >= 0, 1 / (1 + np.exp(-x_array)), np.exp(x_array) / (1 + np.exp(x_array)))
        result = np.round(sigmoid_vals, 4)
    if is_scalar:
        return float(result[0])
    else:
        return result.tolist()

import numpy as np
from collections import Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*.

    Your task is to complete this function so that it builds N-gram count
    tables from *corpus* and then returns the total log-probability of
    *sequence* under the resulting unsmoothed language model.

    The return value must be rounded to 4 decimal places.  If any required
    count is zero you should immediately return ``float('-inf')``.
    """
    if N < 1:
        return float('-inf')
    if len(sequence) < N:
        return 0.0
    if N == 1:
        unigram_counts = Counter(corpus)
        total_unigrams = len(corpus)
        log_prob = 0.0
        for word in sequence:
            count = unigram_counts.get(word, 0)
            if count == 0:
                return float('-inf')
            prob = count / total_unigrams
            log_prob += np.log(prob)
        return round(log_prob, 4)
    else:
        ngram_counts = Counter((tuple(corpus[i:i + N]) for i in range(len(corpus) - N + 1)))
        prefix_counts = Counter((tuple(corpus[i:i + N - 1]) for i in range(len(corpus) - N + 2)))
        log_prob = 0.0
        for i in range(len(sequence) - N + 1):
            ngram = tuple(sequence[i:i + N])
            prefix = tuple(sequence[i:i + N - 1])
            n_count = ngram_counts.get(ngram, 0)
            p_count = prefix_counts.get(prefix, 0)
            if n_count == 0 or p_count == 0:
                return float('-inf')
            prob = n_count / p_count
            log_prob += np.log(prob)
        return round(log_prob, 4)

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    if not S or not A or (not B) or (not observations):
        return -1
    n = len(S)
    if len(A) != n:
        return -1
    for row in A:
        if len(row) != n:
            return -1
    if len(B) != n:
        return -1
    m = len(B[0])
    for row in B:
        if len(row) != m:
            return -1
    for obs in observations:
        if not isinstance(obs, int) or obs < 0 or obs >= m:
            return -1
    try:
        S = np.array(S)
        A = np.array(A)
        B = np.array(B)
    except:
        return -1
    alpha = S * B[:, observations[0]]
    for t in range(1, len(observations)):
        alpha = np.dot(alpha, A) * B[:, observations[t]]
    probability = np.sum(alpha)
    return round(probability, 4)

from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    from collections import defaultdict, namedtuple

    class FPNode:

        def __init__(self, item, count, parent):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = {}
            self.node_link = None

        def increment(self, count):
            self.count += count

    class FPTree:

        def __init__(self):
            self.root = FPNode(None, 1, None)
            self.header_table = defaultdict(lambda : [0, None])

        def add_transaction(self, transaction, count=1):
            current_node = self.root
            for item in transaction:
                if item in current_node.children:
                    current_node.children[item].increment(count)
                else:
                    new_node = FPNode(item, count, current_node)
                    current_node.children[item] = new_node
                    if self.header_table[item][1] is None:
                        self.header_table[item][1] = new_node
                    else:
                        last_node = self.header_table[item][1]
                        while last_node.node_link is not None:
                            last_node = last_node.node_link
                        last_node.node_link = new_node
                current_node = current_node.children[item]
            return

        def is_single_path(self):
            current = self.root
            while True:
                children = list(current.children.values())
                if len(children) > 1:
                    return False
                elif len(children) == 0:
                    break
                else:
                    current = children[0]
            return True

    def mine_tree(tree, prefix, frequent_itemsets, min_support):
        sorted_items = sorted(tree.header_table.items(), key=lambda x: (x[1][0], x[0]))
        for (item, (count, node)) in sorted_items:
            new_freq_set = prefix.copy()
            new_freq_set.add(item)
            frequent_itemsets.append(sorted(new_freq_set))
            conditional_patterns = []
            current_node = node
            while current_node is not None:
                path = []
                parent = current_node.parent
                while parent is not None and parent.item is not None:
                    path.append(parent.item)
                    parent = parent.parent
                path.reverse()
                if path:
                    conditional_patterns.append((path, current_node.count))
                current_node = current_node.node_link
            conditional_counts = Counter()
            for (path, cnt) in conditional_patterns:
                conditional_counts.update(path)
            conditional_counts = {item: cnt for (item, cnt) in conditional_counts.items() if cnt >= min_support}
            if not conditional_counts:
                continue
            ordered_items = sorted(conditional_counts.items(), key=lambda x: (-x[1], x[0]))
            order = {item: idx for (idx, (item, _)) in enumerate(ordered_items)}
            conditional_tree = FPTree()
            for (path, cnt) in conditional_patterns:
                filtered_path = [item for item in path if item in conditional_counts]
                if not filtered_path:
                    continue
                sorted_path = sorted(filtered_path, key=lambda x: (-conditional_counts[x], x))
                conditional_tree.add_transaction(sorted_path, cnt)
            mine_tree(conditional_tree, new_freq_set, frequent_itemsets, min_support)
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)
    frequent_items = {item for (item, cnt) in item_counts.items() if cnt >= min_support}
    if not frequent_items:
        return []
    ordered_items = sorted(frequent_items, key=lambda x: (-item_counts[x], x))
    item_order = {item: idx for (idx, item) in enumerate(ordered_items)}
    tree = FPTree()
    for transaction in transactions:
        filtered = [item for item in transaction if item in frequent_items]
        if not filtered:
            continue
        sorted_transaction = sorted(filtered, key=lambda x: (-item_counts[x], x))
        tree.add_transaction(sorted_transaction)
    frequent_itemsets = []
    mine_tree(tree, set(), frequent_itemsets, min_support)
    for item in frequent_items:
        frequent_itemsets.append([item])
    unique_itemsets = []
    seen = set()
    for itemset in frequent_itemsets:
        sorted_set = tuple(sorted(itemset))
        if sorted_set not in seen:
            seen.add(sorted_set)
            unique_itemsets.append(list(sorted_set))
    unique_itemsets.sort(key=lambda x: (len(x), x))
    return unique_itemsets

import numpy as np
def relu_(Z):
    """Apply the element-wise Rectified Linear Unit (ReLU) activation.

    The function keeps the container type of the input:
    scalar → scalar, list → list, NumPy array → NumPy array.

    Args:
        Z (int | float | list | numpy.ndarray): Input data – scalar, 1-D or 2-D.

    Returns:
        Same type as *Z* with all negative values clipped to 0.
    """
    if isinstance(Z, (int, float)):
        return max(0, Z)
    elif isinstance(Z, np.ndarray):
        return np.maximum(0, Z)
    elif isinstance(Z, list):
        array_Z = np.array(Z)
        relu_array = np.maximum(0, array_Z)
        return relu_array.tolist()
    else:
        raise TypeError('Unsupported data type. Input must be a scalar, list, or NumPy array.')

import numpy as np
def linear(z):
    """Linear (identity) activation function.

    Args:
        z: Scalar, list, tuple or NumPy ndarray.

    Returns:
        The same value(s) as *z*, obeying the rules:
            • Scalars are returned unchanged.
            • Non-scalar inputs are returned as a NumPy ndarray.
    """
    if isinstance(z, (int, float)):
        return z
    elif isinstance(z, (list, tuple)):
        return np.array(z)
    elif isinstance(z, np.ndarray):
        return z
    else:
        raise TypeError('Input must be a scalar, list, tuple, or NumPy ndarray.')

