from collections import deque
import math
def escape_fire_maze(grid: list[str]) -> int:
    """Escape from a maze with spreading fire.

    Parameters
    ----------
    grid : list[str]
        Square maze represented as a list of strings. Each character must be
        '.', '#', or 'F'.  The agent starts at the upper-left corner (0,0) and
        wishes to reach the lower-right corner (n-1,n-1).  Fire starts in every
        cell marked with 'F' and spreads to the four neighbouring cells every
        minute.  The fire spreads first, then the agent moves.

    Returns
    -------
    int
        Minimum number of minutes required for the agent to reach the goal
        without entering a burning cell, or -1 if this is impossible.
    """
    n = len(grid)
    if n == 0:
        return -1
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    fire_time = [[math.inf for _ in range(n)] for _ in range(n)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_time[i][j] = 0
                fire_queue.append((i, j, 0))
            elif grid[i][j] == '#':
                fire_time[i][j] = -1
    while fire_queue:
        (x, y, t) = fire_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            nt = t + 1
            if 0 <= nx < n and 0 <= ny < n:
                if grid[nx][ny] != '#' and fire_time[nx][ny] > nt:
                    fire_time[nx][ny] = nt
                    fire_queue.append((nx, ny, nt))
    if grid[0][0] == 'F' or grid[n - 1][n - 1] == 'F':
        return -1
    if n == 1:
        if grid[0][0] == '.':
            return 0
        else:
            return -1
    agent_queue = deque()
    agent_queue.append((0, 0, 0))
    visited = [[math.inf for _ in range(n)] for _ in range(n)]
    visited[0][0] = 0
    if fire_time[0][0] <= 0:
        return -1
    while agent_queue:
        (x, y, t) = agent_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            nt = t + 1
            if 0 <= nx < n and 0 <= ny < n:
                if nx == n - 1 and ny == n - 1:
                    if fire_time[nx][ny] > nt:
                        return nt
                if grid[nx][ny] == '.' and fire_time[nx][ny] > nt:
                    if visited[nx][ny] > nt:
                        visited[nx][ny] = nt
                        agent_queue.append((nx, ny, nt))
    return -1

import numpy as np
def sigmoid(X: np.ndarray) -> np.ndarray:
    """Compute the sigmoid activation function."""
    A = 1 / (1 + np.exp(-X))
    return np.round(A, 4)
def sigmoid_derivative(A: np.ndarray) -> np.ndarray:
    """Compute the derivative of the sigmoid function."""
    dA = A * (1 - A)
    return np.round(dA, 4)
def tanh_func(X: np.ndarray) -> np.ndarray:
    """Compute the tanh activation function."""
    A = np.tanh(X)
    return np.round(A, 4)
def tanh_derivative(A: np.ndarray) -> np.ndarray:
    """Compute the derivative of the tanh function."""
    dA = 1 - np.square(A)
    return np.round(dA, 4)
def relu(X: np.ndarray) -> np.ndarray:
    """Compute the ReLU activation function."""
    A = np.maximum(0, X)
    return np.round(A, 4)
def relu_derivative(A: np.ndarray) -> np.ndarray:
    """Compute the derivative of the ReLU function."""
    dA = (A > 0).astype(float)
    return np.round(dA, 4)
def softmax(X: np.ndarray) -> np.ndarray:
    """Compute the softmax activation function in a numerically stable way."""
    shift_X = X - np.max(X, axis=-1, keepdims=True)
    exps = np.exp(shift_X)
    sum_exps = np.sum(exps, axis=-1, keepdims=True)
    A = exps / sum_exps
    return np.round(A, 4)
def softmax_derivative(A: np.ndarray) -> np.ndarray:
    """
    Compute the derivative of the softmax function.

    As per the task description, return an array of ones with the same shape as A.
    """
    dA = np.ones_like(A)
    return np.round(dA, 4)
def activation_forward_backward(X: np.ndarray, activation: str, upstream_grad: np.ndarray) -> tuple[np.ndarray, np.ndarray]:
    """Apply an activation function and compute its local gradient.

    Parameters
    ----------
    X : np.ndarray
        Raw pre-activation values.
    activation : str
        Name of the activation function. Supported values are
        "sigmoid", "tanh", "relu", and "softmax".
    upstream_grad : np.ndarray
        Gradient that arrives from the layer above (must have the same shape as
        `X`).

    Returns
    -------
    tuple[np.ndarray, np.ndarray]
        A two-element tuple `(A, grad)` where:
          • `A`    – the activation applied to `X`.
          • `grad` – the element-wise product of `upstream_grad` and the local
                      derivative of the activation (rounded to 4 decimals).
    """
    if activation == 'sigmoid':
        A = sigmoid(X)
        dA = sigmoid_derivative(A)
    elif activation == 'tanh':
        A = tanh_func(X)
        dA = tanh_derivative(A)
    elif activation == 'relu':
        A = relu(X)
        dA = relu_derivative(A)
    elif activation == 'softmax':
        A = softmax(X)
        dA = softmax_derivative(A)
    else:
        raise ValueError(f'Unsupported activation function: {activation}')
    grad = upstream_grad * dA
    grad = np.round(grad, 4)
    return (A, grad)

def accuracy_score(y_true, y_pred):
    """Return the classification accuracy between *y_true* and *y_pred*.

    The function must:
      • return ‑1 if the two containers have different lengths;
      • return 0 when both containers are empty;
      • otherwise return the proportion of matching elements rounded to
        4 decimal places.

    Args:
        y_true (list | tuple): Ground-truth class labels.
        y_pred (list | tuple): Predicted class labels.

    Returns:
        float | int: The accuracy or ‑1 according to the rules above.
    """
    if len(y_true) != len(y_pred):
        return -1
    if len(y_true) == 0:
        return 0
    matches = sum((1 for (true, pred) in zip(y_true, y_pred) if true == pred))
    accuracy = matches / len(y_true)
    return round(accuracy, 4)

import numpy as np
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    """Fill in here. The final implementation must follow the specification given in the task
    description and return a list with the predicted class labels for *X_test*."""
    X_train_np = np.array(X_train)
    y_train_np = np.array(y_train)
    X_test_np = np.array(X_test)
    (n_samples, n_features) = X_train_np.shape
    y_train_mapped = np.where(y_train_np == 0, -1, 1)
    weights = np.full(n_samples, 1 / n_samples)
    stumps = []
    alphas = []
    for estimator in range(n_estimators):
        min_error = float('inf')
        best_stump = {'feature_index': None, 'threshold': None, 'polarity': None}
        for feature in range(n_features):
            X_feature = X_train_np[:, feature]
            unique_values = np.unique(X_feature)
            for threshold in unique_values:
                for polarity in [1, -1]:
                    if polarity == 1:
                        predictions = np.where(X_feature < threshold, 1, -1)
                    else:
                        predictions = np.where(X_feature >= threshold, 1, -1)
                    misclassified = predictions != y_train_mapped
                    error = np.sum(weights * misclassified)
                    if error < min_error:
                        min_error = error
                        best_stump['feature_index'] = feature
                        best_stump['threshold'] = threshold
                        best_stump['polarity'] = polarity
                    elif error == min_error:
                        if feature < best_stump['feature_index']:
                            best_stump['feature_index'] = feature
                            best_stump['threshold'] = threshold
                            best_stump['polarity'] = polarity
                        elif feature == best_stump['feature_index']:
                            if threshold < best_stump['threshold']:
                                best_stump['threshold'] = threshold
                                best_stump['polarity'] = polarity
                            elif threshold == best_stump['threshold']:
                                if polarity > best_stump['polarity']:
                                    best_stump['polarity'] = polarity
        if min_error == 0:
            alpha = 0.5 * np.log((1 - min_error) / (min_error + 1e-10))
            alphas.append(alpha)
            stumps.append(best_stump)
            break
        alpha = 0.5 * np.log((1 - min_error) / (min_error + 1e-10))
        alphas.append(alpha)
        stumps.append(best_stump)
        feature = best_stump['feature_index']
        threshold = best_stump['threshold']
        polarity = best_stump['polarity']
        if polarity == 1:
            predictions = np.where(X_train_np[:, feature] < threshold, 1, -1)
        else:
            predictions = np.where(X_train_np[:, feature] >= threshold, 1, -1)
        weights *= np.exp(-alpha * y_train_mapped * predictions)
        weights /= np.sum(weights)
    final_predictions = np.zeros(X_test_np.shape[0])
    for (alpha, stump) in zip(alphas, stumps):
        feature = stump['feature_index']
        threshold = stump['threshold']
        polarity = stump['polarity']
        if polarity == 1:
            predictions = np.where(X_test_np[:, feature] < threshold, 1, -1)
        else:
            predictions = np.where(X_test_np[:, feature] >= threshold, 1, -1)
        final_predictions += alpha * predictions
    y_pred = np.where(final_predictions >= 0, 1, 0)
    return y_pred.tolist()

import numpy as np
from typing import Optional, Tuple, List
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    class TreeNode:

        def __init__(self, feature_index: Optional[int]=None, threshold: Optional[float]=None, left: Optional['TreeNode']=None, right: Optional['TreeNode']=None, value: Optional[int]=None):
            self.feature_index = feature_index
            self.threshold = threshold
            self.left = left
            self.right = right
            self.value = value

    def gini(y: np.ndarray) -> float:
        """Calculate Gini impurity for a set of labels."""
        if len(y) == 0:
            return 0.0
        counts = np.bincount(y)
        probabilities = counts / len(y)
        return 1.0 - np.sum(probabilities ** 2)

    def majority_class(y: np.ndarray) -> int:
        """Return the majority class in y. In case of tie, return the smallest class."""
        counts = np.bincount(y)
        max_count = np.max(counts)
        majority_classes = np.where(counts == max_count)[0]
        return int(majority_classes.min())

    def best_split(X: np.ndarray, y: np.ndarray) -> Optional[Tuple[int, float, float]]:
        """Find the best split for the current node."""
        (n_samples, n_features) = X.shape
        if n_samples <= 1:
            return None
        parent_gini = gini(y)
        best_gain = 0.0
        best_feature = None
        best_threshold = None
        for feature in range(n_features):
            X_feature = X[:, feature]
            sorted_indices = np.argsort(X_feature)
            X_sorted = X_feature[sorted_indices]
            y_sorted = y[sorted_indices]
            unique_values = np.unique(X_sorted)
            if len(unique_values) == 1:
                continue
            potential_thresholds = (unique_values[:-1] + unique_values[1:]) / 2
            for threshold in potential_thresholds:
                left_mask = X_sorted <= threshold
                right_mask = X_sorted > threshold
                y_left = y_sorted[left_mask]
                y_right = y_sorted[right_mask]
                if len(y_left) == 0 or len(y_right) == 0:
                    continue
                gini_left = gini(y_left)
                gini_right = gini(y_right)
                weighted_gini = len(y_left) / n_samples * gini_left + len(y_right) / n_samples * gini_right
                gain = parent_gini - weighted_gini
                if gain > best_gain:
                    best_gain = gain
                    best_feature = feature
                    best_threshold = threshold
        if best_gain > 0:
            return (best_feature, best_threshold, best_gain)
        else:
            return None

    def build_tree(X: np.ndarray, y: np.ndarray, depth: int=0) -> TreeNode:
        """Recursively build the decision tree."""
        if len(np.unique(y)) == 1:
            return TreeNode(value=int(y[0]))
        if max_depth is not None and depth >= max_depth:
            return TreeNode(value=majority_class(y))
        split = best_split(X, y)
        if split is None:
            return TreeNode(value=majority_class(y))
        (feature, threshold, gain) = split
        left_indices = X[:, feature] <= threshold
        right_indices = X[:, feature] > threshold
        if not np.any(left_indices) or not np.any(right_indices):
            return TreeNode(value=majority_class(y))
        left_child = build_tree(X[left_indices], y[left_indices], depth + 1)
        right_child = build_tree(X[right_indices], y[right_indices], depth + 1)
        return TreeNode(feature_index=feature, threshold=threshold, left=left_child, right=right_child)

    def predict_single(x: np.ndarray, node: TreeNode) -> int:
        """Predict the class label for a single sample."""
        while node.value is None:
            if x[node.feature_index] <= node.threshold:
                node = node.left
            else:
                node = node.right
        return node.value
    tree_root = build_tree(X_train, y_train)
    predictions = [predict_single(sample, tree_root) for sample in X_test]
    return predictions

import numpy as np
import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported_metrics = {'euclidean', 'manhattan', 'chebyshev', 'cosine'}

    def d(vec1, vec2):
        if name not in supported_metrics:
            return -1

        def to_float_array(vec):
            try:
                arr = np.array(vec, dtype=float)
                return arr
            except (ValueError, TypeError):
                return None
        arr1 = to_float_array(vec1)
        arr2 = to_float_array(vec2)
        if arr1 is None or arr2 is None:
            return -1
        if arr1.ndim != 1 or arr2.ndim != 1 or arr1.size == 0 or (arr2.size == 0):
            return -1
        if arr1.size != arr2.size:
            return -1
        if name == 'cosine':
            norm1 = np.linalg.norm(arr1)
            norm2 = np.linalg.norm(arr2)
            if norm1 == 0 or norm2 == 0:
                return -1
        if name == 'euclidean':
            distance = np.sqrt(np.sum((arr1 - arr2) ** 2))
        elif name == 'manhattan':
            distance = np.sum(np.abs(arr1 - arr2))
        elif name == 'chebyshev':
            distance = np.max(np.abs(arr1 - arr2))
        elif name == 'cosine':
            dot_product = np.dot(arr1, arr2)
            cosine_similarity = dot_product / (np.linalg.norm(arr1) * np.linalg.norm(arr2))
            distance = 1 - cosine_similarity
        else:
            return -1
        return round(float(distance), 4)
    return d

import numpy as np
def softsign(z):
    """Compute the Softsign activation function.

    Softsign(z) = z / (1 + |z|)

    The function accepts either a scalar or a list/NumPy array. For a scalar it
    returns a float; for an array-like input it returns a list of floats, with
    every element rounded to 4 decimal places.
    """
    if np.isscalar(z):
        result = z / (1 + abs(z))
        return round(result, 4)
    else:
        arr = np.array(z)
        if arr.size == 0:
            return []
        softsign_arr = arr / (1 + np.abs(arr))
        rounded = np.round(softsign_arr, 4)
        return rounded.tolist()

def convolution_shape(img_height: int, img_width: int, filter_shape: tuple[int, int], stride: tuple[int, int], padding: tuple[int, int]) -> tuple[int, int] | int:
    """Compute the spatial dimensions of the output produced by a 2-D convolution.

    Args:
        img_height (int): Height of the input image.
        img_width (int):  Width of the input image.
        filter_shape (tuple[int, int]): (kernel_height, kernel_width).
        stride (tuple[int, int]):       (stride_height, stride_width).
        padding (tuple[int, int]):      (pad_height, pad_width).

    Returns:
        tuple[int, int] | int: (output_height, output_width) if the parameters
            yield a valid integer output size; otherwise -1.
    """
    (H, W) = (img_height, img_width)
    (KH, KW) = filter_shape
    (SH, SW) = stride
    (PH, PW) = padding
    if H <= 0 or W <= 0:
        return -1
    if KH <= 0 or KW <= 0:
        return -1
    if SH <= 0 or SW <= 0:
        return -1
    if PH < 0 or PW < 0:
        return -1
    OH_numerator = H + 2 * PH - KH
    if SH == 0:
        return -1
    if OH_numerator < 0:
        return -1
    if OH_numerator % SH != 0:
        return -1
    OH = OH_numerator // SH + 1
    if OH <= 0:
        return -1
    OW_numerator = W + 2 * PW - KW
    if SW == 0:
        return -1
    if OW_numerator < 0:
        return -1
    if OW_numerator % SW != 0:
        return -1
    OW = OW_numerator // SW + 1
    if OW <= 0:
        return -1
    return (OH, OW)

import numpy as np
def one_hot_encode(labels: list[str], categories: list[str] | None=None) -> list[list[int]]:
    """Convert categorical labels to a one-hot encoded matrix.

    Args:
        labels: A list of categorical labels of length *N*.
        categories: Optional complete list of unique categories of length *C*.
            If *None*, the set of categories is inferred from *labels* in the
            order of their first appearance.

    Returns:
        A list of lists that represents the one-hot encoded matrix of shape
        (N, C) where *C* is the number of categories. If an unknown label is
        found when *categories* is supplied, the function returns -1.
    """
    if categories is None:
        seen = set()
        deduced_categories = []
        for label in labels:
            if label not in seen:
                seen.add(label)
                deduced_categories.append(label)
        categories = deduced_categories
    category_to_index = {category: idx for (idx, category) in enumerate(categories)}
    if categories is not None:
        for label in labels:
            if label not in category_to_index:
                return -1
    one_hot_matrix = np.zeros((len(labels), len(categories)), dtype=int)
    for (i, label) in enumerate(labels):
        if label in category_to_index:
            index = category_to_index[label]
            one_hot_matrix[i, index] = 1
        else:
            return -1
    return one_hot_matrix.tolist()

import functools
from typing import Callable, Any
def doublewrap(decorator_func: Callable[..., Callable[..., Any]]) -> Callable[..., Any]:
    """Return a decorator that can be used *with* or *without* parentheses.

    Args:
        decorator_func: A callable that expects the function to decorate as
            its first positional argument followed only by *optional*
            positional or keyword arguments.

    Returns:
        A new decorator that forwards the call in the correct form so that
        ``decorator_func`` can be applied either directly or after receiving
        its own optional parameters.
    """

    @functools.wraps(decorator_func)
    def decorator(*args, **kwargs):
        if len(args) == 1 and callable(args[0]) and (not kwargs):
            return decorator_func(args[0])
        else:

            def real_decorator(func):
                return decorator_func(func, *args, **kwargs)
            return real_decorator
    return decorator

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def get_score(hand: list[int]) -> int:
        total = sum(hand)
        if 1 in hand and total + 10 <= 21:
            return total + 10
        return total

    def is_natural_blackjack(hand: list[int]) -> bool:
        return len(hand) == 2 and (1 in hand and 10 in hand)
    player_score = get_score(player)
    dealer_score = get_score(dealer)
    player_bust = player_score > 21
    dealer_bust = dealer_score > 21
    player_natural = is_natural_blackjack(player)
    dealer_natural = is_natural_blackjack(dealer)
    if player_bust:
        return -1.0
    if dealer_bust:
        return 1.0
    if player_score > dealer_score:
        if player_natural:
            return 1.5
        return 1.0
    elif player_score < dealer_score:
        return -1.0
    else:
        return 0.0

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """

    def apply_activation(x):
        if isinstance(x, (int, float, np.number)):
            return x if x >= 0 else a * x
        elif isinstance(x, np.ndarray):
            return leaky_relu(x, a)
        elif isinstance(x, list):
            return [apply_activation(item) for item in x]
        else:
            raise TypeError(f'Unsupported type {type(x)} encountered in input.')
    activated = apply_activation(z)
    if isinstance(activated, np.ndarray):
        return activated.tolist()
    elif isinstance(activated, list):
        return activated
    else:
        return activated

import numpy as np
def gaussian_naive_bayes(X_train, y_train, X_test):
    """Implement Gaussian Naive Bayes for binary classification from scratch."""
    epsilon = 1e-09
    classes = np.unique(y_train)
    n_features = X_train.shape[1]
    mean = {}
    var = {}
    prior = {}
    for c in classes:
        X_c = X_train[y_train == c]
        mean[c] = X_c.mean(axis=0)
        var[c] = X_c.var(axis=0) + epsilon
        prior[c] = X_c.shape[0] / X_train.shape[0]

    def log_gaussian(x, mean, var):
        return -0.5 * np.log(2.0 * np.pi * var) - (x - mean) ** 2 / (2 * var)
    predictions = []
    for x in X_test:
        log_posteriors = {}
        for c in classes:
            log_prior = np.log(prior[c])
            log_likelihood = log_gaussian(x, mean[c], var[c]).sum()
            log_posteriors[c] = log_prior + log_likelihood
        predicted_class = max(log_posteriors, key=log_posteriors.get)
        predictions.append(int(predicted_class))
    return predictions

import numpy as np
def create_encoding_dicts(obs_ids: list, action_ids: list, obs_encoder: dict | None=None, act_encoder: dict | None=None) -> dict:
    """Generate forward and backward mappings for observations and actions.

    Args:
        obs_ids (list): All distinct observation identifiers.
        action_ids (list): All distinct action identifiers.
        obs_encoder (dict | None, optional): Existing obs→number mapping.
        act_encoder (dict | None, optional): Existing act→number mapping.

    Returns:
        dict: A dictionary that contains the keys 'obs2num', 'num2obs',
        'act2num', and 'num2act'.
    """

    def build_forward_inverse(id_list, encoder):
        if encoder is not None:
            forward = encoder
        else:
            forward = {id_: idx for (idx, id_) in enumerate(id_list)} if id_list else {}
        inverse = {num: sym for (sym, num) in forward.items()} if forward else {}
        return (forward, inverse)
    (obs2num, num2obs) = build_forward_inverse(obs_ids, obs_encoder)
    (act2num, num2act) = build_forward_inverse(action_ids, act_encoder)
    return {'obs2num': obs2num, 'num2obs': num2obs, 'act2num': act2num, 'num2act': num2act}

import numpy as np
def sigmoid(x):
    """Implement element-wise logistic sigmoid."""
    if isinstance(x, (int, float)):
        result = 1 / (1 + np.exp(-x))
        return round(float(result), 4)
    else:
        arr = np.array(x)
        result = 1 / (1 + np.exp(-arr))
        return np.round(result, 4)
def tanh(x):
    """Implement element-wise hyperbolic tangent."""
    if isinstance(x, (int, float)):
        result = np.tanh(x)
        return round(float(result), 4)
    else:
        arr = np.array(x)
        result = np.tanh(arr)
        return np.round(result, 4)
def relu(x):
    """Implement element-wise Rectified Linear Unit."""
    if isinstance(x, (int, float)):
        result = x if x > 0 else 0.0
        return round(float(result), 4)
    else:
        arr = np.array(x)
        result = np.maximum(arr, 0)
        return np.round(result, 4)
def leaky_relu(x, alpha: float=0.01):
    """Implement element-wise Leaky-ReLU with slope *alpha* for x<0."""
    if isinstance(x, (int, float)):
        result = x if x > 0 else alpha * x
        return round(float(result), 4)
    else:
        arr = np.array(x)
        result = np.where(arr > 0, arr, alpha * arr)
        return np.round(result, 4)
def softmax(x):
    """Implement numerically stable softmax over a 1-D input."""
    if isinstance(x, (int, float)):
        return 1.0
    else:
        arr = np.array(x, dtype=np.float64)
        shiftx = arr - np.max(arr)
        exps = np.exp(shiftx)
        sum_exps = np.sum(exps)
        softmax_vals = exps / sum_exps
        return np.round(softmax_vals, 4)
def get_activation(name):
    """Return the activation function by *name*.

    Args:
        name (str): One of {"sigmoid", "tanh", "relu", "leaky_relu", "softmax"}

    Returns:
        Callable: the requested activation function.

    Raises:
        ValueError: If *name* is not recognised.
    """
    activations = {'sigmoid': sigmoid, 'tanh': tanh, 'relu': relu, 'leaky_relu': leaky_relu, 'softmax': softmax}
    if name in activations:
        return activations[name]
    else:
        raise ValueError('Invalid activation function.')

import numpy as np
def feedforward_predict(x, weights, activations):
    """Runs a forward pass through a fully-connected neural network.

    Parameters
    ----------
    x : list[float] | numpy.ndarray
        Input feature vector (bias term **not** included).
    weights : list[list[list[float]]]
        List of weight matrices; each inner list represents one 2-D weight
        matrix where the first column contains the bias weights.
    activations : list[str]
        Activation functions per layer.  Allowed values: "sigmoid", "relu",
        "linear".

    Returns
    -------
    float | list[float]
        Network output rounded to 4 decimal places.  A scalar is returned for
        a single-neuron output layer, otherwise a list is returned.
    """

    def sigmoid(z):
        return 1 / (1 + np.exp(-z))

    def relu(z):
        return np.maximum(0, z)

    def linear(z):
        return z
    activation_funcs = {'sigmoid': sigmoid, 'relu': relu, 'linear': linear}
    a_prev = np.array(x)
    for (W, act) in zip(weights, activations):
        W_np = np.array(W)
        tilde_a = np.insert(a_prev, 0, 1)
        z = W_np @ tilde_a
        a_prev = activation_funcs[act](z)
    a_final = np.round(a_prev, 4)
    if a_final.size == 1:
        return float(a_final)
    else:
        return a_final.tolist()

import numpy as np
from itertools import combinations, permutations
def random_unweighted_graph(n_vertices: int, edge_prob: float=0.5, directed: bool=False) -> list[list[int]]:
    """Generate an un-weighted Erdős–Rényi random graph.

    The function must return the adjacency matrix (a nested list) of the graph.

    Args:
        n_vertices (int): Number of vertices in the graph (labelled 0..n-1).
        edge_prob  (float): Probability *p* that an admissible edge exists.
        directed   (bool):  Whether the graph is directed (default ``False``).

    Returns:
        list[list[int]]: The adjacency matrix *A* where *A[i][j] == 1* iff an
        edge from vertex *i* to vertex *j* exists.  The matrix must consist of
        integers (0/1) only and have shape *(n_vertices, n_vertices)*.
    """
    if n_vertices == 1:
        return [[0]]
    adjacency_matrix = [[0 for _ in range(n_vertices)] for _ in range(n_vertices)]
    if directed:
        for i in range(n_vertices):
            for j in range(n_vertices):
                if i != j:
                    if np.random.rand() <= edge_prob:
                        adjacency_matrix[i][j] = 1
    else:
        for (i, j) in combinations(range(n_vertices), 2):
            if np.random.rand() <= edge_prob:
                adjacency_matrix[i][j] = 1
                adjacency_matrix[j][i] = 1
    return adjacency_matrix

import math
from collections import Counter
def best_split(feature, target):
    """Determine the numerical threshold that produces the highest information gain.

    Parameters
    ----------
    feature : list[int | float] | 1-D numpy.ndarray
        Numerical values of a single attribute.
    target  : list[int] | 1-D numpy.ndarray
        Corresponding class labels.

    Returns
    -------
    tuple
        (threshold, information_gain) where
        * threshold – float rounded to 4 decimals or None when no useful split exists;
        * information_gain – float rounded to 4 decimals.
    """
    if hasattr(feature, 'tolist'):
        feature = feature.tolist()
    if hasattr(target, 'tolist'):
        target = target.tolist()
    n = len(feature)
    if n == 0:
        return (None, 0.0)
    sorted_pairs = sorted(zip(feature, target), key=lambda x: x[0])
    (sorted_feature, sorted_target) = zip(*sorted_pairs)
    thresholds = []
    for i in range(n - 1):
        if sorted_feature[i] != sorted_feature[i + 1]:
            t = (sorted_feature[i] + sorted_feature[i + 1]) / 2
            thresholds.append(t)
    if not thresholds:
        return (None, 0.0)

    def entropy(labels):
        count = Counter(labels)
        total = len(labels)
        if total == 0:
            return 0.0
        ent = 0.0
        for cnt in count.values():
            p = cnt / total
            if p > 0:
                ent -= p * math.log2(p)
        return ent
    H_parent = entropy(target)
    if H_parent == 0:
        return (None, 0.0)
    max_IG = -1
    best_t = None
    for t in thresholds:
        left = []
        right = []
        for (x, y) in sorted_pairs:
            if x < t:
                left.append(y)
            else:
                right.append(y)
        H_left = entropy(left)
        H_right = entropy(right)
        IG = H_parent - len(left) / n * H_left - len(right) / n * H_right
        if IG > max_IG or (IG == max_IG and (best_t is None or t < best_t)):
            max_IG = IG
            best_t = t
    if max_IG <= 0:
        return (None, 0.0)
    return (round(best_t, 4), round(max_IG, 4))

import numpy as np
def L1Regularization(alpha: float):
    """Create a callable object that implements L1 regularisation.

    The returned object must support two operations:
    1. call with a NumPy array to obtain the L1 penalty value
    2. call its .grad(w) method to obtain the gradient of the penalty

    Args:
        alpha: Non-negative float controlling the strength of the regularisation.

    Returns:
        An instance that fulfils the described interface.
    """

    class L1RegularizationInstance:

        def __init__(self, alpha):
            self.alpha = alpha

        def __call__(self, w: np.ndarray) -> float:
            if self.alpha == 0:
                return 0.0
            return float(self.alpha * np.sum(np.abs(w)))

        def grad(self, w: np.ndarray) -> np.ndarray:
            if self.alpha == 0:
                return np.zeros_like(w)
            return self.alpha * np.sign(w)
    return L1RegularizationInstance(alpha)

import numpy as np
def squared_error(actual, predicted):
    """Calculate the squared error or mean squared error.

    If *actual* and *predicted* are scalars, return the squared difference.
    If they are one-dimensional iterables (same length), return the mean squared
    error.  Return -1 for length mismatches or empty iterables.

    All results must be rounded to 4 decimal places.
    """
    if isinstance(actual, (int, float)) and isinstance(predicted, (int, float)):
        return round((actual - predicted) ** 2, 4)
    try:
        actual_arr = np.array(actual)
        predicted_arr = np.array(predicted)
    except:
        return -1
    if actual_arr.ndim != 1 or predicted_arr.ndim != 1:
        return -1
    if actual_arr.size == 0 or predicted_arr.size == 0 or actual_arr.size != predicted_arr.size:
        return -1
    mse = np.mean((actual_arr - predicted_arr) ** 2)
    return round(mse, 4)

import math
from collections import Counter
from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    """Return the total log-probability of *query* under an N-gram MLE model.

    Parameters
    ----------
    corpus : list[str]
        A list of training sentences. Each sentence is a plain string; tokens
        are assumed to be separated by white-spaces.
    query : str
        The sentence whose probability should be calculated.
    N : int
        The order of the N-gram model (N ≥ 1).

    Returns
    -------
    float
        The sum of natural logarithms of the probabilities of every
        consecutive N-gram occurring in *query*, rounded to 4 decimal places.
        If any necessary N-gram is unseen in the corpus the function returns
        float('-inf').
    """
    if N < 1:
        raise ValueError('N must be at least 1.')

    def generate_ngrams(tokens: List[str], N: int) -> List[tuple]:
        if N > 1:
            padded_tokens = ['<bol>'] * (N - 1) + tokens + ['<eol>']
        else:
            padded_tokens = tokens
        if len(padded_tokens) < N:
            return []
        return [tuple(padded_tokens[i:i + N]) for i in range(len(padded_tokens) - N + 1)]
    ngram_counts = Counter()
    prefix_counts = Counter()
    total_unigrams = 0
    for sentence in corpus:
        tokens = sentence.strip().split()
        if N > 1:
            ngrams = generate_ngrams(tokens, N)
            prefixes = [ngram[:-1] for ngram in ngrams]
            ngram_counts.update(ngrams)
            prefix_counts.update(prefixes)
        else:
            ngrams = generate_ngrams(tokens, N)
            ngram_counts.update(ngrams)
            total_unigrams += len(ngrams)
    query_tokens = query.strip().split()
    query_ngrams = generate_ngrams(query_tokens, N)
    if N > 1 and (not query_ngrams):
        return float('-inf')
    elif N == 1 and (not query_ngrams) and query:
        return float('-inf')
    log_prob = 0.0
    for ngram in query_ngrams:
        if N > 1:
            prefix = ngram[:-1]
            count_prefix = prefix_counts.get(prefix, 0)
            count_ngram = ngram_counts.get(ngram, 0)
            if count_ngram == 0 or count_prefix == 0:
                return float('-inf')
            prob = count_ngram / count_prefix
        else:
            count_ngram = ngram_counts.get(ngram, 0)
            if count_ngram == 0 or total_unigrams == 0:
                return float('-inf')
            prob = count_ngram / total_unigrams
        log_prob += math.log(prob)
    return round(log_prob, 4)

import heapq
from typing import List, Union
Number = Union[int, float]
def top_k_elements(sequence: List[Number], k: int, order: str='largest') -> List[Number]:
    """Keeps only the k largest or k smallest numbers from *sequence*.

    The function must visit each element exactly once and store no more than
    *k* items at any time by making use of the heapq module (binary heap).

    Args:
        sequence: A list of integers or floats representing the data stream.
        k: The number of extreme values to keep.
        order: "largest" to retain the k biggest values, "smallest" to retain
            the k smallest.

    Returns:
        A list with at most k numbers sorted according to *order*.
    """
    if k <= 0 or not sequence:
        return []
    n = len(sequence)
    if k >= n:
        return sorted(sequence, reverse=order == 'largest')
    heap = []
    if order == 'largest':
        for num in sequence:
            if len(heap) < k:
                heapq.heappush(heap, num)
            elif num > heap[0]:
                heapq.heapreplace(heap, num)
        result = sorted(heap, reverse=True)
    elif order == 'smallest':
        for num in sequence:
            if len(heap) < k:
                heapq.heappush(heap, -num)
            elif num < -heap[0]:
                heapq.heapreplace(heap, -num)
        result = sorted([-num for num in heap])
    else:
        raise ValueError("order must be either 'largest' or 'smallest'")
    return result

def inorder_keys(pairs):
    """Build a BST from (key, value) *pairs* and return keys from an in-order traversal.

    Args:
        pairs (list[tuple[Any, int | float]]): List of (key, numeric value) pairs.

    Returns:
        list: Keys in in-order (ascending by numeric value). Return an empty
        list if *pairs* is empty.
    """
    if not pairs:
        return []

    class Node:

        def __init__(self, key, val):
            self.key = key
            self.val = val
            self.left = None
            self.right = None

        def __lt__(self, other):
            return self.val < other.val

        def __le__(self, other):
            return self.val <= other.val

        def __gt__(self, other):
            return self.val > other.val

        def __ge__(self, other):
            return self.val >= other.val
    root = Node(pairs[0][0], pairs[0][1])

    def insert(node, new_node):
        current = node
        while True:
            if new_node < current:
                if current.left is None:
                    current.left = new_node
                    break
                else:
                    current = current.left
            elif current.right is None:
                current.right = new_node
                break
            else:
                current = current.right
    for (key, val) in pairs[1:]:
        new_node = Node(key, val)
        insert(root, new_node)

    def inorder_traversal(node, result):
        if node:
            inorder_traversal(node.left, result)
            result.append(node.key)
            inorder_traversal(node.right, result)
    keys_in_order = []
    inorder_traversal(root, keys_in_order)
    return keys_in_order

def update_kernel_settings(parameters: dict, hyperparameters: dict, summary_dict: dict) -> tuple[dict, dict]:
    """Update kernel parameters and hyperparameters.

    The function must not modify the input dictionaries.  It should return two
    *new* dictionaries in which the supplied `summary_dict` updates have been
    applied following the rules explained in the task description.

    Args:
        parameters (dict):      Existing kernel parameters.
        hyperparameters (dict): Existing kernel hyperparameters.
        summary_dict (dict):    Dictionary that may contain the keys
            "parameters" and/or "hyperparameters" whose values are dictionaries
            of updates.  Any top-level keys are also treated as updates.

    Returns:
        tuple[dict, dict]: `(new_parameters, new_hyperparameters)` – the
        updated dictionaries.
    """
    new_parameters = parameters.copy()
    new_hyperparameters = hyperparameters.copy()
    flattened_updates = {}
    params_updates = summary_dict.get('parameters')
    if isinstance(params_updates, dict):
        flattened_updates.update(params_updates)
    hyp_params_updates = summary_dict.get('hyperparameters')
    if isinstance(hyp_params_updates, dict):
        flattened_updates.update(hyp_params_updates)
    for (key, value) in summary_dict.items():
        if key not in {'parameters', 'hyperparameters'}:
            flattened_updates[key] = value
    for (key, value) in flattened_updates.items():
        if key in parameters:
            new_parameters[key] = value
        elif key in hyperparameters:
            new_hyperparameters[key] = value
    return (new_parameters, new_hyperparameters)

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries.

    Parameters
    ----------
    transactions : list[list[str]]
        A list where each element is a list of items representing one transaction.
    min_support : int
        Minimum number of transactions an item has to appear in to be kept.

    Returns
    -------
    dict
        The root node of the FP-Tree.  Each node contains two keys:
        "support" and "children" (the latter mapping item → child-node).
    """
    item_counts = Counter()
    for transaction in transactions:
        unique_items = set(transaction)
        item_counts.update(unique_items)
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return {'support': 0, 'children': {}}
    sorted_items = sorted(frequent_items, key=lambda item: (-item_counts[item], item))
    item_order = {item: idx for (idx, item) in enumerate(sorted_items)}
    fp_tree = {'support': 0, 'children': {}}
    for transaction in transactions:
        filtered_items = [item for item in set(transaction) if item in frequent_items]
        if not filtered_items:
            continue
        sorted_transaction = sorted(filtered_items, key=lambda item: item_order[item])
        current_node = fp_tree
        current_node['support'] += 1
        for item in sorted_transaction:
            children = current_node['children']
            if item in children:
                children[item]['support'] += 1
            else:
                children[item] = {'support': 1, 'children': {}}
            current_node = children[item]
    return fp_tree

import numpy as np
import numpy as np
def get_initializer(name: str):
    """Return a weight-initialisation callable associated with *name*.

    The callable must have the signature
        initializer(shape: tuple[int, ...], scale: float = 1.0) -> np.ndarray
    and create a numpy array filled according to the requested strategy.

    Supported strategies (case-insensitive):
        - "zeros"
        - "ones"
        - "normal"
        - "glorot_uniform"

    If *name* is not recognised the function must raise `ValueError`.
    """
    name_lower = name.lower()
    if name_lower == 'zeros':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.zeros(shape)
    elif name_lower == 'ones':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.ones(shape) * scale
    elif name_lower == 'normal':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.random.normal(loc=0.0, scale=scale, size=shape)
    elif name_lower == 'glorot_uniform':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            if len(shape) < 2:
                raise ValueError('glorot_uniform initializer requires at least 2 dimensions')
            (fan_in, fan_out) = (shape[0], shape[1])
            limit = scale * np.sqrt(6.0 / (fan_in + fan_out))
            return np.random.uniform(low=-limit, high=limit, size=shape)
    else:
        raise ValueError(f'Unsupported initializer: {name}')
    return initializer

import heapq
from collections import Counter
from typing import List, Dict
def huffman_encode(tokens: List[str]) -> Dict[str, str]:
    """Generate a Huffman code for the supplied tokens.

    The function must return a dictionary mapping **every** unique token in
    *tokens* to its binary Huffman code.  All codes must be prefix-free and
    consist only of the characters ‘0’ and ‘1’.
    """
    if not tokens:
        return {}
    frequency = Counter(tokens)
    if len(frequency) == 1:
        return {next(iter(frequency)): '0'}

    class Node:

        def __init__(self, freq: int, token: str='', left: 'Node'=None, right: 'Node'=None):
            self.freq = freq
            self.token = token
            self.left = left
            self.right = right
    heap = []
    for (token, freq) in frequency.items():
        heapq.heappush(heap, (freq, token, Node(freq, token)))
    while len(heap) > 1:
        (freq1, token1, node1) = heapq.heappop(heap)
        (freq2, token2, node2) = heapq.heappop(heap)
        merged_freq = freq1 + freq2
        merged_node = Node(merged_freq, '', node1, node2)
        heapq.heappush(heap, (merged_freq, '', merged_node))
    (_, _, root) = heap[0]
    huffman_codes = {}

    def traverse(node: Node, code: str):
        if node.token:
            huffman_codes[node.token] = code
            return
        if node.left:
            traverse(node.left, code + '0')
        if node.right:
            traverse(node.right, code + '1')
    traverse(root, '')
    return huffman_codes

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_continuous(space: Space, is_composite: bool) -> bool:
        if is_composite:
            if isinstance(space, Tuple):
                sub_spaces = space.spaces
            elif isinstance(space, Dict):
                sub_spaces = space.spaces.values()
            else:
                return False
            return all((isinstance(sub_space, Box) for sub_space in sub_spaces))
        else:
            return isinstance(space, Box)
    cont_action = check_continuous(env.action_space, tuple_action)
    cont_obs = check_continuous(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import re
import ast
from typing import Any, Dict, Optional
class KernelBase:
    """Minimal base class for all kernels.  Do NOT modify this class."""

    def __init__(self, **kwargs: Any) -> None:
        self.params: Dict[str, Any] = kwargs

    def set_params(self, state: Dict[str, Any]):
        """(Re)sets parameters from a previously stored summary dict."""
        hp = state.get('hyperparameters', {})
        self.params.update({k: v for (k, v) in hp.items() if k != 'id'})
        return self

    def summary(self) -> Dict[str, Any]:
        """Dictionary description that can be fed back to *initialize_kernel*."""
        return {'hyperparameters': {'id': self.__class__.__name__, **self.params}}
class LinearKernel(KernelBase):

    def __init__(self):
        super().__init__()
class PolynomialKernel(KernelBase):

    def __init__(self, degree: int=3, coef0: float=1.0):
        super().__init__(degree=degree, coef0=coef0)
class RBFKernel(KernelBase):

    def __init__(self, gamma: float=1.0):
        super().__init__(gamma=gamma)
def initialize_kernel(param: Optional[Any]=None) -> KernelBase:
    """Create and return a kernel object from various representations.

    Args:
        param: None (default LinearKernel), an existing KernelBase instance, a
            string description such as 'RBF(gamma=0.5)', or a dictionary with a
            'hyperparameters' entry as produced by KernelBase.summary().

    Returns:
        KernelBase: the newly created or forwarded kernel instance.
    """
    kernel_map = {'linear': LinearKernel, 'polynomial': PolynomialKernel, 'rbf': RBFKernel}
    id_map = {'LinearKernel': LinearKernel, 'PolynomialKernel': PolynomialKernel, 'RBFKernel': RBFKernel}
    if param is None:
        return LinearKernel()
    elif isinstance(param, KernelBase):
        return param
    elif isinstance(param, str):
        match = re.fullmatch('\\s*(\\w+)\\s*(?:\\((.*)\\))?\\s*', param, re.IGNORECASE)
        if not match:
            raise ValueError(f"Invalid string format: '{param}'")
        (kernel_type, params_str) = match.groups()
        kernel_type_lower = kernel_type.lower()
        if kernel_type_lower not in kernel_map:
            raise NotImplementedError(f"Unknown kernel type: '{kernel_type}'")
        kernel_class = kernel_map[kernel_type_lower]
        kwargs = {}
        if params_str:
            params_pairs = re.findall('(\\w+)\\s*=\\s*([^,]+)', params_str)
            for (key, value_str) in params_pairs:
                try:
                    value = ast.literal_eval(value_str)
                except (ValueError, SyntaxError):
                    value = value_str.strip().strip('"').strip("'")
                kwargs[key] = value
        try:
            return kernel_class(**kwargs)
        except TypeError as e:
            raise ValueError(f'Invalid parameters for {kernel_class.__name__}: {e}')
    elif isinstance(param, dict):
        if 'hyperparameters' not in param:
            raise ValueError("Dictionary must contain 'hyperparameters' key")
        hyperparams = param['hyperparameters']
        if 'id' not in hyperparams:
            raise ValueError("Hyperparameters dictionary must contain 'id' key")
        kernel_id = hyperparams['id']
        if kernel_id not in id_map:
            raise NotImplementedError(f"Unknown kernel id: '{kernel_id}'")
        kernel_class = id_map[kernel_id]
        kwargs = {k: v for (k, v) in hyperparams.items() if k != 'id'}
        try:
            return kernel_class(**kwargs)
        except TypeError as e:
            raise ValueError(f'Invalid parameters for {kernel_class.__name__}: {e}')
    else:
        raise ValueError(f'Unsupported parameter type: {type(param).__name__}')

import numpy as np
import re
def apply_activation(x, param=None):
    """Apply a chosen activation to the input vector.

    Args:
        x (list[float] | np.ndarray): Input data. Anything that can be
            converted to a NumPy array of floats is accepted.
        param (str | None, optional): Description of the activation. See the
            task specification for the accepted values. If *None* the
            identity function is applied.

    Returns:
        list[float]: Result of applying the activation element-wise, rounded
            to six decimals and converted to a standard Python *list*.
    """
    x_arr = np.array(x, dtype=float)
    param_normalized = param.lower() if isinstance(param, str) else param
    if param_normalized is None or param_normalized == 'identity':
        activated = x_arr
    elif param_normalized == 'relu':
        activated = np.maximum(0, x_arr)
    elif param_normalized == 'tanh':
        activated = np.tanh(x_arr)
    elif param_normalized == 'sigmoid':
        activated = 1 / (1 + np.exp(-x_arr))
    elif isinstance(param_normalized, str) and param_normalized.startswith('leaky relu'):
        leaky_relu_pattern = '^leaky\\s*relu(?:\\s*\\(\\s*alpha\\s*=\\s*([-+]?\\d*\\.?\\d+)\\s*\\))?$'
        match = re.match(leaky_relu_pattern, param_normalized)
        if match:
            alpha_str = match.group(1)
            alpha = float(alpha_str) if alpha_str is not None else 0.01
            activated = np.where(x_arr > 0, x_arr, alpha * x_arr)
        else:
            raise ValueError(f'Unknown activation: {param}')
    elif isinstance(param_normalized, str) and param_normalized.startswith('affine'):
        affine_pattern = '^affine\\s*\\(\\s*slope\\s*=\\s*([-+]?\\d*\\.?\\d+)\\s*,\\s*intercept\\s*=\\s*([-+]?\\d*\\.?\\d+)\\s*\\)$'
        match = re.match(affine_pattern, param_normalized)
        if match:
            slope = float(match.group(1))
            intercept = float(match.group(2))
            activated = slope * x_arr + intercept
        else:
            raise ValueError(f'Unknown activation: {param}')
    else:
        raise ValueError(f'Unknown activation: {param}')
    activated = np.round(activated, 6)
    if activated.ndim == 0:
        return [activated.item()]
    else:
        return activated.tolist()

import re
def parse_activation(act_str: str):
    """Parse an activation-function description string.

    The string can be one of the following forms:
        • "ReLU"
        • "Sigmoid"
        • "Tanh"
        • "Affine(slope=<number>, intercept=<number>)"
        • "Leaky ReLU(alpha=<number>)"

    Args:
        act_str: Description of the activation function.

    Returns:
        A dictionary with key "type" and, when applicable, its parameters.
        If the input format is invalid, return -1.
    """
    number_pattern = '([-]?(?:\\d+(?:\\.\\d*)?|\\.\\d+))'
    patterns = {'ReLU': '^ReLU$', 'Sigmoid': '^Sigmoid$', 'Tanh': '^Tanh$', 'Affine': f'^Affine\\(slope={number_pattern}, intercept={number_pattern}\\)$', 'Leaky ReLU': f'^Leaky ReLU\\(alpha={number_pattern}\\)$'}
    for act in ['ReLU', 'Sigmoid', 'Tanh']:
        if re.fullmatch(patterns[act], act_str):
            return {'type': act}
    affine_match = re.fullmatch(patterns['Affine'], act_str)
    if affine_match:
        (slope_str, intercept_str) = affine_match.groups()
        try:
            slope = float(slope_str)
            intercept = float(intercept_str)
            return {'type': 'Affine', 'slope': slope, 'intercept': intercept}
        except ValueError:
            return -1
    leaky_relu_match = re.fullmatch(patterns['Leaky ReLU'], act_str)
    if leaky_relu_match:
        alpha_str = leaky_relu_match.group(1)
        try:
            alpha = float(alpha_str)
            return {'type': 'Leaky ReLU', 'alpha': alpha}
        except ValueError:
            return -1
    return -1

import numpy as np
def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function must return the MAP predictive means for every row in
    `X_new`.  See the task description for full details.
    """
    if fit_intercept:
        intercept_train = np.ones((X.shape[0], 1))
        X = np.hstack((intercept_train, X))
        intercept_new = np.ones((X_new.shape[0], 1))
        X_new = np.hstack((intercept_new, X_new))
    M = X.shape[1]
    if np.isscalar(mu):
        mu = np.full(M, mu)
    else:
        mu = np.asarray(mu)
        if mu.shape[0] != M:
            raise ValueError(f"'mu' must be a scalar or a vector of length {M}")
    if V is None:
        V_matrix = np.eye(M)
    elif np.isscalar(V):
        V_matrix = V * np.eye(M)
    elif V.ndim == 1:
        if V.shape[0] != M:
            raise ValueError(f"'V' as a 1-D array must have length {M}")
        V_matrix = np.diag(V)
    elif V.ndim == 2:
        if V.shape[0] != M or V.shape[1] != M:
            raise ValueError(f"'V' as a matrix must be of shape ({M}, {M})")
        V_matrix = V
    else:
        raise ValueError("'V' must be a scalar, 1-D array, 2-D array, or None")
    try:
        inv_V = np.linalg.inv(V_matrix)
    except np.linalg.LinAlgError:
        raise ValueError("'V' must be invertible")
    XtX = X.T @ X
    inv_posterior_cov = inv_V + XtX
    try:
        Sigma_p = np.linalg.inv(inv_posterior_cov)
    except np.linalg.LinAlgError:
        raise ValueError('Posterior covariance matrix is not invertible')
    inv_V_mu = inv_V @ mu
    Xt_y = X.T @ y
    mu_p = Sigma_p @ (inv_V_mu + Xt_y)
    y_pred = X_new @ mu_p
    y_pred_rounded = np.round(y_pred, 4).tolist()
    return y_pred_rounded

from typing import List, Dict, Tuple, Optional
def topological_sort(vertices: list, edges: list[Tuple]) -> Optional[List]:
    """Determine a topological ordering of a directed graph.

    The implementation **must** use depth-first search (DFS) and follow the
    deterministic exploration rule described in the task description.

    Args:
        vertices: List of vertex identifiers (hashable).
        edges:    List of directed edges given as 2-tuples (source, target).

    Returns:
        • List of vertices in topological order when the graph is acyclic.
        • ``None`` when the graph contains at least one directed cycle.
    """
    from collections import defaultdict
    adjacency: Dict = defaultdict(list)
    vertex_order: Dict = {vertex: idx for (idx, vertex) in enumerate(vertices)}
    for (src, dest) in edges:
        adjacency[src].append(dest)
    for src in adjacency:
        adjacency[src].sort(key=lambda x: vertex_order.get(x, -1))
    visited: Dict = {}
    result: List = []
    has_cycle = False

    def dfs(node):
        nonlocal has_cycle
        if has_cycle:
            return
        visited[node] = 'visiting'
        for neighbor in adjacency.get(node, []):
            state = visited.get(neighbor, 'unvisited')
            if state == 'visiting':
                has_cycle = True
                return
            elif state == 'unvisited':
                dfs(neighbor)
        visited[node] = 'visited'
        result.append(node)
    for vertex in vertices:
        if visited.get(vertex, 'unvisited') == 'unvisited':
            dfs(vertex)
            if has_cycle:
                return None
    return result[::-1]

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in {0, 1, 2}:
        return -1
    is_scalar = np.isscalar(x)
    arr = np.array(x, dtype=float)
    sigmoid_val = 1 / (1 + np.exp(-arr))
    if order == 1:
        result = sigmoid_val * (1 - sigmoid_val)
    elif order == 2:
        result = sigmoid_val * (1 - sigmoid_val) * (1 - 2 * sigmoid_val)
    else:
        result = sigmoid_val
    result = np.round(result, 4)
    if is_scalar:
        return float(result)
    else:
        return result.tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    try:
        X_np = np.array(X, dtype=float)
        if X_np.ndim != 2:
            return -1
        (N, C) = X_np.shape
        if Y is None:
            Y_np = X_np
        else:
            Y_np = np.array(Y, dtype=float)
            if Y_np.ndim != 2:
                return -1
            if Y_np.shape[1] != C:
                return -1
        if sigma is None:
            sigma_val = np.sqrt(C / 2)
            if sigma_val <= 0:
                return -1
            inv_sigma_sq = 1 / sigma_val ** 2
        elif isinstance(sigma, (float, int)):
            sigma_val = float(sigma)
            if sigma_val <= 0:
                return -1
            inv_sigma_sq = 1 / sigma_val ** 2
        elif isinstance(sigma, list):
            if len(sigma) != C:
                return -1
            sigma_array = np.array(sigma, dtype=float)
            if np.any(sigma_array <= 0):
                return -1
            inv_sigma_sq = 1 / sigma_array ** 2
        else:
            return -1
        diff = X_np[:, np.newaxis, :] - Y_np[np.newaxis, :, :]
        diff_sq = diff ** 2
        if isinstance(inv_sigma_sq, np.ndarray):
            scaled_diff_sq = diff_sq * inv_sigma_sq
        else:
            scaled_diff_sq = diff_sq * inv_sigma_sq
        sum_scaled_diff_sq = np.sum(scaled_diff_sq, axis=2)
        K = np.exp(-0.5 * sum_scaled_diff_sq)
        K_rounded = np.round(K, 4)
        return K_rounded.tolist()
    except:
        return -1

import math
def compute_ball_radius(centroid: list[float], X: list[list[float]]) -> float:
    """Compute the maximum Euclidean distance between *centroid* and the
    points in *X* (i.e., the Ball-tree node radius), rounded to 4 decimals.
    """
    if not X:
        return 0.0
    max_distance = 0.0
    for point in X:
        squared_diffs = [(c - p) ** 2 for (c, p) in zip(centroid, point)]
        distance = math.sqrt(sum(squared_diffs))
        if distance > max_distance:
            max_distance = distance
    return round(max_distance, 4)

import heapq
from typing import List, Tuple
def astar(grid: List[List[int]], start: Tuple[int, int], dest: Tuple[int, int]) -> int:
    """A* search on a 2-D grid.

    Args:
        grid: 2-D list containing only 0 (free) and 1 (obstacle).
        start: (row, column) pair indicating the start position.
        dest:  (row, column) pair indicating the goal position.

    Returns:
        The minimum number of moves needed to reach *dest* from *start* using
        4-directional movement, or ‑1 if the destination is unreachable.
    """
    (rows, cols) = (len(grid), len(grid[0]) if grid else 0)
    (start_r, start_c) = start
    (dest_r, dest_c) = dest
    if not (0 <= start_r < rows and 0 <= start_c < cols) or grid[start_r][start_c] == 1:
        return -1
    if not (0 <= dest_r < rows and 0 <= dest_c < cols) or grid[dest_r][dest_c] == 1:
        return -1
    if start == dest:
        return 0

    def heuristic(r: int, c: int) -> int:
        return abs(r - dest_r) + abs(c - dest_c)
    open_set = []
    g_start = 0
    f_start = g_start + heuristic(start_r, start_c)
    heapq.heappush(open_set, (f_start, g_start, (start_r, start_c)))
    visited = {}
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    while open_set:
        (current_f, current_g, (current_r, current_c)) = heapq.heappop(open_set)
        if (current_r, current_c) == dest:
            return current_g
        if (current_r, current_c) in visited and visited[current_r, current_c] <= current_g:
            continue
        visited[current_r, current_c] = current_g
        for (dr, dc) in directions:
            (neighbor_r, neighbor_c) = (current_r + dr, current_c + dc)
            if 0 <= neighbor_r < rows and 0 <= neighbor_c < cols:
                if grid[neighbor_r][neighbor_c] == 0:
                    tentative_g = current_g + 1
                    if (neighbor_r, neighbor_c) not in visited or tentative_g < visited[neighbor_r, neighbor_c]:
                        f = tentative_g + heuristic(neighbor_r, neighbor_c)
                        heapq.heappush(open_set, (f, tentative_g, (neighbor_r, neighbor_c)))
    return -1

import numpy as np
def to_nominal(one_hot):
    """Convert a batch of one-hot encoded vectors to nominal class labels.

    Parameters
    ----------
    one_hot : list[list[int]] | numpy.ndarray
        A 2-D structure where each row is expected to be a one-hot encoded
        vector (all zeros except for a single 1).

    Returns
    -------
    list[int] | int
        A list of integer class labels when the input is valid; otherwise the
        integer ‑1.
    """
    try:
        arr = np.array(one_hot)
        if isinstance(one_hot, list) and len(one_hot) == 0:
            return []
        if arr.ndim != 2:
            return -1
        if arr.shape[1] < 1:
            return -1
        if not np.all((arr == 0) | (arr == 1)):
            return -1
        if not np.all(arr.sum(axis=1) == 1):
            return -1
        labels = np.argmax(arr, axis=1)
        return labels.tolist()
    except:
        return -1

from collections import Counter, defaultdict
import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """

    class FPTreeNode:

        def __init__(self, item, count, parent):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = {}
            self.link = None

        def increment(self, count):
            self.count += count

    class FPTree:

        def __init__(self):
            self.root = FPTreeNode(None, 1, None)
            self.header_table = {}

        def add_tree(self, items, count):
            current_node = self.root
            for item in items:
                if item in current_node.children:
                    current_node.children[item].increment(count)
                else:
                    new_node = FPTreeNode(item, count, current_node)
                    current_node.children[item] = new_node
                    if item in self.header_table:
                        last_node = self.header_table[item]
                        while last_node.link:
                            last_node = last_node.link
                        last_node.link = new_node
                    else:
                        self.header_table[item] = new_node
                current_node = current_node.children[item]

    def build_fp_tree(transactions, min_sup):
        item_counts = Counter()
        for transaction in transactions:
            item_counts.update(transaction)
        freq_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
        if not freq_items:
            return (None, None)
        sorted_items = sorted(freq_items.items(), key=lambda x: (-x[1], x[0]))
        item_order = {item: idx for (idx, (item, _)) in enumerate(sorted_items)}
        tree = FPTree()
        for transaction in transactions:
            filtered = [item for item in transaction if item in freq_items]
            sorted_transaction = sorted(filtered, key=lambda item: (-freq_items[item], item))
            if sorted_transaction:
                tree.add_tree(sorted_transaction, 1)
        return (tree, freq_items)

    def ascend_tree(node):
        path = []
        while node.parent and node.parent.item is not None:
            node = node.parent
            path.append(node.item)
        return path

    def find_conditional_pattern_base(node):
        conditional_patterns = []
        while node:
            path = ascend_tree(node)
            if path:
                conditional_patterns.append((path, node.count))
            node = node.link
        return conditional_patterns

    def mine_tree(tree, prefix, frequent_itemsets, min_sup):
        items = sorted(tree.header_table.items(), key=lambda x: (freq_order[x[0]], x[0]))
        for (item, node) in items:
            new_prefix = prefix.copy()
            new_prefix.append(item)
            frequent_itemsets.append(tuple(sorted(new_prefix)))
            conditional_patterns = find_conditional_pattern_base(node)
            conditional_counts = Counter()
            for (path, count) in conditional_patterns:
                conditional_counts.update(dict.fromkeys(path, count))
            conditional_freq = {i: c for (i, c) in conditional_counts.items() if c >= min_sup}
            if not conditional_freq:
                continue
            sorted_conditional = sorted(conditional_freq.items(), key=lambda x: (-x[1], x[0]))
            ordered_conditional = {item: idx for (idx, (item, _)) in enumerate(sorted_conditional)}
            conditional_transactions = []
            for (path, count) in conditional_patterns:
                filtered = [i for i in path if i in conditional_freq]
                sorted_path = sorted(filtered, key=lambda x: (-conditional_freq[x], x))
                if sorted_path:
                    conditional_transactions.append((sorted_path, count))
            conditional_tree = FPTree()
            for (sorted_path, count) in conditional_transactions:
                conditional_tree.add_tree(sorted_path, count)
            mine_tree(conditional_tree, new_prefix, frequent_itemsets, min_sup)
    (tree, freq_items) = build_fp_tree(transactions, min_sup)
    if not tree or not tree.header_table:
        return []
    freq_order = {item: count for (item, count) in freq_items.items()}
    frequent_itemsets = [tuple([item]) for item in sorted(freq_items.keys())]
    mine_tree(tree, [], frequent_itemsets, min_sup)
    unique_itemsets = sorted(set(frequent_itemsets), key=lambda x: (len(x), x))
    return unique_itemsets

import numpy as np
def leaky_relu(x: np.ndarray | list | int | float, alpha: float=0.3, order: int=0) -> list:
    """Compute Leaky ReLU, its first derivative, or its second derivative.

    Args:
        x: Values to be transformed. Accepts NumPy array, list, or scalar.
        alpha: Slope used when an element is non-positive (default 0.3).
        order: 0 – return the function value; 1 – return the first derivative; 2 – return the second derivative.

    Returns:
        Python list with the same shape as *x* that contains the requested quantity.
    """
    arr = np.array(x)
    if order == 0:
        result = np.where(arr > 0, arr, alpha * arr)
    elif order == 1:
        result = np.where(arr > 0, 1.0, alpha)
    elif order == 2:
        result = np.zeros_like(arr, dtype=float)
    else:
        return []
    return result.tolist()

import numpy as np
def relu_grad(z):
    """Compute the element-wise derivative of the ReLU activation.

    The function should return 0 for every entry that is less than or equal to
    zero and 1 for every entry that is strictly greater than zero.  Scalars
    must yield a single integer; arrays/lists must yield a list of identical
    shape.
    """
    np_array = np.array(z)
    grad = (np_array > 0).astype(int)
    if np.isscalar(z) or (isinstance(z, np.ndarray) and np_array.ndim == 0):
        return int(grad)
    else:
        return grad.tolist()

