import os
def extract_archive(file_path: str, path: str='.', archive_format: Union[str, List[str], None]='auto') -> bool:
    """Checks whether *file_path* belongs to an allowed archive format.

    The function MUST NOT raise any exception and MUST NOT perform real file
    extraction.  Read the task description for the full specification.

    Args:
        file_path: Full path (or name) of the archive file.
        path: Destination folder – ignored in this simplified version.
        archive_format: See task description.

    Returns:
        True if the file name matches one of the allowed formats, False otherwise.
    """
    try:
        format_extensions = {'tar': ['.tar', '.tar.gz', '.tgz', '.tar.bz', '.tar.bz2', '.tbz'], 'zip': ['.zip']}
        if archive_format == 'auto':
            allowed_formats = ['tar', 'zip']
        elif isinstance(archive_format, str):
            allowed_formats = [archive_format.lower()]
        elif isinstance(archive_format, list):
            allowed_formats = [fmt.lower() for fmt in archive_format if isinstance(fmt, str)]
        else:
            allowed_formats = []
        if not allowed_formats:
            return False
        allowed_extensions = []
        for fmt in allowed_formats:
            extensions = format_extensions.get(fmt)
            if extensions:
                allowed_extensions.extend(extensions)
        if not allowed_extensions:
            return False
        file_path_lower = file_path.lower()
        for ext in allowed_extensions:
            if file_path_lower.endswith(ext):
                return True
        return False
    except:
        return False

def tree_stats(root: 'node | None') -> tuple[int, int]:
    """Return the number of leaf nodes and the maximum depth of the tree.

    A **leaf** is defined as a node whose ``results`` attribute is not ``None``.

    Parameters
    ----------
    root : node | None
        Root of the decision tree.  If *None*, the tree is considered empty.

    Returns
    -------
    tuple[int, int]
        ``(leaf_count, max_depth)`` where
        * *leaf_count* is the total number of leaves in the tree, and
        * *max_depth* is the length of the longest path from *root* to a leaf.
    """

    def helper(node: 'node | None') -> tuple[int, int]:
        if node is None:
            return (0, 0)
        if node.results is not None:
            return (1, 1)
        (left_leaves, left_depth) = helper(node.left)
        (right_leaves, right_depth) = helper(node.right)
        total_leaves = left_leaves + right_leaves
        current_depth = 1 + max(left_depth, right_depth)
        return (total_leaves, current_depth)
    return helper(root)

from collections import Counter
from typing import Any, Dict, List
def build_tree(data: List[List[Any]]) -> Dict[str, Any]:
    """Build a CART decision tree using the Gini impurity criterion.

    The *data* argument is a list where every inner list represents a training
    sample.  All but the last elements of each sample are feature values; the
    last element is the class label.

    The function returns the root of the generated tree expressed as a
    nested dictionary:
        Leaf   -> {"label": class_label}
        Branch -> {"feature": index,
                    "value":   split_value,
                    "left":    <subtree for samples with feature == value>,
                    "right":   <subtree for samples with feature != value>}

    If *data* is empty, returns the empty dictionary.
    """
    if not data:
        return {}
    labels = [sample[-1] for sample in data]

    def compute_gini(subset: List[List[Any]]) -> float:
        if not subset:
            return 0.0
        label_counts = Counter((sample[-1] for sample in subset))
        total = len(subset)
        return 1.0 - sum(((count / total) ** 2 for count in label_counts.values()))

    def majority_class(subset: List[List[Any]]) -> Any:
        label_counts = Counter((sample[-1] for sample in subset))
        max_count = max(label_counts.values())
        candidates = [label for (label, count) in label_counts.items() if count == max_count]
        if len(candidates) == 1:
            return candidates[0]
        for sample in data:
            if sample[-1] in candidates:
                return sample[-1]
        return candidates[0]
    current_gini = compute_gini(data)
    num_features = len(data[0]) - 1
    best_gain = -1
    best_split = None
    for feature_index in range(num_features):
        feature_values = sorted(set((sample[feature_index] for sample in data)))
        for value in feature_values:
            left_subset = [sample for sample in data if sample[feature_index] == value]
            right_subset = [sample for sample in data if sample[feature_index] != value]
            if not left_subset or not right_subset:
                continue
            gini_left = compute_gini(left_subset)
            gini_right = compute_gini(right_subset)
            total = len(data)
            weighted_gini = len(left_subset) / total * gini_left + len(right_subset) / total * gini_right
            gain = current_gini - weighted_gini
            if gain > best_gain:
                best_gain = gain
                best_split = (feature_index, value, left_subset, right_subset)
    if best_gain <= 0 or best_split is None:
        return {'label': majority_class(data)}
    (feature, value, left_data, right_data) = best_split
    left_subtree = build_tree(left_data)
    right_subtree = build_tree(right_data)
    return {'feature': feature, 'value': value, 'left': left_subtree, 'right': right_subtree}

import numpy as np
def forward_step(A: list[list[float]], B: list[list[float]], S: list[float], obs: list[int], t: int) -> list[float]:
    """Compute the forward probability vector α_t for a Hidden Markov Model.

    Implement the forward recursion and return α_t rounded to 4 decimals.
    If the inputs are invalid (shape mismatch, illegal t, etc.) return -1.

    Args:
        A (list[list[float]]): State-transition matrix (n×n).
        B (list[list[float]]): Emission probability matrix (n×m).
        S (list[float]):       Initial state distribution (length n).
        obs (list[int]):       Observation sequence (symbol indices).
        t (int):               Desired time step (0-based).

    Returns:
        list[float] | int: α_t as a list or -1 on error.
    """
    try:
        if not isinstance(A, list) or len(A) == 0:
            return -1
        n = len(A)
        for row in A:
            if not isinstance(row, list) or len(row) != n:
                return -1
        if not isinstance(B, list) or len(B) != n:
            return -1
        if n == 0:
            return -1
        m = None
        for row in B:
            if not isinstance(row, list):
                return -1
            if m is None:
                m = len(row)
                if m == 0:
                    return -1
            elif len(row) != m:
                return -1
        if not isinstance(S, list) or len(S) != n:
            return -1
        if not isinstance(obs, list):
            return -1
        for o in obs:
            if not isinstance(o, int) or o < 0 or o >= m:
                return -1
        if not isinstance(t, int) or t < 0 or t >= len(obs):
            return -1
        A_np = np.array(A, dtype=float)
        B_np = np.array(B, dtype=float)
        S_np = np.array(S, dtype=float)
        alpha = S_np * B_np[:, obs[0]]
        for step in range(1, t + 1):
            alpha = alpha @ A_np * B_np[:, obs[step]]
        alpha_rounded = np.round(alpha, 4).tolist()
        return alpha_rounded
    except:
        return -1

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    current_node = root
    while True:
        if hasattr(current_node, 'y'):
            return current_node.y
        feature_index = current_node.label
        feature_value = features[feature_index]
        if task_type == 'classification':
            matched = False
            for child in current_node.child:
                if hasattr(child, 'x') and child.x == feature_value:
                    current_node = child
                    matched = True
                    break
            if not matched:
                current_node = current_node.child[1]
        elif task_type == 'regression':
            if feature_value <= current_node.s:
                current_node = current_node.child[0]
            else:
                current_node = current_node.child[1]
        else:
            raise ValueError("task_type must be either 'classification' or 'regression'")

import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    import math
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    if sample_size > n_samples:
        sample_size = n_samples
    if sample_size > 1:
        phi = 2 * math.log(sample_size - 1) - 2 * (sample_size - 1) / sample_size
    else:
        phi = 0

    def c(n):
        if n <= 1:
            return 0
        else:
            return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    class Tree:

        def __init__(self, data_subset, height_limit):
            self.height_limit = height_limit
            self.n_features = data_subset.shape[1]
            self.root = self.build_tree(data_subset, current_depth=0)

        def build_tree(self, data_subset, current_depth):
            if current_depth >= self.height_limit or data_subset.shape[0] <= 1:
                return {'leaf': True, 'size': data_subset.shape[0]}
            else:
                feature = np.random.randint(0, self.n_features)
                feature_values = data_subset[:, feature]
                min_val = feature_values.min()
                max_val = feature_values.max()
                if min_val == max_val:
                    return {'leaf': True, 'size': data_subset.shape[0]}
                threshold = np.random.uniform(min_val, max_val)
                left_indices = data_subset[:, feature] < threshold
                right_indices = data_subset[:, feature] >= threshold
                left = data_subset[left_indices]
                right = data_subset[right_indices]
                if left.shape[0] == 0 or right.shape[0] == 0:
                    return {'leaf': True, 'size': data_subset.shape[0]}
                return {'leaf': False, 'feature': feature, 'threshold': threshold, 'left': self.build_tree(left, current_depth + 1), 'right': self.build_tree(right, current_depth + 1)}

        def path_length(self, x):
            node = self.root
            path = 0
            while not node.get('leaf', False):
                path += 1
                feature = node['feature']
                threshold = node['threshold']
                if x[feature] < threshold:
                    node = node['left']
                else:
                    node = node['right']
            size = node['size']
            path += c(size)
            return path
    height_limit = math.ceil(math.log2(sample_size)) if sample_size > 0 else 0
    trees = []
    for _ in range(n_trees):
        if sample_size >= n_samples:
            sample_indices = np.arange(n_samples)
        else:
            sample_indices = np.random.choice(n_samples, size=sample_size, replace=False)
        data_subset = data[sample_indices]
        tree = Tree(data_subset, height_limit)
        trees.append(tree)
    path_lengths = np.zeros((n_samples, n_trees))
    for (tree_idx, tree) in enumerate(trees):
        for i in range(n_samples):
            x = data[i]
            h = tree.path_length(x)
            path_lengths[i, tree_idx] = h
    avg_h = path_lengths.mean(axis=1)
    with np.errstate(divide='ignore', invalid='ignore'):
        scores = np.power(2, -avg_h / phi) if phi != 0 else np.zeros(n_samples)
    if epsilon == 0:
        return []
    else:
        num_outliers = math.ceil(epsilon * n_samples)
        if num_outliers == 0:
            return []
        sorted_indices = np.argsort(-scores, kind='stable')
        top_indices = sorted_indices[:num_outliers]
        return sorted(top_indices.tolist())

import numpy as np
def minesweeper_step(ground: list[list[float | int]], flags: list[list[bool]]) -> tuple[list[tuple[int, int]], list[tuple[int, int]]]:
    """Perform one logical deduction step on a Minesweeper board.

    The function receives the currently visible part of the board (`ground`)
    and the already placed flags (`flags`). It must return two *sorted* lists:

    1. **clicks** – every square that can be safely revealed.
    2. **new_flags** – every square that must be flagged according to the rules
       described in the task description.

    Notes:
        * `ground` and `flags` have identical dimensions.
        * Hidden squares are represented by `np.nan` inside `ground`.
        * Neighbourhood is the 8-connected Moore neighbourhood.
    """
    rows = len(ground)
    if rows == 0:
        return ([], [])
    cols = len(ground[0])
    clicks = set()
    new_flags = set()

    def get_neighbors(r, c):
        for dr in (-1, 0, 1):
            for dc in (-1, 0, 1):
                if dr == 0 and dc == 0:
                    continue
                (nr, nc) = (r + dr, c + dc)
                if 0 <= nr < rows and 0 <= nc < cols:
                    yield (nr, nc)
    for r in range(rows):
        for c in range(cols):
            if not np.isnan(ground[r][c]) and (not flags[r][c]):
                value = ground[r][c]
                neighbors = list(get_neighbors(r, c))
                if value == 0:
                    for (nr, nc) in neighbors:
                        if np.isnan(ground[nr][nc]) and (not flags[nr][nc]):
                            clicks.add((nr, nc))
                elif value == 8:
                    for (nr, nc) in neighbors:
                        if np.isnan(ground[nr][nc]) and (not flags[nr][nc]):
                            new_flags.add((nr, nc))
                else:
                    bombs = sum((flags[nr][nc] for (nr, nc) in neighbors))
                    unexplored = [(nr, nc) for (nr, nc) in neighbors if np.isnan(ground[nr][nc]) and (not flags[nr][nc])]
                    if bombs == value:
                        for coord in unexplored:
                            clicks.add(coord)
                    elif len(unexplored) == value:
                        for coord in unexplored:
                            new_flags.add(coord)
    clicks_sorted = sorted(clicks)
    new_flags_sorted = sorted(new_flags)
    return (clicks_sorted, new_flags_sorted)

from copy import deepcopy
def deduce_mines(constraints: list[list]) -> tuple[list[str], list[str]]:
    """Simplify Minesweeper constraints.

    You receive *constraints* as a list of 2-element lists *[variables, value]*
    where *variables* is a list of identifiers and *value* is an int telling how
    many of those variables hide a mine.

    The function must apply the logical rules described in the task statement
    (solved constraints, propagation, subset rule, duplicate removal) until no
    new information can be obtained.

    The result is a tuple (safe, mines) of two **alphabetically sorted** lists.
    The first element contains all variables that are certainly safe, the second
    one all variables that are certainly mines.  If nothing can be deduced an
    empty list must be returned instead.
    """
    constraints = [(frozenset(c[0]), c[1]) for c in constraints]
    safe = set()
    mines = set()
    changed = True
    while changed:
        changed = False
        new_safe = set()
        new_mines = set()
        for (vars_set, value) in constraints:
            if value == 0:
                safe.update(vars_set)
            elif value == len(vars_set):
                mines.update(vars_set)
        if (new_safe := (safe - new_safe)):
            changed = True
        if (new_mines := (mines - new_mines)):
            changed = True
        if safe:
            new_constraints = []
            for (vars_set, value) in constraints:
                if not vars_set.isdisjoint(safe):
                    new_set = vars_set - safe
                    if len(new_set) != len(vars_set):
                        changed = True
                        vars_set = new_set
                new_constraints.append((vars_set, value))
            constraints = new_constraints
        if mines:
            new_constraints = []
            for (vars_set, value) in constraints:
                if not vars_set.isdisjoint(mines):
                    removed = vars_set & mines
                    if removed:
                        vars_set = vars_set - removed
                        value = value - len(removed)
                        changed = True
                new_constraints.append((vars_set, value))
            constraints = new_constraints
        constraints_copy = deepcopy(constraints)
        for i in range(len(constraints_copy)):
            (S1, v1) = constraints_copy[i]
            for j in range(len(constraints_copy)):
                if i == j:
                    continue
                (S2, v2) = constraints_copy[j]
                if S1 < S2:
                    new_S = S2 - S1
                    new_v = v2 - v1
                    new_constraint = (frozenset(new_S), new_v)
                    if new_constraint not in constraints:
                        constraints.append(new_constraint)
                        changed = True
        unique_constraints = set()
        duplicates_found = False
        for constraint in constraints:
            unique_constraints.add(constraint)
        if len(unique_constraints) != len(constraints):
            duplicates_found = True
            constraints = list(unique_constraints)
            changed = True
    for (vars_set, value) in constraints:
        if value == 0:
            safe.update(vars_set)
        elif value == len(vars_set):
            mines.update(vars_set)
    safe = sorted(safe)
    mines = sorted(mines)
    return (safe, mines)

def maze_to_graph(maze: list[list[int]]) -> dict[tuple[int, int], list[tuple[int, int]]]:
    """Convert a binary maze into an adjacency-list graph.

    A 0 in the maze denotes an open cell, while 1 denotes a wall.  Two open
    cells are adjacent if they share an edge (up, down, left, right).

    Args:
        maze: Rectangular 2-D list containing only 0s and 1s.

    Returns:
        A dictionary mapping each open cell coordinate (row, col) to a sorted
        list of neighbouring open cell coordinates.  Walls are not included in
        the dictionary.  If there are no open cells the function returns an
        empty dictionary.
    """
    if not maze or not maze[0]:
        return {}
    rows = len(maze)
    cols = len(maze[0])
    graph = {}
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    for r in range(rows):
        for c in range(cols):
            if maze[r][c] == 0:
                neighbors = []
                for (dr, dc) in directions:
                    (nr, nc) = (r + dr, c + dc)
                    if 0 <= nr < rows and 0 <= nc < cols and (maze[nr][nc] == 0):
                        neighbors.append((nr, nc))
                neighbors.sort()
                graph[r, c] = neighbors
    return graph
