def escape_fire_maze(grid: list[str]) -> int:
    """Escape from a maze with spreading fire.

    Parameters
    ----------
    grid : list[str]
        Square maze represented as a list of strings. Each character must be
        '.', '#', or 'F'.  The agent starts at the upper-left corner (0,0) and
        wishes to reach the lower-right corner (n-1,n-1).  Fire starts in every
        cell marked with 'F' and spreads to the four neighbouring cells every
        minute.  The fire spreads first, then the agent moves.

    Returns
    -------
    int
        Minimum number of minutes required for the agent to reach the goal
        without entering a burning cell, or -1 if this is impossible.
    """
    n = len(grid)
    if n == 0:
        return -1
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    fire_time = [[math.inf for _ in range(n)] for _ in range(n)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_time[i][j] = 0
                fire_queue.append((i, j, 0))
            elif grid[i][j] == '#':
                fire_time[i][j] = -1
    while fire_queue:
        (x, y, t) = fire_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            nt = t + 1
            if 0 <= nx < n and 0 <= ny < n:
                if grid[nx][ny] != '#' and fire_time[nx][ny] > nt:
                    fire_time[nx][ny] = nt
                    fire_queue.append((nx, ny, nt))
    if grid[0][0] == 'F' or grid[n - 1][n - 1] == 'F':
        return -1
    if n == 1:
        if grid[0][0] == '.':
            return 0
        else:
            return -1
    agent_queue = deque()
    agent_queue.append((0, 0, 0))
    visited = [[math.inf for _ in range(n)] for _ in range(n)]
    visited[0][0] = 0
    if fire_time[0][0] <= 0:
        return -1
    while agent_queue:
        (x, y, t) = agent_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            nt = t + 1
            if 0 <= nx < n and 0 <= ny < n:
                if nx == n - 1 and ny == n - 1:
                    if fire_time[nx][ny] > nt:
                        return nt
                if grid[nx][ny] == '.' and fire_time[nx][ny] > nt:
                    if visited[nx][ny] > nt:
                        visited[nx][ny] = nt
                        agent_queue.append((nx, ny, nt))
    return -1

import numpy as np
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    """Fill in here. The final implementation must follow the specification given in the task
    description and return a list with the predicted class labels for *X_test*."""
    X_train_np = np.array(X_train)
    y_train_np = np.array(y_train)
    X_test_np = np.array(X_test)
    (n_samples, n_features) = X_train_np.shape
    y_train_mapped = np.where(y_train_np == 0, -1, 1)
    weights = np.full(n_samples, 1 / n_samples)
    stumps = []
    alphas = []
    for estimator in range(n_estimators):
        min_error = float('inf')
        best_stump = {'feature_index': None, 'threshold': None, 'polarity': None}
        for feature in range(n_features):
            X_feature = X_train_np[:, feature]
            unique_values = np.unique(X_feature)
            for threshold in unique_values:
                for polarity in [1, -1]:
                    if polarity == 1:
                        predictions = np.where(X_feature < threshold, 1, -1)
                    else:
                        predictions = np.where(X_feature >= threshold, 1, -1)
                    misclassified = predictions != y_train_mapped
                    error = np.sum(weights * misclassified)
                    if error < min_error:
                        min_error = error
                        best_stump['feature_index'] = feature
                        best_stump['threshold'] = threshold
                        best_stump['polarity'] = polarity
                    elif error == min_error:
                        if feature < best_stump['feature_index']:
                            best_stump['feature_index'] = feature
                            best_stump['threshold'] = threshold
                            best_stump['polarity'] = polarity
                        elif feature == best_stump['feature_index']:
                            if threshold < best_stump['threshold']:
                                best_stump['threshold'] = threshold
                                best_stump['polarity'] = polarity
                            elif threshold == best_stump['threshold']:
                                if polarity > best_stump['polarity']:
                                    best_stump['polarity'] = polarity
        if min_error == 0:
            alpha = 0.5 * np.log((1 - min_error) / (min_error + 1e-10))
            alphas.append(alpha)
            stumps.append(best_stump)
            break
        alpha = 0.5 * np.log((1 - min_error) / (min_error + 1e-10))
        alphas.append(alpha)
        stumps.append(best_stump)
        feature = best_stump['feature_index']
        threshold = best_stump['threshold']
        polarity = best_stump['polarity']
        if polarity == 1:
            predictions = np.where(X_train_np[:, feature] < threshold, 1, -1)
        else:
            predictions = np.where(X_train_np[:, feature] >= threshold, 1, -1)
        weights *= np.exp(-alpha * y_train_mapped * predictions)
        weights /= np.sum(weights)
    final_predictions = np.zeros(X_test_np.shape[0])
    for (alpha, stump) in zip(alphas, stumps):
        feature = stump['feature_index']
        threshold = stump['threshold']
        polarity = stump['polarity']
        if polarity == 1:
            predictions = np.where(X_test_np[:, feature] < threshold, 1, -1)
        else:
            predictions = np.where(X_test_np[:, feature] >= threshold, 1, -1)
        final_predictions += alpha * predictions
    y_pred = np.where(final_predictions >= 0, 1, 0)
    return y_pred.tolist()

import numpy as np
from typing import Optional, Tuple, List
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    class TreeNode:

        def __init__(self, feature_index: Optional[int]=None, threshold: Optional[float]=None, left: Optional['TreeNode']=None, right: Optional['TreeNode']=None, value: Optional[int]=None):
            self.feature_index = feature_index
            self.threshold = threshold
            self.left = left
            self.right = right
            self.value = value

    def gini(y: np.ndarray) -> float:
        """Calculate Gini impurity for a set of labels."""
        if len(y) == 0:
            return 0.0
        counts = np.bincount(y)
        probabilities = counts / len(y)
        return 1.0 - np.sum(probabilities ** 2)

    def majority_class(y: np.ndarray) -> int:
        """Return the majority class in y. In case of tie, return the smallest class."""
        counts = np.bincount(y)
        max_count = np.max(counts)
        majority_classes = np.where(counts == max_count)[0]
        return int(majority_classes.min())

    def best_split(X: np.ndarray, y: np.ndarray) -> Optional[Tuple[int, float, float]]:
        """Find the best split for the current node."""
        (n_samples, n_features) = X.shape
        if n_samples <= 1:
            return None
        parent_gini = gini(y)
        best_gain = 0.0
        best_feature = None
        best_threshold = None
        for feature in range(n_features):
            X_feature = X[:, feature]
            sorted_indices = np.argsort(X_feature)
            X_sorted = X_feature[sorted_indices]
            y_sorted = y[sorted_indices]
            unique_values = np.unique(X_sorted)
            if len(unique_values) == 1:
                continue
            potential_thresholds = (unique_values[:-1] + unique_values[1:]) / 2
            for threshold in potential_thresholds:
                left_mask = X_sorted <= threshold
                right_mask = X_sorted > threshold
                y_left = y_sorted[left_mask]
                y_right = y_sorted[right_mask]
                if len(y_left) == 0 or len(y_right) == 0:
                    continue
                gini_left = gini(y_left)
                gini_right = gini(y_right)
                weighted_gini = len(y_left) / n_samples * gini_left + len(y_right) / n_samples * gini_right
                gain = parent_gini - weighted_gini
                if gain > best_gain:
                    best_gain = gain
                    best_feature = feature
                    best_threshold = threshold
        if best_gain > 0:
            return (best_feature, best_threshold, best_gain)
        else:
            return None

    def build_tree(X: np.ndarray, y: np.ndarray, depth: int=0) -> TreeNode:
        """Recursively build the decision tree."""
        if len(np.unique(y)) == 1:
            return TreeNode(value=int(y[0]))
        if max_depth is not None and depth >= max_depth:
            return TreeNode(value=majority_class(y))
        split = best_split(X, y)
        if split is None:
            return TreeNode(value=majority_class(y))
        (feature, threshold, gain) = split
        left_indices = X[:, feature] <= threshold
        right_indices = X[:, feature] > threshold
        if not np.any(left_indices) or not np.any(right_indices):
            return TreeNode(value=majority_class(y))
        left_child = build_tree(X[left_indices], y[left_indices], depth + 1)
        right_child = build_tree(X[right_indices], y[right_indices], depth + 1)
        return TreeNode(feature_index=feature, threshold=threshold, left=left_child, right=right_child)

    def predict_single(x: np.ndarray, node: TreeNode) -> int:
        """Predict the class label for a single sample."""
        while node.value is None:
            if x[node.feature_index] <= node.threshold:
                node = node.left
            else:
                node = node.right
        return node.value
    tree_root = build_tree(X_train, y_train)
    predictions = [predict_single(sample, tree_root) for sample in X_test]
    return predictions

