import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported_metrics = {'euclidean', 'manhattan', 'chebyshev', 'cosine'}

    def d(vec1, vec2):
        if name not in supported_metrics:
            return -1

        def to_float_array(vec):
            try:
                arr = np.array(vec, dtype=float)
                return arr
            except (ValueError, TypeError):
                return None
        arr1 = to_float_array(vec1)
        arr2 = to_float_array(vec2)
        if arr1 is None or arr2 is None:
            return -1
        if arr1.ndim != 1 or arr2.ndim != 1 or arr1.size == 0 or (arr2.size == 0):
            return -1
        if arr1.size != arr2.size:
            return -1
        if name == 'cosine':
            norm1 = np.linalg.norm(arr1)
            norm2 = np.linalg.norm(arr2)
            if norm1 == 0 or norm2 == 0:
                return -1
        if name == 'euclidean':
            distance = np.sqrt(np.sum((arr1 - arr2) ** 2))
        elif name == 'manhattan':
            distance = np.sum(np.abs(arr1 - arr2))
        elif name == 'chebyshev':
            distance = np.max(np.abs(arr1 - arr2))
        elif name == 'cosine':
            dot_product = np.dot(arr1, arr2)
            cosine_similarity = dot_product / (np.linalg.norm(arr1) * np.linalg.norm(arr2))
            distance = 1 - cosine_similarity
        else:
            return -1
        return round(float(distance), 4)
    return d

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def get_score(hand: list[int]) -> int:
        total = sum(hand)
        if 1 in hand and total + 10 <= 21:
            return total + 10
        return total

    def is_natural_blackjack(hand: list[int]) -> bool:
        return len(hand) == 2 and (1 in hand and 10 in hand)
    player_score = get_score(player)
    dealer_score = get_score(dealer)
    player_bust = player_score > 21
    dealer_bust = dealer_score > 21
    player_natural = is_natural_blackjack(player)
    dealer_natural = is_natural_blackjack(dealer)
    if player_bust:
        return -1.0
    if dealer_bust:
        return 1.0
    if player_score > dealer_score:
        if player_natural:
            return 1.5
        return 1.0
    elif player_score < dealer_score:
        return -1.0
    else:
