from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    """Return the total log-probability of *query* under an N-gram MLE model.

    Parameters
    ----------
    corpus : list[str]
        A list of training sentences. Each sentence is a plain string; tokens
        are assumed to be separated by white-spaces.
    query : str
        The sentence whose probability should be calculated.
    N : int
        The order of the N-gram model (N ≥ 1).

    Returns
    -------
    float
        The sum of natural logarithms of the probabilities of every
        consecutive N-gram occurring in *query*, rounded to 4 decimal places.
        If any necessary N-gram is unseen in the corpus the function returns
        float('-inf').
    """
    if N < 1:
        raise ValueError('N must be at least 1.')

    def generate_ngrams(tokens: List[str], N: int) -> List[tuple]:
        if N > 1:
            padded_tokens = ['<bol>'] * (N - 1) + tokens + ['<eol>']
        else:
            padded_tokens = tokens
        if len(padded_tokens) < N:
            return []
        return [tuple(padded_tokens[i:i + N]) for i in range(len(padded_tokens) - N + 1)]
    ngram_counts = Counter()
    prefix_counts = Counter()
    total_unigrams = 0
    for sentence in corpus:
        tokens = sentence.strip().split()
        if N > 1:
            ngrams = generate_ngrams(tokens, N)
            prefixes = [ngram[:-1] for ngram in ngrams]
            ngram_counts.update(ngrams)
            prefix_counts.update(prefixes)
        else:
            ngrams = generate_ngrams(tokens, N)
            ngram_counts.update(ngrams)
            total_unigrams += len(ngrams)
    query_tokens = query.strip().split()
    query_ngrams = generate_ngrams(query_tokens, N)
    if N > 1 and (not query_ngrams):
        return float('-inf')
    elif N == 1 and (not query_ngrams) and query:
        return float('-inf')
    log_prob = 0.0
    for ngram in query_ngrams:
        if N > 1:
            prefix = ngram[:-1]
            count_prefix = prefix_counts.get(prefix, 0)
            count_ngram = ngram_counts.get(ngram, 0)
            if count_ngram == 0 or count_prefix == 0:
                return float('-inf')
            prob = count_ngram / count_prefix
        else:
            count_ngram = ngram_counts.get(ngram, 0)
            if count_ngram == 0 or total_unigrams == 0:
                return float('-inf')
            prob = count_ngram / total_unigrams
        log_prob += math.log(prob)
    return round(log_prob, 4)

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries.

    Parameters
    ----------
    transactions : list[list[str]]
        A list where each element is a list of items representing one transaction.
    min_support : int
        Minimum number of transactions an item has to appear in to be kept.

    Returns
    -------
    dict
        The root node of the FP-Tree.  Each node contains two keys:
        "support" and "children" (the latter mapping item → child-node).
    """
    item_counts = Counter()
    for transaction in transactions:
        unique_items = set(transaction)
        item_counts.update(unique_items)
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return {'support': 0, 'children': {}}
    sorted_items = sorted(frequent_items, key=lambda item: (-item_counts[item], item))
    item_order = {item: idx for (idx, item) in enumerate(sorted_items)}
    fp_tree = {'support': 0, 'children': {}}
    for transaction in transactions:
        filtered_items = [item for item in set(transaction) if item in frequent_items]
        if not filtered_items:
            continue
        sorted_transaction = sorted(filtered_items, key=lambda item: item_order[item])
        current_node = fp_tree
        current_node['support'] += 1
        for item in sorted_transaction:
            children = current_node['children']
            if item in children:
                children[item]['support'] += 1
            else:
                children[item] = {'support': 1, 'children': {}}
            current_node = children[item]
    return fp_tree

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_continuous(space: Space, is_composite: bool) -> bool:
        if is_composite:
            if isinstance(space, Tuple):
                sub_spaces = space.spaces
            elif isinstance(space, Dict):
                sub_spaces = space.spaces.values()
            else:
                return False
            return all((isinstance(sub_space, Box) for sub_space in sub_spaces))
        else:
            return isinstance(space, Box)
    cont_action = check_continuous(env.action_space, tuple_action)
    cont_obs = check_continuous(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function must return the MAP predictive means for every row in
    `X_new`.  See the task description for full details.
    """
    if fit_intercept:
        intercept_train = np.ones((X.shape[0], 1))
        X = np.hstack((intercept_train, X))
        intercept_new = np.ones((X_new.shape[0], 1))
        X_new = np.hstack((intercept_new, X_new))
    M = X.shape[1]
    if np.isscalar(mu):
        mu = np.full(M, mu)
    else:
        mu = np.asarray(mu)
        if mu.shape[0] != M:
            raise ValueError(f"'mu' must be a scalar or a vector of length {M}")
    if V is None:
        V_matrix = np.eye(M)
    elif np.isscalar(V):
        V_matrix = V * np.eye(M)
    elif V.ndim == 1:
        if V.shape[0] != M:
            raise ValueError(f"'V' as a 1-D array must have length {M}")
        V_matrix = np.diag(V)
    elif V.ndim == 2:
        if V.shape[0] != M or V.shape[1] != M:
            raise ValueError(f"'V' as a matrix must be of shape ({M}, {M})")
        V_matrix = V
    else:
        raise ValueError("'V' must be a scalar, 1-D array, 2-D array, or None")
    try:
        inv_V = np.linalg.inv(V_matrix)
    except np.linalg.LinAlgError:
        raise ValueError("'V' must be invertible")
    XtX = X.T @ X
    inv_posterior_cov = inv_V + XtX
    try:
        Sigma_p = np.linalg.inv(inv_posterior_cov)
    except np.linalg.LinAlgError:
        raise ValueError('Posterior covariance matrix is not invertible')
    inv_V_mu = inv_V @ mu
    Xt_y = X.T @ y
    mu_p = Sigma_p @ (inv_V_mu + Xt_y)
    y_pred = X_new @ mu_p
    y_pred_rounded = np.round(y_pred, 4).tolist()
    return y_pred_rounded

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in {0, 1, 2}:
        return -1
    is_scalar = np.isscalar(x)
    arr = np.array(x, dtype=float)
    sigmoid_val = 1 / (1 + np.exp(-arr))
    if order == 1:
        result = sigmoid_val * (1 - sigmoid_val)
    elif order == 2:
        result = sigmoid_val * (1 - sigmoid_val) * (1 - 2 * sigmoid_val)
    else:
        result = sigmoid_val
    result = np.round(result, 4)
    if is_scalar:
        return float(result)
    else:
        return result.tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    try:
        X_np = np.array(X, dtype=float)
        if X_np.ndim != 2:
            return -1
        (N, C) = X_np.shape
        if Y is None:
            Y_np = X_np
        else:
            Y_np = np.array(Y, dtype=float)
            if Y_np.ndim != 2:
                return -1
            if Y_np.shape[1] != C:
                return -1
        if sigma is None:
            sigma_val = np.sqrt(C / 2)
            if sigma_val <= 0:
                return -1
            inv_sigma_sq = 1 / sigma_val ** 2
        elif isinstance(sigma, (float, int)):
            sigma_val = float(sigma)
            if sigma_val <= 0:
                return -1
            inv_sigma_sq = 1 / sigma_val ** 2
        elif isinstance(sigma, list):
            if len(sigma) != C:
                return -1
            sigma_array = np.array(sigma, dtype=float)
            if np.any(sigma_array <= 0):
                return -1
            inv_sigma_sq = 1 / sigma_array ** 2
        else:
            return -1
        diff = X_np[:, np.newaxis, :] - Y_np[np.newaxis, :, :]
        diff_sq = diff ** 2
        if isinstance(inv_sigma_sq, np.ndarray):
            scaled_diff_sq = diff_sq * inv_sigma_sq
        else:
            scaled_diff_sq = diff_sq * inv_sigma_sq
        sum_scaled_diff_sq = np.sum(scaled_diff_sq, axis=2)
        K = np.exp(-0.5 * sum_scaled_diff_sq)
        K_rounded = np.round(K, 4)
        return K_rounded.tolist()
    except:
        return -1

