import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """

    class FPTreeNode:

        def __init__(self, item, count, parent):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = {}
            self.link = None

        def increment(self, count):
            self.count += count

    class FPTree:

        def __init__(self):
            self.root = FPTreeNode(None, 1, None)
            self.header_table = {}

        def add_tree(self, items, count):
            current_node = self.root
            for item in items:
                if item in current_node.children:
                    current_node.children[item].increment(count)
                else:
                    new_node = FPTreeNode(item, count, current_node)
                    current_node.children[item] = new_node
                    if item in self.header_table:
                        last_node = self.header_table[item]
                        while last_node.link:
                            last_node = last_node.link
                        last_node.link = new_node
                    else:
                        self.header_table[item] = new_node
                current_node = current_node.children[item]

    def build_fp_tree(transactions, min_sup):
        item_counts = Counter()
        for transaction in transactions:
            item_counts.update(transaction)
        freq_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
        if not freq_items:
            return (None, None)
        sorted_items = sorted(freq_items.items(), key=lambda x: (-x[1], x[0]))
        item_order = {item: idx for (idx, (item, _)) in enumerate(sorted_items)}
        tree = FPTree()
        for transaction in transactions:
            filtered = [item for item in transaction if item in freq_items]
            sorted_transaction = sorted(filtered, key=lambda item: (-freq_items[item], item))
            if sorted_transaction:
                tree.add_tree(sorted_transaction, 1)
        return (tree, freq_items)

    def ascend_tree(node):
        path = []
        while node.parent and node.parent.item is not None:
            node = node.parent
            path.append(node.item)
        return path

    def find_conditional_pattern_base(node):
        conditional_patterns = []
        while node:
            path = ascend_tree(node)
            if path:
                conditional_patterns.append((path, node.count))
            node = node.link
        return conditional_patterns

    def mine_tree(tree, prefix, frequent_itemsets, min_sup):
        items = sorted(tree.header_table.items(), key=lambda x: (freq_order[x[0]], x[0]))
        for (item, node) in items:
            new_prefix = prefix.copy()
            new_prefix.append(item)
            frequent_itemsets.append(tuple(sorted(new_prefix)))
            conditional_patterns = find_conditional_pattern_base(node)
            conditional_counts = Counter()
            for (path, count) in conditional_patterns:
                conditional_counts.update(dict.fromkeys(path, count))
            conditional_freq = {i: c for (i, c) in conditional_counts.items() if c >= min_sup}
            if not conditional_freq:
                continue
            sorted_conditional = sorted(conditional_freq.items(), key=lambda x: (-x[1], x[0]))
            ordered_conditional = {item: idx for (idx, (item, _)) in enumerate(sorted_conditional)}
            conditional_transactions = []
            for (path, count) in conditional_patterns:
                filtered = [i for i in path if i in conditional_freq]
                sorted_path = sorted(filtered, key=lambda x: (-conditional_freq[x], x))
                if sorted_path:
                    conditional_transactions.append((sorted_path, count))
            conditional_tree = FPTree()
            for (sorted_path, count) in conditional_transactions:
                conditional_tree.add_tree(sorted_path, count)
            mine_tree(conditional_tree, new_prefix, frequent_itemsets, min_sup)
    (tree, freq_items) = build_fp_tree(transactions, min_sup)
    if not tree or not tree.header_table:
        return []
    freq_order = {item: count for (item, count) in freq_items.items()}
    frequent_itemsets = [tuple([item]) for item in sorted(freq_items.keys())]
    mine_tree(tree, [], frequent_itemsets, min_sup)
    unique_itemsets = sorted(set(frequent_itemsets), key=lambda x: (len(x), x))
    return unique_itemsets

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    current_node = root
    while True:
        if hasattr(current_node, 'y'):
            return current_node.y
        feature_index = current_node.label
        feature_value = features[feature_index]
        if task_type == 'classification':
            matched = False
            for child in current_node.child:
                if hasattr(child, 'x') and child.x == feature_value:
                    current_node = child
                    matched = True
                    break
            if not matched:
                current_node = current_node.child[1]
        elif task_type == 'regression':
            if feature_value <= current_node.s:
                current_node = current_node.child[0]
            else:
                current_node = current_node.child[1]
        else:
            raise ValueError("task_type must be either 'classification' or 'regression'")

import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    import math
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    if sample_size > n_samples:
        sample_size = n_samples
    if sample_size > 1:
        phi = 2 * math.log(sample_size - 1) - 2 * (sample_size - 1) / sample_size
    else:
        phi = 0

    def c(n):
        if n <= 1:
            return 0
        else:
            return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    class Tree:

        def __init__(self, data_subset, height_limit):
            self.height_limit = height_limit
            self.n_features = data_subset.shape[1]
            self.root = self.build_tree(data_subset, current_depth=0)

        def build_tree(self, data_subset, current_depth):
            if current_depth >= self.height_limit or data_subset.shape[0] <= 1:
                return {'leaf': True, 'size': data_subset.shape[0]}
            else:
                feature = np.random.randint(0, self.n_features)
                feature_values = data_subset[:, feature]
                min_val = feature_values.min()
                max_val = feature_values.max()
                if min_val == max_val:
                    return {'leaf': True, 'size': data_subset.shape[0]}
                threshold = np.random.uniform(min_val, max_val)
                left_indices = data_subset[:, feature] < threshold
                right_indices = data_subset[:, feature] >= threshold
                left = data_subset[left_indices]
                right = data_subset[right_indices]
                if left.shape[0] == 0 or right.shape[0] == 0:
                    return {'leaf': True, 'size': data_subset.shape[0]}
                return {'leaf': False, 'feature': feature, 'threshold': threshold, 'left': self.build_tree(left, current_depth + 1), 'right': self.build_tree(right, current_depth + 1)}

        def path_length(self, x):
            node = self.root
            path = 0
            while not node.get('leaf', False):
                path += 1
                feature = node['feature']
                threshold = node['threshold']
                if x[feature] < threshold:
                    node = node['left']
                else:
                    node = node['right']
            size = node['size']
            path += c(size)
            return path
    height_limit = math.ceil(math.log2(sample_size)) if sample_size > 0 else 0
    trees = []
    for _ in range(n_trees):
        if sample_size >= n_samples:
            sample_indices = np.arange(n_samples)
        else:
            sample_indices = np.random.choice(n_samples, size=sample_size, replace=False)
        data_subset = data[sample_indices]
        tree = Tree(data_subset, height_limit)
        trees.append(tree)
    path_lengths = np.zeros((n_samples, n_trees))
    for (tree_idx, tree) in enumerate(trees):
        for i in range(n_samples):
            x = data[i]
            h = tree.path_length(x)
            path_lengths[i, tree_idx] = h
    avg_h = path_lengths.mean(axis=1)
    with np.errstate(divide='ignore', invalid='ignore'):
        scores = np.power(2, -avg_h / phi) if phi != 0 else np.zeros(n_samples)
    if epsilon == 0:
        return []
    else:
        num_outliers = math.ceil(epsilon * n_samples)
        if num_outliers == 0:
            return []
        sorted_indices = np.argsort(-scores, kind='stable')
        top_indices = sorted_indices[:num_outliers]
        return sorted(top_indices.tolist())
