import math
from collections import Counter
from itertools import combinations
from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                      itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count. If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    if not transactions:
        return {}
    transactions = [set(t) for t in transactions]
    N = len(transactions)
    min_support = math.ceil(minsup * N)
    if min_support <= 0:
        return {}
    item_counts = Counter()
    for t in transactions:
        for item in t:
            item_counts[item] += 1
    L1 = []
    freq_itemsets = {}
    for (item, count) in item_counts.items():
        if count >= min_support:
            itemset_tuple = (item,)
            freq_itemsets[itemset_tuple] = count
            L1.append(itemset_tuple)
    L1.sort()
    Lk = L1
    k = 2
    while Lk:
        Ck = []
        Lk_set = set(Lk)
        for i in range(len(Lk)):
            for j in range(i + 1, len(Lk)):
                p = Lk[i]
                q = Lk[j]
                if p[:k - 2] == q[:k - 2]:
                    candidate = tuple(sorted(set(p).union(q)))
                    if len(candidate) == k:
                        all_subsets_frequent = True
                        for subset in combinations(candidate, k - 1):
                            if tuple(sorted(subset)) not in Lk_set:
                                all_subsets_frequent = False
                                break
                        if all_subsets_frequent:
                            Ck.append(candidate)
                else:
                    break
        candidate_count = Counter()
        for t in transactions:
            for c in Ck:
                if set(c).issubset(t):
                    candidate_count[c] += 1
        Lk_new = []
        for c in Ck:
            if candidate_count[c] >= min_support:
                freq_itemsets[c] = candidate_count[c]
                Lk_new.append(c)
        Lk_new.sort()
        Lk = Lk_new
        k += 1
    return freq_itemsets

def check_data(a, b):
    """Validate and convert the two inputs so that both are numeric.

    The function must accept integers, floats, or numeric strings.  Any string
    should be converted with `float()`; existing numeric values stay as they
    are.  The pair `(a, b)` must then be returned.
    """

    def convert_if_string(x):
        return float(x) if isinstance(x, str) else x
    a = convert_if_string(a)
    b = convert_if_string(b)
    return (a, b)
def validate_input(function):

    def wrapper(a, b):
        (a, b) = check_data(a, b)
        return function(a, b)
    return wrapper
@validate_input
def sum_numbers(a, b):
    """Return the arithmetic sum of *a* and *b* after validation."""
    total = a + b
    return int(total) if total.is_integer() else total

from typing import List, Optional
class Node:
    """Binary decision-tree node (provided).

    Non-leaf node:   rule = (feature_index, threshold)
    Leaf node:       rule = (None, prediction_value)
    """

    def __init__(self, left: Optional['Node'], right: Optional['Node'], rule: tuple):
        self.left = left
        self.right = right
        self.feature = rule[0]
        self.threshold = rule[1]
def predict_tree(root: Optional[Node], X: List[List[float]]) -> List[float]:
    """Return a list of predictions for each sample in X using the given tree."""
    if root is None:
        return []

    def _predict_single(node: Node, sample: List[float]) -> float:
        if node.left is None and node.right is None:
            return node.threshold
        if sample[node.feature] <= node.threshold:
            return _predict_single(node.left, sample)
        else:
            return _predict_single(node.right, sample)
    predictions = [_predict_single(root, sample) for sample in X]
    return predictions

from typing import List, Union
Number = Union[int, float]
def k_smallest(nums: List[Number], k: int) -> Union[List[Number], int]:
    """Return the k smallest numbers contained in *nums* in ascending order,
    maintaining a custom max-heap of size at most k during one pass over nums.
    Return -1 if k is invalid (≤ 0 or > len(nums)).

    The max-heap is implemented using a Python list and
    two helper procedures _move_up (for inserts) and _move_down (for re-heaping
    after root replacement).
    """
    if k <= 0 or k > len(nums):
        return -1

    def _move_up(heap: List[Number], idx: int) -> None:
        """Move the item at index idx up the max-heap until the heap property is satisfied."""
        while idx > 0:
            parent = (idx - 1) // 2
            if heap[idx] > heap[parent]:
                (heap[idx], heap[parent]) = (heap[parent], heap[idx])
                idx = parent
            else:
                break

    def _move_down(heap: List[Number], idx: int) -> None:
        """Move the item at index idx down the max-heap until the heap property is satisfied."""
        size = len(heap)
        while True:
            left = 2 * idx + 1
            right = 2 * idx + 2
            largest = idx
            if left < size and heap[left] > heap[largest]:
                largest = left
            if right < size and heap[right] > heap[largest]:
                largest = right
            if largest == idx:
                break
            (heap[idx], heap[largest]) = (heap[largest], heap[idx])
            idx = largest
    heap: List[Number] = []
    for num in nums:
        if len(heap) < k:
            heap.append(num)
            _move_up(heap, len(heap) - 1)
        elif num < heap[0]:
            heap[0] = num
            _move_down(heap, 0)
    heap.sort()
    return heap

import numpy as np
from collections import Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*.

    The return value must be rounded to 4 decimal places. If any required
    count is zero you should immediately return float('-inf').
    """
    if len(sequence) < N:
        return round(0.0, 4)
    if N == 1:
        total_tokens = len(corpus)
        unigram_counts = Counter(corpus)
        log_prob = 0.0
        for w in sequence:
            count_w = unigram_counts[w]
            if count_w == 0:
                return float('-inf')
            log_prob += np.log(count_w / total_tokens)
        return round(log_prob, 4)
    else:
        ngram_counts = Counter()
        prefix_counts = Counter()
        for i in range(len(corpus) - N + 1):
            ngram = tuple(corpus[i:i + N])
            ngram_counts[ngram] += 1
            prefix = tuple(corpus[i:i + N - 1])
            prefix_counts[prefix] += 1
        log_prob = 0.0
        for i in range(len(sequence) - N + 1):
            current_ngram = tuple(sequence[i:i + N])
            current_prefix = tuple(sequence[i:i + N - 1])
            numerator = ngram_counts[current_ngram]
            denominator = prefix_counts[current_prefix]
            if numerator == 0 or denominator == 0:
                return float('-inf')
            log_prob += np.log(numerator / denominator)
        return round(log_prob, 4)

import numpy as np
def compute_impurity(y, criterion):
    """Compute a node's impurity.

    Args:
        y (numpy.ndarray): 1-D array with the target values.
        criterion (str): One of {"entropy", "gini", "mse"}.

    Returns:
        float: Impurity value rounded to 4 decimal places.
    """
    if y.size == 0:
        return 0.0
    if criterion == 'entropy':
        (unique_values, counts) = np.unique(y, return_counts=True)
        p = counts / y.size
        entropy = 0.0
        for prob in p:
            if prob > 0:
                entropy -= prob * np.log2(prob)
        return float(round(entropy, 4))
    elif criterion == 'gini':
        (unique_values, counts) = np.unique(y, return_counts=True)
        p = counts / y.size
        gini = 1.0 - np.sum(p ** 2)
        return float(round(gini, 4))
    elif criterion == 'mse':
        mean_value = np.mean(y)
        mse = np.mean((y - mean_value) ** 2)
        return float(round(mse, 4))
    else:
        raise ValueError("Criterion must be one of {'entropy', 'gini', 'mse'}.")

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities (length n).
        A (list[list[float]]): State–transition probability matrix (n×n).
        B (list[list[float]]): Emission probability matrix (n×m).
        observations (list[int]): Observation index sequence (each in [0, m-1]).

    Returns:
        float: The likelihood of the observation sequence under the HMM,
               rounded to 4 decimal places, or −1 for invalid input.
    """
    n = len(S)
    if n == 0:
        return -1
    if len(A) != n:
        return -1
    for row in A:
        if len(row) != n:
            return -1
    if len(B) != n:
        return -1
    if n > 0:
        m = len(B[0])
        if m == 0:
            return -1
        for row in B:
            if len(row) != m:
                return -1
    else:
        return -1
    T = len(observations)
    if T == 0:
        return -1
    for obs in observations:
        if obs < 0 or obs >= m:
            return -1
    S = np.array(S, dtype=float)
    A = np.array(A, dtype=float)
    B = np.array(B, dtype=float)
    alpha = np.zeros((T, n), dtype=float)
    first_obs = observations[0]
    for i in range(n):
        alpha[0, i] = S[i] * B[i, first_obs]
    for t in range(1, T):
        obs_t = observations[t]
        for j in range(n):
            alpha[t, j] = np.sum((alpha[t - 1, i] * A[i, j] for i in range(n)))
            alpha[t, j] *= B[j, obs_t]
    likelihood = np.sum(alpha[T - 1, :])
    return round(float(likelihood), 4)

import numpy as np
def relu_(Z):
    """Apply the element-wise Rectified Linear Unit (ReLU) activation.

    The function keeps the container type of the input:
    scalar → scalar, list → list, NumPy array → NumPy array.

    Args:
        Z (int | float | list | numpy.ndarray): Input data – scalar, 1-D or 2-D.

    Returns:
        Same type as *Z* with all negative values clipped to 0.
    """
    if isinstance(Z, (int, float)):
        return max(0, Z)
    if isinstance(Z, np.ndarray):
        return np.maximum(Z, 0)
    arr = np.array(Z, dtype=float)
    arr_relu = np.maximum(arr, 0)
    return arr_relu.tolist()

import numpy as np
def linear(z):
    """Linear (identity) activation function.

    Args:
        z: Scalar, list, tuple or NumPy ndarray.

    Returns:
        The same value(s) as *z*, obeying the rules:
            • Scalars are returned unchanged.
            • Non-scalar inputs are returned as a NumPy ndarray.
    """
    if isinstance(z, (int, float)):
        return z
    elif isinstance(z, np.ndarray):
        return z
    else:
        return np.array(z)

import numpy as np
def best_arm(payoff_probs: list[float]) -> tuple[float, int]:
    """Find the arm with the highest expected reward in a Bernoulli bandit.

    Parameters
    ----------
    payoff_probs : list[float]
        A list where each element is the success probability of the
        corresponding arm. Probabilities must lie in the interval [0, 1].

    Returns
    -------
    tuple
        (max_expected_reward, best_arm_index).  If the input list is empty or
        contains an invalid probability (<0 or >1), the function must return
        (-1.0, -1).
    """
    if not payoff_probs:
        return (-1.0, -1)
    for p in payoff_probs:
        if p < 0 or p > 1:
            return (-1.0, -1)
    best_arm_index = np.argmax(payoff_probs)
    max_expected_reward = payoff_probs[best_arm_index]
    return (float(max_expected_reward), int(best_arm_index))

from collections import deque
import math
def escape_fire_maze(grid: list[str]) -> int:
    """Escape from a maze with spreading fire.

    Parameters
    ----------
    grid : list[str]
        Square maze represented as a list of strings. Each character must be
        '.', '#', or 'F'.  The agent starts at the upper-left corner (0,0) and
        wishes to reach the lower-right corner (n-1,n-1).  Fire starts in every
        cell marked with 'F' and spreads to the four neighbouring cells every
        minute.  The fire spreads first, then the agent moves.

    Returns
    -------
    int
        Minimum number of minutes required for the agent to reach the goal
        without entering a burning cell, or -1 if this is impossible.
    """
    n = len(grid)
    if n == 0:
        return -1
    if n == 1:
        return 0 if grid[0][0] == '.' else -1
    maze = [list(row) for row in grid]
    if maze[0][0] == '#' or maze[n - 1][n - 1] == '#':
        return -1
    if maze[0][0] == 'F' or maze[n - 1][n - 1] == 'F':
        return -1
    directions = [(1, 0), (-1, 0), (0, 1), (0, -1)]
    fire_time = [[math.inf] * n for _ in range(n)]
    queue_fire = deque()
    for r in range(n):
        for c in range(n):
            if maze[r][c] == 'F':
                fire_time[r][c] = 0
                queue_fire.append((r, c, 0))
    while queue_fire:
        (r, c, t) = queue_fire.popleft()
        for (dr, dc) in directions:
            (nr, nc) = (r + dr, c + dc)
            if 0 <= nr < n and 0 <= nc < n:
                if maze[nr][nc] != '#' and fire_time[nr][nc] > t + 1:
                    fire_time[nr][nc] = t + 1
                    queue_fire.append((nr, nc, t + 1))
    if fire_time[0][0] == 0:
        return -1
    visited = [[False] * n for _ in range(n)]
    queue_agent = deque()
    visited[0][0] = True
    queue_agent.append((0, 0, 0))
    while queue_agent:
        (r, c, t) = queue_agent.popleft()
        if r == n - 1 and c == n - 1:
            return t
        for (dr, dc) in directions:
            (nr, nc) = (r + dr, c + dc)
            if 0 <= nr < n and 0 <= nc < n:
                if maze[nr][nc] != '#' and (not visited[nr][nc]):
                    if fire_time[nr][nc] > t + 1:
                        visited[nr][nc] = True
                        queue_agent.append((nr, nc, t + 1))
    return -1

import numpy as np
def _sigmoid(X: np.ndarray) -> np.ndarray:
    return 1.0 / (1.0 + np.exp(-X))
def _sigmoid_derivative(A: np.ndarray) -> np.ndarray:
    return A * (1.0 - A)
def _tanh(X: np.ndarray) -> np.ndarray:
    return np.tanh(X)
def _tanh_derivative(A: np.ndarray) -> np.ndarray:
    return 1.0 - A ** 2
def _relu(X: np.ndarray) -> np.ndarray:
    return np.maximum(0, X)
def _relu_derivative(A: np.ndarray) -> np.ndarray:
    return (A > 0).astype(A.dtype)
def _softmax(X: np.ndarray) -> np.ndarray:
    shift = X - np.max(X, axis=-1, keepdims=True)
    exps = np.exp(shift)
    sums = np.sum(exps, axis=-1, keepdims=True)
    return exps / sums
def _softmax_derivative(A: np.ndarray) -> np.ndarray:
    return np.ones_like(A)
def activation_forward_backward(X: np.ndarray, activation: str, upstream_grad: np.ndarray) -> tuple[np.ndarray, np.ndarray]:
    """
    Apply an activation function and compute its local gradient.

    Parameters
    ----------
    X : np.ndarray
        Raw pre-activation values.
    activation : str
        Name of the activation function. Supported values are
        "sigmoid", "tanh", "relu", and "softmax".
    upstream_grad : np.ndarray
        Gradient that arrives from the layer above (must have the same shape as `X`).

    Returns
    -------
    tuple[np.ndarray, np.ndarray]
        A two-element tuple `(A, grad)` where:
          • `A`    – the activation applied to `X` (rounded to 4 decimals).
          • `grad` – the element-wise product of `upstream_grad` and the local
                     derivative of the activation (rounded to 4 decimals).
    """
    activations = {'sigmoid': (_sigmoid, _sigmoid_derivative), 'tanh': (_tanh, _tanh_derivative), 'relu': (_relu, _relu_derivative), 'softmax': (_softmax, _softmax_derivative)}
    if activation not in activations:
        raise ValueError(f"Unsupported activation '{activation}'. Choose from 'sigmoid', 'tanh', 'relu', or 'softmax'.")
    (forward_func, derivative_func) = activations[activation]
    A = forward_func(X)
    dA = derivative_func(A)
    grad = upstream_grad * dA
    A = np.round(A, 4)
    grad = np.round(grad, 4)
    return (A, grad)

def accuracy_score(y_true, y_pred):
    """Return the classification accuracy between *y_true* and *y_pred*.

    The function must:
      • return -1 if the two containers have different lengths;
      • return 0 when both containers are empty;
      • otherwise return the proportion of matching elements rounded to
        4 decimal places.

    Args:
        y_true (list | tuple): Ground-truth class labels.
        y_pred (list | tuple): Predicted class labels.

    Returns:
        float | int: The accuracy or -1 according to the rules above.
    """
    if len(y_true) != len(y_pred):
        return -1
    if len(y_true) == 0:
        return 0
    correct_matches = sum((1 for (true_label, pred_label) in zip(y_true, y_pred) if true_label == pred_label))
    accuracy = correct_matches / len(y_true)
    return round(accuracy, 4)

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """
    Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    class Node:
        """Tree node storing feature, threshold, children, or a leaf value."""

        def __init__(self, feature=None, threshold=None, left=None, right=None, *, value=None):
            self.feature = feature
            self.threshold = threshold
            self.left = left
            self.right = right
            self.value = value

        def is_leaf_node(self):
            return self.value is not None

    def gini_impurity(labels: np.ndarray) -> float:
        """Compute Gini impurity for a 1D array of class labels."""
        if len(labels) == 0:
            return 0.0
        counts = np.bincount(labels)
        freqs = counts / len(labels)
        return 1.0 - np.sum(freqs ** 2)

    def majority_class(labels: np.ndarray) -> int:
        """Return the majority class label in the given array."""
        return np.bincount(labels).argmax()

    def find_best_split(X: np.ndarray, y: np.ndarray):
        """
        Find the best (feature, threshold) split based on Gini gain.
        Returns (best_feature, best_threshold, best_gain).
        If no split improves Gini, returns (None, None, 0).
        """
        (n_samples, n_features) = X.shape
        if n_samples <= 1:
            return (None, None, 0)
        parent_gini = gini_impurity(y)
        best_gain = 0.0
        best_feature = None
        best_threshold = None
        for feature_idx in range(n_features):
            sorted_indices = np.argsort(X[:, feature_idx])
            x_sorted = X[sorted_indices, feature_idx]
            y_sorted = y[sorted_indices]
            for i in range(1, n_samples):
                if x_sorted[i] == x_sorted[i - 1]:
                    continue
                threshold = (x_sorted[i] + x_sorted[i - 1]) / 2.0
                left_mask = x_sorted <= threshold
                right_mask = ~left_mask
                (y_left, y_right) = (y_sorted[left_mask], y_sorted[right_mask])
                if len(y_left) == 0 or len(y_right) == 0:
                    continue
                left_gini = gini_impurity(y_left)
                right_gini = gini_impurity(y_right)
                weighted_child_gini = len(y_left) / n_samples * left_gini + len(y_right) / n_samples * right_gini
                gain = parent_gini - weighted_child_gini
                if gain > best_gain:
                    best_gain = gain
                    best_feature = feature_idx
                    best_threshold = threshold
        return (best_feature, best_threshold, best_gain)

    def build_tree(X: np.ndarray, y: np.ndarray, depth: int=0) -> Node:
        """
        Recursively build a decision tree node.
        """
        if len(np.unique(y)) == 1:
            return Node(value=y[0])
        if max_depth is not None and depth >= max_depth:
            return Node(value=majority_class(y))
        (feature, threshold, gain) = find_best_split(X, y)
        if gain == 0 or feature is None:
            return Node(value=majority_class(y))
        left_indices = X[:, feature] <= threshold
        right_indices = ~left_indices
        if not np.any(left_indices) or not np.any(right_indices):
            return Node(value=majority_class(y))
        left_child = build_tree(X[left_indices], y[left_indices], depth + 1)
        right_child = build_tree(X[right_indices], y[right_indices], depth + 1)
        return Node(feature=feature, threshold=threshold, left=left_child, right=right_child)

    def predict_row(x: np.ndarray, node: Node) -> int:
        """Traverse the tree and predict the class for a single sample x."""
        if node.is_leaf_node():
            return node.value
        if x[node.feature] <= node.threshold:
            return predict_row(x, node.left)
        else:
            return predict_row(x, node.right)
    root = build_tree(X_train, y_train, depth=0)
    predictions = []
    for x in X_test:
        pred = predict_row(x, root)
        predictions.append(int(pred))
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    valid_names = {'euclidean', 'manhattan', 'chebyshev', 'cosine'}

    def distance_fn(x, y):
        if name not in valid_names:
            return -1
        try:
            x_arr = np.asarray(x, dtype=float)
            y_arr = np.asarray(y, dtype=float)
        except (ValueError, TypeError):
            return -1
        if x_arr.ndim != 1 or y_arr.ndim != 1:
            return -1
        if x_arr.size == 0 or y_arr.size == 0 or x_arr.size != y_arr.size:
            return -1
        if name == 'euclidean':
            dist = np.sqrt(np.sum((x_arr - y_arr) ** 2))
        elif name == 'manhattan':
            dist = np.sum(np.abs(x_arr - y_arr))
        elif name == 'chebyshev':
            dist = np.max(np.abs(x_arr - y_arr))
        elif name == 'cosine':
            norm_x = np.linalg.norm(x_arr)
            norm_y = np.linalg.norm(y_arr)
            if norm_x == 0 or norm_y == 0:
                return -1
            dist = 1 - np.dot(x_arr, y_arr) / (norm_x * norm_y)
        else:
            return -1
        return round(dist, 4)
    return distance_fn

import numpy as np
import math
def num_missing(data) -> int:
    """Count missing values in a 1-D iterable.

    An element is missing if it is ``None`` or ``NaN``.

    Args:
        data: Any 1-D iterable (list, tuple, NumPy array, etc.).

    Returns:
        int: The number of missing elements.
    """
    arr = np.array(data)
    if arr.size == 0:
        return 0
    missing_count = 0
    for item in arr:
        if item is None:
            missing_count += 1
        else:
            try:
                if math.isnan(item):
                    missing_count += 1
            except (TypeError, ValueError):
                pass
    return missing_count

import numpy as np
def one_hot_encode(labels: list[str], categories: list[str] | None=None) -> list[list[int]]:
    """Convert categorical labels to a one-hot encoded matrix.

    Args:
        labels: A list of categorical labels of length *N*.
        categories: Optional complete list of unique categories of length *C*.
            If *None*, the set of categories is inferred from *labels* in the
            order of their first appearance.

    Returns:
        A list of lists that represents the one-hot encoded matrix of shape
        (N, C) where *C* is the number of categories. If an unknown label is
        found when *categories* is supplied, the function returns -1.
    """
    if categories is None:
        categories = []
        for label in labels:
            if label not in categories:
                categories.append(label)
    category_to_index = {cat: i for (i, cat) in enumerate(categories)}
    for label in labels:
        if label not in category_to_index:
            return -1
    encoded = np.zeros((len(labels), len(categories)), dtype=int)
    for (i, label) in enumerate(labels):
        encoded[i, category_to_index[label]] = 1
    return encoded.tolist()

import functools
from typing import Callable, Any
def doublewrap(decorator_func: Callable[..., Callable[..., Any]]) -> Callable[..., Any]:
    """
    Return a decorator that can be used *with* or *without* parentheses.

    Args:
        decorator_func: A callable that expects the function to decorate as
            its first positional argument followed only by *optional*
            positional or keyword arguments.

    Returns:
        A new decorator that forwards the call in the correct form so that
        ``decorator_func`` can be applied either directly or after receiving
        its own optional parameters.
    """

    @functools.wraps(decorator_func)
    def wrapper(*args, **kwargs):
        if len(args) == 1 and callable(args[0]) and (not kwargs):
            return decorator_func(args[0])
        else:

            @functools.wraps(decorator_func)
            def real_decorator(func):
                return decorator_func(func, *args, **kwargs)
            return real_decorator
    return wrapper

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def best_score(hand: list[int]) -> int:
        """Return the best blackjack score for a hand, counting at most one Ace as 11 if beneficial."""
        total = sum(hand)
        if 1 in hand and total + 10 <= 21:
            total += 10
        return total

    def is_natural_blackjack(hand: list[int]) -> bool:
        """Return True if the hand is exactly two cards: one Ace and one Ten-value card."""
        return len(hand) == 2 and 1 in hand and (10 in hand)
    player_score = best_score(player)
    dealer_score = best_score(dealer)
    if player_score > 21:
        return -1.0
    if dealer_score > 21:
        return 1.0
    if player_score > dealer_score:
        if is_natural_blackjack(player):
            return 1.5
        return 1.0
    elif player_score < dealer_score:
        return -1.0
    else:
        return 0.0

import numpy as np
def sigmoid(z):
    """Compute the element-wise sigmoid (logistic) function.

    The function must work for scalar numbers, Python lists and NumPy arrays and
    must be numerically stable for very large positive or negative inputs.

    Args:
        z: A scalar (int/float) or array-like object (list or np.ndarray)
           containing numeric values.

    Returns:
        float | list: The sigmoid of the input rounded to 4 decimal places.
        If `z` is a scalar the return value is a float, otherwise it is a Python
        list with the same nested structure.
    """

    def _sigmoid_scalar(x):
        if x >= 0:
            return 1.0 / (1.0 + np.exp(-x))
        else:
            return np.exp(x) / (1.0 + np.exp(x))
    if np.isscalar(z):
        return round(_sigmoid_scalar(float(z)), 4)
    else:
        arr = np.array(z, dtype=float)
        res = np.empty_like(arr, dtype=float)
        pos_mask = arr >= 0
        neg_mask = ~pos_mask
        res[pos_mask] = 1.0 / (1.0 + np.exp(-arr[pos_mask]))
        exp_z = np.exp(arr[neg_mask])
        res[neg_mask] = exp_z / (1.0 + exp_z)
        return np.round(res, 4).tolist()

from typing import List
def score(hand: List[str]) -> int:
    """Return the Blackjack score of *hand* or 0 if the hand is bust.

    Args:
        hand: A list of card labels ('2'-'10', 'J', 'Q', 'K', 'A').

    Returns:
        An integer between 0 and 21 (inclusive).  If every possible value of
        the hand exceeds 21 the function returns 0.
    """
    total = 0
    aces = 0
    for card in hand:
        if card in {'J', 'Q', 'K'}:
            total += 10
        elif card == 'A':
            aces += 1
            total += 1
        else:
            total += int(card)
    for _ in range(aces):
        if total + 10 <= 21:
            total += 10
        else:
            break
    return total if total <= 21 else 0

import numpy as np
def create_encoding_dicts(obs_ids: list, action_ids: list, obs_encoder: dict | None=None, act_encoder: dict | None=None) -> dict:
    """Generate forward and backward mappings for observations and actions.

    Args:
        obs_ids (list): All distinct observation identifiers.
        action_ids (list): All distinct action identifiers.
        obs_encoder (dict | None, optional): Existing obs→number mapping.
        act_encoder (dict | None, optional): Existing act→number mapping.

    Returns:
        dict: A dictionary that contains the keys 'obs2num', 'num2obs',
        'act2num', and 'num2act'.
    """
    if obs_encoder is not None:
