import numpy as np
def sigmoid(x):
    """Element-wise logistic sigmoid, rounded to 4 decimals."""
    if np.isscalar(x):
        val = 1.0 / (1.0 + np.exp(-x))
        return float(np.round(val, 4))
    arr = np.array(x, dtype=float)
    val = 1.0 / (1.0 + np.exp(-arr))
    return np.round(val, 4)
def tanh(x):
    """Element-wise hyperbolic tangent, rounded to 4 decimals."""
    if np.isscalar(x):
        val = np.tanh(x)
        return float(np.round(val, 4))
    arr = np.array(x, dtype=float)
    val = np.tanh(arr)
    return np.round(val, 4)
def relu(x):
    """Element-wise Rectified Linear Unit, rounded to 4 decimals."""
    if np.isscalar(x):
        val = x if x > 0 else 0
        return float(np.round(val, 4))
    arr = np.array(x, dtype=float)
    val = np.maximum(arr, 0)
    return np.round(val, 4)
def leaky_relu(x, alpha: float=0.01):
    """Element-wise Leaky-ReLU with slope alpha for x<0, rounded to 4 decimals."""
    if np.isscalar(x):
        val = x if x >= 0 else alpha * x
        return float(np.round(val, 4))
    arr = np.array(x, dtype=float)
    val = np.where(arr >= 0, arr, alpha * arr)
    return np.round(val, 4)
def softmax(x):
    """Numerically stable softmax over a 1-D input, rounded to 4 decimals."""
    if np.isscalar(x):
        return float(np.round(1.0, 4))
    arr = np.array(x, dtype=float)
    shifted = arr - np.max(arr)
    exp_vals = np.exp(shifted)
    sums = np.sum(exp_vals)
    val = exp_vals / sums
    return np.round(val, 4)
def get_activation(name):
    """
    Return the activation function by name.

    Args:
        name (str): One of {"sigmoid", "tanh", "relu", "leaky_relu", "softmax"}

    Returns:
        Callable: the requested activation function.

    Raises:
        ValueError: If name is not recognised.
    """
    if name == 'sigmoid':
        return sigmoid
    elif name == 'tanh':
        return tanh
    elif name == 'relu':
        return relu
    elif name == 'leaky_relu':
        return leaky_relu
    elif name == 'softmax':
        return softmax
    else:
        raise ValueError('Invalid activation function.')

import random
def sample_replay_memory(memory: list, batch_size: int) -> list:
    """Randomly samples *batch_size* transitions from the replay buffer.

    The selection must be deterministic (use random.seed(42)).  
    If *batch_size* exceeds the buffer length, return -1.
    """
    if batch_size > len(memory):
        return -1
    random.seed(42)
    return random.sample(memory, batch_size)

import numpy as np
from itertools import combinations, permutations
def random_unweighted_graph(n_vertices: int, edge_prob: float=0.5, directed: bool=False) -> list[list[int]]:
    """Generate an un-weighted Erdős–Rényi random graph.

    The function must return the adjacency matrix (a nested list) of the graph.

    Args:
        n_vertices (int): Number of vertices in the graph (labelled 0..n-1).
        edge_prob  (float): Probability *p* that an admissible edge exists.
        directed   (bool):  Whether the graph is directed (default ``False``).

    Returns:
        list[list[int]]: The adjacency matrix *A* where *A[i][j] == 1* iff an
        edge from vertex *i* to vertex *j* exists. The matrix must consist of
        integers (0/1) only and have shape *(n_vertices, n_vertices)*.
    """
    if n_vertices == 1:
        return [[0]]
    adjacency_matrix = [[0] * n_vertices for _ in range(n_vertices)]
    if directed:
        for (i, j) in permutations(range(n_vertices), 2):
            if i != j:
                if np.random.rand() <= edge_prob:
                    adjacency_matrix[i][j] = 1
    else:
        for (i, j) in combinations(range(n_vertices), 2):
            if np.random.rand() <= edge_prob:
                adjacency_matrix[i][j] = 1
                adjacency_matrix[j][i] = 1
    return adjacency_matrix

import math
from collections import Counter
def best_split(feature, target):
    """Determine the numerical threshold that produces the highest information gain.

    Parameters
    ----------
    feature : list[int | float] | 1-D numpy.ndarray
        Numerical values of a single attribute.
    target  : list[int] | 1-D numpy.ndarray
        Corresponding class labels.

    Returns
    -------
    tuple
        (threshold, information_gain) where
        * threshold – float rounded to 4 decimals or None when no useful split exists;
        * information_gain – float rounded to 4 decimals.
    """

    def compute_entropy(freq):
        total = sum(freq)
        if total == 0:
            return 0.0
        entropy = 0.0
        for f in freq:
            if f > 0:
                p = f / total
                entropy -= p * math.log2(p)
        return entropy
    n = len(feature)
    if n == 0:
        return (None, 0.0)
    data_sorted = sorted(zip(feature, target), key=lambda x: x[0])
    sorted_x = [row[0] for row in data_sorted]
    sorted_y = [row[1] for row in data_sorted]
    if sorted_x[0] == sorted_x[-1]:
        return (None, 0.0)
    total_counts = Counter(sorted_y)
    parent_entropy = 0.0
    for cnt in total_counts.values():
        p = cnt / n
        parent_entropy -= p * math.log2(p)
    if parent_entropy == 0.0:
        return (None, 0.0)
    class_labels = sorted(list(total_counts.keys()))
    label_to_index = {c: idx for (idx, c) in enumerate(class_labels)}
    k = len(class_labels)
    prefix_count = [[0] * k for _ in range(n + 1)]
    for i in range(n):
        for c_idx in range(k):
            prefix_count[i + 1][c_idx] = prefix_count[i][c_idx]
        c = sorted_y[i]
        c_idx = label_to_index[c]
        prefix_count[i + 1][c_idx] += 1
    best_threshold = None
    best_ig = 0.0
    for i in range(n - 1):
        if sorted_x[i] != sorted_x[i + 1]:
            t_candidate = 0.5 * (sorted_x[i] + sorted_x[i + 1])
            left_freq = prefix_count[i + 1]
            right_freq = [total_counts[label] - left_freq[label_to_index[label]] for label in class_labels]
            left_size = i + 1
            right_size = n - left_size
            h_left = compute_entropy(left_freq)
            h_right = compute_entropy(right_freq)
            ig_candidate = parent_entropy - left_size / n * h_left - right_size / n * h_right
            if ig_candidate > best_ig:
                best_ig = ig_candidate
                best_threshold = t_candidate
            elif abs(ig_candidate - best_ig) < 1e-15:
                if t_candidate < best_threshold:
                    best_threshold = t_candidate
    if best_threshold is None or best_ig <= 0.0:
        return (None, 0.0)
    return (round(best_threshold, 4), round(best_ig, 4))

def evaluate_decision_tree(tree: dict, sample: list[float]):
    """Traverse a binary decision-tree represented by nested dictionaries.

    Args:
        tree (dict): Root node of the decision tree. Internal nodes contain
            'feature_i', 'threshold', 'true_branch', 'false_branch'. Leaf
            nodes contain only 'value'.
        sample (list[float]): Feature vector that will be classified/regressed.

    Returns:
        The value stored in the reached leaf (int, float, or str).
    """
    if 'value' in tree:
        return tree['value']
    feature = tree['feature_i']
    threshold = tree['threshold']
    if sample[feature] <= threshold:
        return evaluate_decision_tree(tree['true_branch'], sample)
    else:
        return evaluate_decision_tree(tree['false_branch'], sample)

import numpy as np
def L1Regularization(alpha: float):
    """Create a callable object that implements L1 regularisation.

    The returned object must support two operations:
    1. call with a NumPy array to obtain the L1 penalty value
    2. call its .grad(w) method to obtain the gradient of the penalty

    Args:
        alpha: Non-negative float controlling the strength of the regularisation.

    Returns:
        An instance that fulfils the described interface.
    """

    class L1RegularizationImpl:

        def __init__(self, alpha):
            self.alpha = alpha

        def __call__(self, w):
            if self.alpha == 0.0:
                return 0.0
            return float(self.alpha * np.sum(np.abs(w)))

        def grad(self, w):
            if self.alpha == 0.0:
                return np.zeros_like(w)
            return self.alpha * np.sign(w)
    return L1RegularizationImpl(alpha)

import math
def running_stats(data: list[int | float]) -> tuple[float, float, float]:
    """Return the running mean, variance and standard deviation of a stream.

    The computation must be *online*: iterate over the data once while
    keeping only a constant amount of memory (do not call numpy.mean,
    numpy.var, statistics.mean, etc.).  Implement Welford's method as
    explained in the task description.  All three returned numbers must
    be rounded to 4 decimal places.  If the input list is empty return
    (0.0, 0.0, 0.0).
    """
    if not data:
        return (0.0, 0.0, 0.0)
    n = 0
    m = 0.0
    s = 0.0
    for x in data:
        n += 1
        delta = x - m
        m += delta / n
        s += delta * (x - m)
    if n > 1:
        var = s / (n - 1)
    else:
        var = 0.0
    std = math.sqrt(var)
    return (round(m, 4), round(var, 4), round(std, 4))

import numpy as np
def squared_error(actual, predicted):
    """Calculate the squared error or mean squared error.

    If *actual* and *predicted* are scalars, return the squared difference.
    If they are one-dimensional iterables (same length), return the mean squared
    error. Return -1 for length mismatches or empty iterables.

    All results must be rounded to 4 decimal places.
    """
    if isinstance(actual, (int, float)) and isinstance(predicted, (int, float)):
        return round((actual - predicted) ** 2, 4)
    try:
        arr_actual = np.array(actual, dtype=float)
        arr_predicted = np.array(predicted, dtype=float)
    except (TypeError, ValueError):
        return -1
    if arr_actual.shape != arr_predicted.shape or arr_actual.size == 0:
        return -1
    mse_value = np.mean((arr_actual - arr_predicted) ** 2)
    return round(mse_value, 4)

from typing import Any, Dict, List, Tuple
def to_networkx(G: Dict[str, Any]) -> Dict[Any, List[Tuple[Any, float]]]:
    """Convert a custom graph dictionary into a plain adjacency dictionary.

    Parameters
    ----------
    G : dict
        A graph represented as a dictionary with the keys:
          * 'is_directed' (bool)
          * '_V2I'        (dict mapping vertex label -> index)
          * '_G'          (list of adjacency lists, each edge is a
                           tuple (source_label, target_label, weight))

    Returns
    -------
    dict
        A dictionary where each key is a vertex label and the value is a list
        of `(neighbour, weight)` tuples.  For undirected graphs every edge
        appears once in each direction and the neighbour lists are sorted
        alphabetically.
    """
    if not G['_V2I']:
        return {}
    adj = {v: [] for v in G['_V2I'].keys()}
    is_directed = G['is_directed']
    edges_seen = set()
    for adj_list in G['_G']:
        for (u, v, w) in adj_list:
            if is_directed:
                adj[u].append((v, w))
            else:
                if u < v:
                    key = (u, v, w)
                else:
                    key = (v, u, w)
                if key not in edges_seen:
                    edges_seen.add(key)
                    adj[u].append((v, w))
                    adj[v].append((u, w))
    for u in adj:
        adj[u].sort(key=lambda x: x[0])
    return adj

import math
from collections import Counter
from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    """Return the total log-probability of *query* under an N-gram MLE model.

    Parameters
    ----------
    corpus : list[str]
        A list of training sentences. Each sentence is a plain string; tokens
        are assumed to be separated by white-spaces.
    query : str
        The sentence whose probability should be calculated.
    N : int
        The order of the N-gram model (N ≥ 1).

    Returns
    -------
    float
        The sum of natural logarithms of the probabilities of every
        consecutive N-gram occurring in *query*, rounded to 4 decimal places.
        If any necessary N-gram is unseen in the corpus the function returns
        float('-inf').
    """
    if N < 1:
        return float('-inf')
    if N == 1:
        all_tokens = []
        for sentence in corpus:
            all_tokens.extend(sentence.split())
        total_unigrams = len(all_tokens)
        if total_unigrams == 0:
            return float('-inf')
        unigram_counts = Counter(all_tokens)
        query_tokens = query.split()
        if len(query_tokens) == 0:
            return float('-inf')
        sum_of_logs = 0.0
        for token in query_tokens:
            count_w = unigram_counts.get(token, 0)
            if count_w == 0:
                return float('-inf')
            prob_w = count_w / total_unigrams
            sum_of_logs += math.log(prob_w)
        return round(sum_of_logs, 4)
    else:
        ngram_counts = Counter()
        prefix_counts = Counter()
        for sentence in corpus:
            tokens = sentence.split()
            padded = ['<bol>'] * (N - 1) + tokens + ['<eol>']
            for i in range(len(padded) - N + 1):
                n_gram = tuple(padded[i:i + N])
                ngram_counts[n_gram] += 1
                prefix_counts[n_gram[:-1]] += 1
        query_tokens = query.split()
        padded_query = ['<bol>'] * (N - 1) + query_tokens + ['<eol>']
        if len(padded_query) < N:
            return float('-inf')
        sum_of_logs = 0.0
        for i in range(len(padded_query) - N + 1):
            n_gram = tuple(padded_query[i:i + N])
            prefix = n_gram[:-1]
            count_ng = ngram_counts.get(n_gram, 0)
            count_prefix = prefix_counts.get(prefix, 0)
            if count_ng == 0 or count_prefix == 0:
                return float('-inf')
            prob = count_ng / count_prefix
            sum_of_logs += math.log(prob)
        return round(sum_of_logs, 4)

import heapq
from typing import List, Union
Number = Union[int, float]
def top_k_elements(sequence: List[Number], k: int, order: str='largest') -> List[Number]:
    """Keeps only the k largest or k smallest numbers from *sequence*.

    The function must visit each element exactly once and store no more than
    *k* items at any time by making use of the heapq module (binary heap).

    Args:
        sequence: A list of integers or floats representing the data stream.
        k: The number of extreme values to keep.
        order: "largest" to retain the k biggest values, "smallest" to retain
            the k smallest.

    Returns:
        A list with at most k numbers sorted according to *order*.
    """
    if k <= 0 or not sequence:
        return []
    if k >= len(sequence):
        return sorted(sequence, reverse=order == 'largest')
    if order == 'largest':
        heap: List[Number] = []
        for x in sequence:
            if len(heap) < k:
                heapq.heappush(heap, x)
            elif x > heap[0]:
                heapq.heapreplace(heap, x)
        return sorted(heap, reverse=True)
    else:
        heap: List[Number] = []
        for x in sequence:
            if len(heap) < k:
                heapq.heappush(heap, -x)
            elif x < -heap[0]:
                heapq.heapreplace(heap, -x)
        result = [-val for val in heap]
        return sorted(result)

def inorder_keys(pairs):
    """Build a BST from (key, value) *pairs* and return keys from an in-order traversal.

    Args:
        pairs (list[tuple[Any, int | float]]): List of (key, numeric value) pairs.

    Returns:
        list: Keys in in-order (ascending by numeric value). Return an empty
        list if *pairs* is empty.
    """
    if not pairs:
        return []

    def insert(root, node):
        if node < root:
            if root.left is None:
                root.left = node
            else:
                insert(root.left, node)
        elif root.right is None:
            root.right = node
        else:
            insert(root.right, node)

    def inorder_traversal(node, result):
        if node is None:
            return
        inorder_traversal(node.left, result)
        result.append(node.key)
        inorder_traversal(node.right, result)
    root = Node(pairs[0][0], pairs[0][1])
    for (key, val) in pairs[1:]:
        new_node = Node(key, val)
        insert(root, new_node)
    keys_in_order = []
    inorder_traversal(root, keys_in_order)
    return keys_in_order

import numpy as np
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.  See the detailed task
    description for the exact algorithm that has to be implemented.
    """
    if max_freq is None:
        max_freq = fs / 2

    def hz_to_mel(freq_hz: float) -> float:
        return 2595.0 * np.log10(1.0 + freq_hz / 700.0)

    def mel_to_hz(freq_mel: float) -> float:
        return 700.0 * (10.0 ** (freq_mel / 2595.0) - 1.0)
    mel_min = hz_to_mel(min_freq)
    mel_max = hz_to_mel(max_freq)
    mel_points = np.linspace(mel_min, mel_max, n_filters + 2)
    hz_points = [mel_to_hz(m) for m in mel_points]
    bin_freqs = [k * fs / N for k in range(N // 2 + 1)]
    fbank = []
    for i in range(n_filters):
        f_left = hz_points[i]
        f_center = hz_points[i + 1]
        f_right = hz_points[i + 2]
        row = []
        for k in range(N // 2 + 1):
            freq_k = bin_freqs[k]
            if f_left <= freq_k <= f_center:
                left_slope = (freq_k - f_left) / (f_center - f_left)
            else:
                left_slope = 0.0
            if f_center <= freq_k <= f_right:
                right_slope = (f_right - freq_k) / (f_right - f_center)
            else:
                right_slope = 0.0
            w = max(0.0, min(left_slope, right_slope))
            if normalize and w > 0.0:
                w *= 2.0 / (f_right - f_left)
            row.append(w)
        fbank.append(row)
    rounded_fbank = [[round(value, 4) for value in row] for row in fbank]
    return rounded_fbank

import numpy as np
import string
def genetic_algorithm(target: str, population_size: int, mutation_rate: float, iterations: int) -> str:
    """Evolve a population of random strings towards the *target* string using a
    basic Genetic Algorithm and return the best string discovered.

    Parameters
    ----------
    target : str
        The string the algorithm should try to reproduce.
    population_size : int
        Number of individuals maintained in each generation.
    mutation_rate : float
        Probability with which each character is replaced by a random
        character during the mutation step.
    iterations : int
        Maximum number of generations the algorithm is allowed to run.

    Returns
    -------
    str
        The highest-fitness string found during the evolutionary process.
    """
    np.random.seed(42)
    valid_chars = ' ' + string.ascii_lowercase + string.ascii_uppercase
    char_to_idx = {c: i for (i, c) in enumerate(valid_chars)}
    idx_to_char = {i: c for (i, c) in enumerate(valid_chars)}
    L = len(target)

    def calc_fitness(individual, target_str):
        diff_sum = 0
        for (c_ind, c_tar) in zip(individual, target_str):
            diff_sum += abs(char_to_idx[c_ind] - char_to_idx[c_tar])
        return 1.0 / (diff_sum + 1e-06)

    def generate_individual(length):
        return ''.join(np.random.choice(list(valid_chars), size=length))
    population = [generate_individual(L) for _ in range(population_size)]

    def select_two_parents(pop, fits):
        probs = fits / fits.sum()
        idxs = np.random.choice(len(pop), size=2, replace=False, p=probs)
        return (pop[idxs[0]], pop[idxs[1]])

    def crossover(parent1, parent2):
        point = np.random.randint(0, L + 1)
        child1 = parent1[:point] + parent2[point:]
        child2 = parent2[:point] + parent1[point:]
        return (child1, child2)

    def mutate(individual, rate):
        new_chars = []
        for c in individual:
            if np.random.random() < rate:
                new_chars.append(np.random.choice(list(valid_chars)))
            else:
                new_chars.append(c)
        return ''.join(new_chars)
    best_string = None
    best_fitness = -float('inf')
    for _ in range(iterations):
        fitnesses = np.array([calc_fitness(ind, target) for ind in population])
        max_index = np.argmax(fitnesses)
        if fitnesses[max_index] > best_fitness:
            best_fitness = fitnesses[max_index]
            best_string = population[max_index]
        if best_string == target:
            break
        new_population = []
        for __ in range(population_size // 2):
            (p1, p2) = select_two_parents(population, fitnesses)
            (c1, c2) = crossover(p1, p2)
            c1 = mutate(c1, mutation_rate)
            c2 = mutate(c2, mutation_rate)
            new_population.extend([c1, c2])
        population = new_population
    return best_string

def update_kernel_settings(parameters: dict, hyperparameters: dict, summary_dict: dict) -> tuple[dict, dict]:
    """Update kernel parameters and hyperparameters.

    The function must not modify the input dictionaries.  It should return two
    *new* dictionaries in which the supplied `summary_dict` updates have been
    applied following the rules explained in the task description.

    Args:
        parameters (dict):      Existing kernel parameters.
        hyperparameters (dict): Existing kernel hyperparameters.
        summary_dict (dict):    Dictionary that may contain the keys
            "parameters" and/or "hyperparameters" whose values are dictionaries
            of updates.  Any top-level keys are also treated as updates.

    Returns:
        tuple[dict, dict]: `(new_parameters, new_hyperparameters)` – the
        updated dictionaries.
    """
    new_parameters = dict(parameters)
    new_hyperparameters = dict(hyperparameters)
    merged_updates = {}
    for (key, val) in summary_dict.items():
        if key == 'parameters' and isinstance(val, dict):
            merged_updates.update(val)
        elif key == 'hyperparameters' and isinstance(val, dict):
            merged_updates.update(val)
        else:
            merged_updates[key] = val
    for (k, v) in merged_updates.items():
        if k in new_parameters:
            new_parameters[k] = v
        elif k in new_hyperparameters:
            new_hyperparameters[k] = v
    return (new_parameters, new_hyperparameters)

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries.

    Parameters
    ----------
    transactions : list[list[str]]
        A list where each element is a list of items representing one transaction.
    min_support : int
        Minimum number of transactions an item has to appear in to be kept.

    Returns
    -------
    dict
        The root node of the FP-Tree.  Each node contains two keys:
        "support" and "children" (the latter mapping item → child-node).
    """
    item_counts = Counter()
    for transaction in transactions:
        for item in set(transaction):
            item_counts[item] += 1
    freq_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
    if not freq_items:
        return {'support': 0, 'children': {}}
    sorted_items = sorted(freq_items.keys(), key=lambda x: (-freq_items[x], x))
    item_order = {item: i for (i, item) in enumerate(sorted_items)}
    root = {'support': 0, 'children': {}}
    for transaction in transactions:
        filtered = [item for item in set(transaction) if item in freq_items]
        filtered.sort(key=lambda x: item_order[x])
        current_node = root
        current_node['support'] += 1
        for item in filtered:
            if item not in current_node['children']:
                current_node['children'][item] = {'support': 0, 'children': {}}
            current_node = current_node['children'][item]
            current_node['support'] += 1
    return root

import numpy as np
def get_initializer(name: str):
    """Return a weight-initialisation callable associated with *name*.

    The callable must have the signature
        initializer(shape: tuple[int, ...], scale: float = 1.0) -> np.ndarray
    and create a numpy array filled according to the requested strategy.

    Supported strategies (case-insensitive):
        - "zeros"
        - "ones"
        - "normal"
        - "glorot_uniform"

    If *name* is not recognised the function must raise `ValueError`.
    """
    name = name.lower()
    if name == 'zeros':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.zeros(shape)
        return initializer
    elif name == 'ones':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.ones(shape) * scale
        return initializer
    elif name == 'normal':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            return np.random.normal(loc=0.0, scale=scale, size=shape)
        return initializer
    elif name == 'glorot_uniform':

        def initializer(shape: tuple[int, ...], scale: float=1.0) -> np.ndarray:
            if len(shape) < 2:
                raise ValueError('glorot_uniform is not defined for 1D shapes.')
            (fan_in, fan_out) = (shape[0], shape[1])
            limit = scale * np.sqrt(6.0 / (fan_in + fan_out))
            return np.random.uniform(low=-limit, high=limit, size=shape)
        return initializer
    else:
        raise ValueError(f'Unrecognized initializer: {name}')

import heapq
class _Node:
    """Internal class to represent a node in the Huffman tree."""
    __slots__ = ('freq', 'token', 'left', 'right')

    def __init__(self, freq: int, token: str='', left=None, right=None):
        self.freq = freq
        self.token = token
        self.left = left
        self.right = right
def huffman_encode(tokens: list[str]) -> dict[str, str]:
    """Generate a Huffman code for the supplied tokens.

    The function must return a dictionary mapping **every** unique token in
    *tokens* to its binary Huffman code.  All codes must be prefix-free and
    consist only of the characters ‘0’ and ‘1’.
    """
    freq_map = {}
    for tok in tokens:
        freq_map[tok] = freq_map.get(tok, 0) + 1
    if not freq_map:
        return {}
    if len(freq_map) == 1:
        unique_token = next(iter(freq_map))
        return {unique_token: '0'}
    heap = []
    for (tok, f) in freq_map.items():
        node = _Node(f, tok)
        heap.append((f, tok, node))
    heapq.heapify(heap)
    while len(heap) > 1:
        (f1, t1, n1) = heapq.heappop(heap)
        (f2, t2, n2) = heapq.heappop(heap)
        parent_freq = f1 + f2
        parent_node = _Node(parent_freq, '', left=n1, right=n2)
        heapq.heappush(heap, (parent_freq, '', parent_node))
    (_, _, root) = heap[0]
    codes = {}

    def _traverse(node: _Node, prefix: str):
        """Helper function to traverse the Huffman tree and build the codes."""
        if not node.left and (not node.right):
            codes[node.token] = prefix
            return
        if node.left:
            _traverse(node.left, prefix + '0')
        if node.right:
            _traverse(node.right, prefix + '1')
    _traverse(root, '')
    return codes

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def all_boxes_composite(space: Space) -> bool:
        """Return True if 'space' is a Tuple/Dict whose sub-spaces are all Box."""
        if isinstance(space, Tuple):
            return all((isinstance(s, Box) for s in space.spaces))
        if isinstance(space, Dict):
            return all((isinstance(s, Box) for s in space.spaces.values()))
        return False
    if tuple_action:
        cont_action = all_boxes_composite(env.action_space)
    else:
        cont_action = isinstance(env.action_space, Box)
    if tuple_obs:
        cont_obs = all_boxes_composite(env.observation_space)
    else:
        cont_obs = isinstance(env.observation_space, Box)
    return (cont_action, cont_obs)

import re
from typing import Any, Dict, Optional
class KernelBase:
    """Minimal base class for all kernels.  Do NOT modify this class."""

    def __init__(self, **kwargs: Any) -> None:
        self.params: Dict[str, Any] = kwargs

    def set_params(self, state: Dict[str, Any]):
        """(Re)sets parameters from a previously stored summary dict."""
        hp = state.get('hyperparameters', {})
        self.params.update({k: v for (k, v) in hp.items() if k != 'id'})
        return self

    def summary(self) -> Dict[str, Any]:
        """Dictionary description that can be fed back to *initialize_kernel*."""
        return {'hyperparameters': {'id': self.__class__.__name__, **self.params}}
class LinearKernel(KernelBase):

    def __init__(self):
        super().__init__()
class PolynomialKernel(KernelBase):

    def __init__(self, degree: int=3, coef0: float=1.0):
        super().__init__(degree=degree, coef0=coef0)
class RBFKernel(KernelBase):

    def __init__(self, gamma: float=1.0):
        super().__init__(gamma=gamma)
def initialize_kernel(param: Optional[Any]=None) -> KernelBase:
    """Create and return a kernel object from various representations.

    Args:
        param: None (default LinearKernel), an existing KernelBase instance, a
               string description such as 'RBF(gamma=0.5)', or a dictionary with a
               'hyperparameters' entry as produced by KernelBase.summary().

    Returns:
        KernelBase: the newly created or forwarded kernel instance.
    """
    if param is None:
        return LinearKernel()
    if isinstance(param, KernelBase):
        return param

    def parse_value(val_str: str) -> Any:
        val_str = val_str.strip()
        if val_str.lower() == 'true':
            return True
        if val_str.lower() == 'false':
            return False
        try:
            return int(val_str)
        except ValueError:
            pass
        try:
            return float(val_str)
        except ValueError:
            pass
        return val_str
    if isinstance(param, str):
        param = param.strip()
        pattern = '^([a-zA-Z]+)(?:\\((.*)\\))?$'
        match = re.match(pattern, param, flags=re.IGNORECASE)
        if not match:
            raise ValueError(f"Cannot parse kernel specification from string '{param}'")
        (kernel_name, args_str) = match.groups()
        kernel_name = kernel_name.lower()
        kwargs = {}
        if args_str:
            for pair in args_str.split(','):
                pair = pair.strip()
                if '=' not in pair:
                    raise ValueError(f"Invalid format: '{pair}' in '{param}'")
                (key, val) = pair.split('=', 1)
                kwargs[key.strip()] = parse_value(val.strip())
        if kernel_name == 'linear':
            return LinearKernel()
        elif kernel_name == 'polynomial':
            return PolynomialKernel(**kwargs)
        elif kernel_name == 'rbf':
            return RBFKernel(**kwargs)
        else:
            raise NotImplementedError(f"Unknown kernel name '{kernel_name}'")
    if isinstance(param, dict):
        if 'hyperparameters' not in param:
            raise ValueError("Dictionary must have 'hyperparameters' key.")
        hp = param['hyperparameters']
        if 'id' not in hp:
            raise ValueError("The 'hyperparameters' dictionary must contain an 'id' entry.")
        kernel_id = hp['id']
        kwargs = {k: v for (k, v) in hp.items() if k != 'id'}
        if kernel_id == 'LinearKernel':
            return LinearKernel()
        elif kernel_id == 'PolynomialKernel':
            return PolynomialKernel(**kwargs)
        elif kernel_id == 'RBFKernel':
            return RBFKernel(**kwargs)
        else:
            raise NotImplementedError(f"Unknown kernel id '{kernel_id}'")
    raise ValueError(f"Cannot initialize a kernel from '{param}'")

import numpy as np
import re
def apply_activation(x, param=None):
    """Apply a chosen activation to the input vector.

    Args:
        x (list[float] | np.ndarray): Input data. Anything that can be
            converted to a NumPy array of floats is accepted.
        param (str | None, optional): Description of the activation. See the
            task specification for the accepted values. If *None* the
            identity function is applied.

    Returns:
        list[float]: Result of applying the activation element-wise, rounded
            to six decimals and converted to a standard Python *list*.
    """
    arr = np.array(x, dtype=float)
    param_str = 'identity' if param is None else param.strip().lower()
    if param_str in ['identity', 'none']:
        out = arr
    elif param_str == 'relu':
        out = np.maximum(0, arr)
    elif param_str == 'tanh':
        out = np.tanh(arr)
    elif param_str == 'sigmoid':
        out = 1 / (1 + np.exp(-arr))
    elif param_str.startswith('leaky relu'):
        match = re.search('leaky\\s*relu\\s*\\(\\s*alpha\\s*=\\s*([+-]?\\d+(\\.\\d+)?)\\s*\\)', param_str)
        if match:
            alpha = float(match.group(1))
        else:
            alpha = 0.01
        out = np.where(arr > 0, arr, alpha * arr)
    elif param_str.startswith('affine'):
        match = re.search('affine\\s*\\(\\s*slope\\s*=\\s*([+-]?\\d+(\\.\\d+)?)\\s*,\\s*intercept\\s*=\\s*([+-]?\\d+(\\.\\d+)?)\\s*\\)', param_str)
        if match:
            slope = float(match.group(1))
            intercept = float(match.group(3))
            out = slope * arr + intercept
        else:
            raise ValueError(f'Unknown activation: {param}')
    else:
        raise ValueError(f'Unknown activation: {param}')
    return np.round(out, 6).tolist()

import re
def parse_activation(act_str: str):
    """Parse an activation-function description string.

    The string can be one of the following forms:
        • "ReLU"
        • "Sigmoid"
        • "Tanh"
        • "Affine(slope=<number>, intercept=<number>)"
        • "Leaky ReLU(alpha=<number>)"

    Args:
        act_str: Description of the activation function.

    Returns:
        A dictionary with key "type" and, when applicable, its parameters.
        If the input format is invalid, return -1.
    """
    if act_str == 'ReLU':
        return {'type': 'ReLU'}
    if act_str == 'Sigmoid':
        return {'type': 'Sigmoid'}
    if act_str == 'Tanh':
        return {'type': 'Tanh'}
    number_pattern = '([+-]?(?:\\d+(?:\\.\\d+)?|\\.\\d+))'
    affine_pattern = f'^Affine\\(slope={number_pattern}, intercept={number_pattern}\\)$'
    match_affine = re.match(affine_pattern, act_str)
    if match_affine:
        (slope_str, intercept_str) = match_affine.groups()
        return {'type': 'Affine', 'slope': float(slope_str), 'intercept': float(intercept_str)}
    leaky_relu_pattern = f'^Leaky ReLU\\(alpha={number_pattern}\\)$'
    match_leaky = re.match(leaky_relu_pattern, act_str)
    if match_leaky:
        alpha_str = match_leaky.group(1)
        return {'type': 'Leaky ReLU', 'alpha': float(alpha_str)}
    return -1

import numpy as np
def env_stats(actions: list, observations: list) -> dict:
    """Compute statistics for collections of actions and observations.

    The function inspects *actions* and *observations* and returns a dictionary
    containing information about dimensionality, data type (discrete or
    continuous) and the unique values appearing in every dimension.

    Parameters
    ----------
    actions : list
        A list with the actions that were taken.  Each element is either a
        scalar or an iterable of scalars (for multi-dimensional spaces).
    observations : list
        A list with the corresponding observations.  Same structural
        requirements as *actions*.

    Returns
    -------
    dict
        A dictionary with the keys described in the task description.
    """

    def get_dim_and_iterable_status(sample):
        """
        Determine the dimension (number of scalar entries) for 'sample' and
        whether it is an iterable (list/tuple/np.ndarray) or a scalar.
        """
        if isinstance(sample, np.ndarray):
            if sample.shape == ():
                return (1, False)
            else:
                return (sample.size, True)
        elif isinstance(sample, (list, tuple)):
            return (len(sample), True)
        else:
            return (1, False)

    def analyze(values):
        """
        Given a list of values (actions or observations), compute:
          - tuple_ (bool): whether any item is an iterable
          - multidim (bool): whether dimension > 1
          - continuous (bool): True if any value is a non-integer float
          - dimension (int): number of components per item (1 if scalar)
          - sets_per_dim (list of sets): collected unique values per dimension
        """
        if not values:
            return {'tuple': False, 'multidim': False, 'continuous': False, 'dimension': 0, 'sets_per_dim': []}
        first = values[0]
        (dimension, is_iterable) = get_dim_and_iterable_status(first)
        tuple_ = is_iterable
        multidim = dimension > 1
        sets_per_dim = [set() for _ in range(dimension)]
        continuous = False
        for val in values:
            (d, it) = get_dim_and_iterable_status(val)
            if d == 1 and (not it):
                x = val
                sets_per_dim[0].add(x)
                if isinstance(x, float) and (not x.is_integer()):
                    continuous = True
            else:
                for i in range(dimension):
                    x = val[i]
                    sets_per_dim[i].add(x)
                    if isinstance(x, float) and (not x.is_integer()):
                        continuous = True
        return {'tuple': tuple_, 'multidim': multidim, 'continuous': continuous, 'dimension': dimension, 'sets_per_dim': sets_per_dim}

    def finalize_stats(stats_dict):
        """
        From the stats_dict (with 'sets_per_dim', etc.) build the required fields:
          - n_per_dim
          - ids_per_dim
        """
        sets = stats_dict['sets_per_dim']
        n_per_dim = [len(s) for s in sets]
        ids_per_dim = [sorted(list(s)) for s in sets]
        return (n_per_dim, ids_per_dim)
    a_stats = analyze(actions)
    o_stats = analyze(observations)
    (a_n_per_dim, a_ids_per_dim) = finalize_stats(a_stats)
    (o_n_per_dim, o_ids_per_dim) = finalize_stats(o_stats)
    return {'tuple_actions': a_stats['tuple'], 'tuple_observations': o_stats['tuple'], 'multidim_actions': a_stats['multidim'], 'multidim_observations': o_stats['multidim'], 'continuous_actions': a_stats['continuous'], 'continuous_observations': o_stats['continuous'], 'n_actions_per_dim': a_n_per_dim, 'action_dim': a_stats['dimension'], 'n_obs_per_dim': o_n_per_dim, 'obs_dim': o_stats['dimension'], 'action_ids': a_ids_per_dim, 'obs_ids': o_ids_per_dim}

def get_metric(name: str):
    """Return an evaluation metric function by *name*.

    Supported names:
    - 'accuracy'
    - 'precision'
    - 'recall'
    - 'f1'

    If the name is invalid, the function should return -1.
    The returned metric functions must accept two equal-length iterables
    (y_true, y_pred) containing only 0s and 1s and return the metric value
    rounded to 4 decimal places.
    """

    def accuracy_score(y_true, y_pred):
        correct = 0
        total = 0
        for (t, p) in zip(y_true, y_pred):
            if t == p:
                correct += 1
            total += 1
        return round(correct / total, 4)

    def precision_score(y_true, y_pred):
        tp = 0
        fp = 0
        for (t, p) in zip(y_true, y_pred):
            if p == 1 and t == 1:
                tp += 1
            elif p == 1 and t == 0:
                fp += 1
        denominator = tp + fp
        if denominator == 0:
            return 0.0
        return round(tp / denominator, 4)

    def recall_score(y_true, y_pred):
        tp = 0
        fn = 0
        for (t, p) in zip(y_true, y_pred):
            if t == 1 and p == 1:
                tp += 1
            elif t == 1 and p == 0:
                fn += 1
        denominator = tp + fn
        if denominator == 0:
            return 0.0
        return round(tp / denominator, 4)

    def f1_score(y_true, y_pred):
        p = precision_score(y_true, y_pred)
        r = recall_score(y_true, y_pred)
        if p == 0 and r == 0:
            return 0.0
        return round(2 * p * r / (p + r), 4)
    metrics = {'accuracy': accuracy_score, 'precision': precision_score, 'recall': recall_score, 'f1': f1_score}
    return metrics.get(name, -1)

import numpy as np
import numpy as np
def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function returns the MAP predictive means for each row in `X_new`,
    rounded to 4 decimal places.  The posterior is given by

        Σ_p = ( V^-1 + X^T X )^-1
        μ_p = Σ_p ( V^-1 μ + X^T y )

    and the predictions for a new design matrix X_new are X_new μ_p.
    """
    if fit_intercept:
        X = np.column_stack([np.ones(X.shape[0]), X])
        X_new = np.column_stack([np.ones(X_new.shape[0]), X_new])
    D = X.shape[1]
    if np.isscalar(mu):
        mu_vec = np.full(D, mu, dtype=float)
    else:
        mu_vec = np.asarray(mu, dtype=float)
        if mu_vec.ndim == 0:
            mu_vec = np.full(D, mu_vec.item(), dtype=float)
        elif mu_vec.shape[0] != D:
            raise ValueError('Dimension mismatch between mu and X.')
    if V is None:
        V_mat = np.eye(D, dtype=float)
    elif np.isscalar(V):
        V_mat = float(V) * np.eye(D, dtype=float)
    else:
        V_arr = np.asarray(V, dtype=float)
        if V_arr.ndim == 1:
            if V_arr.shape[0] != D:
                raise ValueError('Dimension mismatch between V and X.')
            V_mat = np.diag(V_arr)
        else:
            if V_arr.shape != (D, D):
                raise ValueError('Dimension mismatch between V and X.')
            V_mat = V_arr
    V_inv = np.linalg.inv(V_mat)
    Cov_inv = V_inv + X.T @ X
    Sigma_p = np.linalg.inv(Cov_inv)
    posterior_mean = Sigma_p @ (V_inv @ mu_vec + X.T @ y)
    y_pred = X_new @ posterior_mean
    return [round(float(val), 4) for val in y_pred]

from typing import List, Dict, Set, Tuple
def topological_sort(vertices: list, edges: list[tuple]) -> list | None:
    """Determine a topological ordering of a directed graph.

    The implementation **must** use depth-first search (DFS) and follow the
    deterministic exploration rule described in the task description.

    Args:
        vertices: List of vertex identifiers (hashable).
        edges:    List of directed edges given as 2-tuples (source, target).

    Returns:
        • List of vertices in topological order when the graph is acyclic.
        • ``None`` when the graph contains at least one directed cycle.
    """
    index_map = {v: i for (i, v) in enumerate(vertices)}
    adj_list = {v: [] for v in vertices}
    for (src, dst) in edges:
        adj_list[src].append(dst)
    for v in vertices:
        adj_list[v].sort(key=lambda x: index_map[x])
    visited = {v: 0 for v in vertices}
    topo_order = []
    cycle_found = False

    def dfs(node: object) -> bool:
        nonlocal cycle_found
        visited[node] = 1
        for neighbor in adj_list[node]:
            if visited[neighbor] == 0:
                if not dfs(neighbor):
                    return False
            elif visited[neighbor] == 1:
                cycle_found = True
                return False
        visited[node] = 2
        topo_order.append(node)
        return True
    for v in vertices:
        if visited[v] == 0:
            if not dfs(v):
                return None
    topo_order.reverse()
    return topo_order if not cycle_found else None

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    is_scalar = isinstance(x, (int, float))
    arr = np.array(x, dtype=float)
    s = 1 / (1 + np.exp(-arr))
    if order == 0:
        result = s
    elif order == 1:
        result = s * (1 - s)
    else:
        result = s * (1 - s) * (1 - 2 * s)
    result = np.round(result, 4)
    if is_scalar:
        return float(result)
    else:
        return result.tolist()

import itertools
def apriori_frequent_itemsets(transactions: list[list], min_sup: float) -> list[tuple]:
    """
    Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list (or iterable) of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
        Each tuple is in ascending order.
    """
    transaction_sets = [set(t) for t in transactions]
    num_transactions = len(transactions)
    min_count = max(1, int(min_sup * num_transactions + 0.9999999))
    item_counts = {}
    for tset in transaction_sets:
        for item in tset:
            item_counts[item] = item_counts.get(item, 0) + 1
    L1 = {(item,): cnt for (item, cnt) in item_counts.items() if cnt >= min_count}
    if not L1:
        return []
    levels = [L1]

    def count_candidates(candidates, transaction_sets):
        """Count how many transactions contain each candidate."""
        counts = {c: 0 for c in candidates}
        for tset in transaction_sets:
            for c in candidates:
                if all((item in tset for item in c)):
                    counts[c] += 1
        return counts

    def generate_candidates(prev_level_keys, k):
        """
        Generate candidate k-itemsets by self-joining the (k-1)-itemsets.
        Then apply the Apriori pruning step: all (k-1)-subsets must be frequent.
        """
        candidates = set()
        prev_level_list = sorted(prev_level_keys)
        n = len(prev_level_list)
        for i in range(n):
            for j in range(i + 1, n):
                itemset1 = prev_level_list[i]
                itemset2 = prev_level_list[j]
                if itemset1[:-1] == itemset2[:-1]:
                    new_candidate = tuple(sorted(set(itemset1) | set(itemset2)))
                    if len(new_candidate) == k:
                        all_subsets_frequent = True
                        for subset in itertools.combinations(new_candidate, k - 1):
                            subset = tuple(sorted(subset))
                            if subset not in prev_level_keys:
                                all_subsets_frequent = False
                                break
                        if all_subsets_frequent:
                            candidates.add(new_candidate)
                else:
                    break
        return candidates
    k = 2
    while True:
        prev_level = levels[-1]
        prev_level_keys = set(prev_level.keys())
        ck = generate_candidates(prev_level_keys, k)
        if not ck:
            break
        ck_counts = count_candidates(ck, transaction_sets)
        lk = {c: cnt for (c, cnt) in ck_counts.items() if cnt >= min_count}
        if not lk:
            break
        levels.append(lk)
        k += 1
    all_frequent_itemsets = []
    for level_dict in levels:
        all_frequent_itemsets.extend(level_dict.keys())
    all_frequent_itemsets.sort(key=lambda x: (len(x), x))
    return all_frequent_itemsets

import numpy as np
def hamming(window_len: int, symmetric: bool=False) -> list[float]:
    """Generate a Hamming window.

    The function should return window_len samples of a Hamming window.
    When symmetric is False, return the periodic variant (last sample of
    the symmetric window removed). Each value is rounded to 4 decimals.
    """
    if window_len == 0:
        return []
    if window_len == 1:
        return [1.0]
    if symmetric:
        denominator = window_len - 1
    else:
        denominator = window_len
    n = np.arange(window_len)
    w = 0.54 - 0.46 * np.cos(2.0 * np.pi * n / denominator)
    return np.round(w, 4).tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function follows the specifications:
      k(x, y) = exp(-0.5 * sum_j( ( (x_j - y_j)/sigma_j )^2 ))

      • If 'sigma' is a single float, the same value is used for each feature.
      • If 'sigma' is a list of length C, each feature has its own sigma_j.
      • If 'sigma' is None, a default sigma = sqrt(C/2) is used.
      • Return -1 if:
            -- 'sigma' is non-positive,
            -- or 'sigma' is a list of incorrect length or non-positive values,
            -- or feature dimensions of X and Y do not match.
      • Otherwise, return the kernel matrix (N×M) (rounded to 4 decimal places) as a nested list.
    """
    try:
        X_arr = np.array(X, dtype=float)
    except ValueError:
        return -1
    if Y is None:
        Y_arr = X_arr
    else:
        try:
            Y_arr = np.array(Y, dtype=float)
        except ValueError:
            return -1
    if X_arr.ndim != 2 or Y_arr.ndim != 2:
        return -1
    (N, C) = X_arr.shape
    (M, C_y) = Y_arr.shape
    if C != C_y:
        return -1
    if sigma is None:
        sigma_val = np.sqrt(C / 2.0)
        sigma_arr = np.full((C,), sigma_val, dtype=float)
    elif isinstance(sigma, float) or isinstance(sigma, int):
        if sigma <= 0:
            return -1
        sigma_arr = np.full((C,), float(sigma), dtype=float)
    elif isinstance(sigma, list):
        if len(sigma) != C:
            return -1
        for s in sigma:
            if s <= 0:
                return -1
        sigma_arr = np.array(sigma, dtype=float)
    else:
        return -1
    diff = X_arr[:, np.newaxis, :] - Y_arr[np.newaxis, :, :]
    diff = diff / sigma_arr
    dist_sq = np.sum(diff ** 2, axis=2)
    K = np.exp(-0.5 * dist_sq)
    K_rounded = np.round(K, 4)
    return K_rounded.tolist()

import math
def compute_ball_radius(centroid: list[float], X: list[list[float]]) -> float:
    """Compute the maximum Euclidean distance between *centroid* and the
    points in *X* (i.e., the Ball-tree node radius), rounded to 4 decimals.
    If X is empty, returns 0.0.
    """
    if not X:
        return 0.0
    max_distance = 0.0
    for point in X:
        squared_dist = sum(((c - p) ** 2 for (c, p) in zip(centroid, point)))
        dist = math.sqrt(squared_dist)
        if dist > max_distance:
            max_distance = dist
    return round(max_distance, 4)

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    if isinstance(z, list) and len(z) == 0:
        return []
    arr = np.array(z, dtype=float)
    if arr.size == 0:
        return []
    grad = arr * (1 - arr)
    grad = np.round(grad, 4)
    if arr.ndim == 0:
        return float(grad)
    else:
        return grad.tolist()

import numpy as np
def relu_grad(z):
    """Compute the element-wise derivative of the ReLU activation.

    The function should return 0 for every entry that is less than or equal to
    zero and 1 for every entry that is strictly greater than zero. Scalars
    must yield a single integer; arrays/lists must yield a list of identical
    shape.
    """
    arr = np.array(z)
    if arr.ndim == 0:
        return int(arr.item() > 0)
    else:
        return (arr > 0).astype(int).tolist()

import os
from typing import List, Union
def extract_archive(file_path: str, path: str='.', archive_format: Union[str, List[str], None]='auto') -> bool:
    """Checks whether *file_path* belongs to an allowed archive format.

    The function MUST NOT raise any exception and MUST NOT perform real file
    extraction.  Read the task description for the full specification.

    Args:
        file_path: Full path (or name) of the archive file.
        path: Destination folder – ignored in this simplified version.
        archive_format: See task description.

    Returns:
        True if the file name matches one of the allowed formats, False otherwise.
    """
    if not archive_format:
        return False
    if archive_format == 'auto':
        requested_formats = ['tar', 'zip']
    elif isinstance(archive_format, str):
        requested_formats = [archive_format]
    else:
        requested_formats = archive_format
    known_formats = {'tar': ['.tar', '.tar.gz', '.tgz', '.tar.bz', '.tar.bz2', '.tbz'], 'zip': ['.zip']}
    allowed_extensions = []
    for fmt in requested_formats:
        fmt_lower = str(fmt).lower()
        if fmt_lower in known_formats:
            allowed_extensions.extend(known_formats[fmt_lower])
    if not allowed_extensions:
        return False
    filename = os.path.basename(file_path).lower()
    for ext in allowed_extensions:
        if filename.endswith(ext.lower()):
            return True
    return False

def tree_stats(root: 'node | None') -> tuple[int, int]:
    """Return the number of leaf nodes and the maximum depth of the tree.

    A **leaf** is defined as a node whose ``results`` attribute is not ``None``.

    Parameters
    ----------
    root : node | None
        Root of the decision tree.  If *None*, the tree is considered empty.

    Returns
    -------
    tuple[int, int]
        ``(leaf_count, max_depth)`` where
        * *leaf_count* is the total number of leaves in the tree, and
        * *max_depth* is the length of the longest path from *root* to a leaf.
    """
    if root is None:
        return (0, 0)

    def helper(node: 'node | None') -> tuple[int, int]:
        if node is None:
            return (0, 0)
        if node.results is not None:
            return (1, 1)
        (left_leaves, left_depth) = helper(node.left)
        (right_leaves, right_depth) = helper(node.right)
        return (left_leaves + right_leaves, 1 + max(left_depth, right_depth))
    return helper(root)

from collections import Counter
from typing import Any, Dict, List
def build_tree(data: List[List[Any]]) -> Dict[str, Any]:
    """Build a CART decision tree using the Gini impurity criterion.

    The *data* argument is a list where every inner list represents a training
    sample.  All but the last elements of each sample are feature values; the
    last element is the class label.

    The function should return the root of the generated tree expressed as a
    nested dictionary:
        Leaf   -> {"label": class_label}
        Branch -> {"feature": index,
                    "value":   split_value,
                    "left":    <subtree for samples with feature == value>,
                    "right":   <subtree for samples with feature != value>}

    If *data* is empty return the empty dictionary.
    """

    def gini(subset: List[List[Any]]) -> float:
        if not subset:
            return 0
        labels = [row[-1] for row in subset]
        counts = Counter(labels)
        total = len(subset)
        return 1 - sum(((count / total) ** 2 for count in counts.values()))

    def majority_class(subset: List[List[Any]]) -> Any:
        counts = Counter((row[-1] for row in subset))
        max_count = max(counts.values())
        for row in subset:
            label = row[-1]
            if counts[label] == max_count:
                return label
    if not data:
        return {}
    current_gini = gini(data)
    best_gain = 0.0
    best_split = None
    n_features = len(data[0]) - 1
    for feature_index in range(n_features):
        values = sorted(set((row[feature_index] for row in data)))
        for value in values:
            left_subset = [row for row in data if row[feature_index] == value]
            right_subset = [row for row in data if row[feature_index] != value]
            if not left_subset or not right_subset:
                continue
            left_g = gini(left_subset)
            right_g = gini(right_subset)
            left_w = len(left_subset) / len(data)
            right_w = len(right_subset) / len(data)
            weighted_gini = left_g * left_w + right_g * right_w
            gain = current_gini - weighted_gini
            if gain > best_gain:
                best_gain = gain
                best_split = (feature_index, value, left_subset, right_subset)
    if best_gain <= 0:
        return {'label': majority_class(data)}
    (feature_index, value, left_subset, right_subset) = best_split
    left_branch = build_tree(left_subset)
    right_branch = build_tree(right_subset)
    return {'feature': feature_index, 'value': value, 'left': left_branch, 'right': right_branch}

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if hasattr(root, 'y') and root.y is not None:
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for child in root.child:
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        if features[root.label] <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)
    raise ValueError('Invalid task_type or malformed node.')

import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """
    Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n, d) = data.shape
    if epsilon == 0:
        return []

    def c(n: int) -> float:
        if n <= 1:
            return 0.0
        return 2.0 * np.log(n - 1) + 0.5772156649 - 2.0 * ((n - 1) / n)

    def build_tree(X: np.ndarray, current_depth: int, height_limit: int):
        if current_depth >= height_limit or len(X) <= 1:
            return {'leaf_size': len(X)}
        feature = np.random.randint(0, d)
        min_val = X[:, feature].min()
        max_val = X[:, feature].max()
        if min_val == max_val:
            return {'leaf_size': len(X)}
        threshold = np.random.uniform(min_val, max_val)
        left_mask = X[:, feature] <= threshold
        right_mask = ~left_mask
        left_sub = X[left_mask]
        right_sub = X[right_mask]
        left_node = build_tree(left_sub, current_depth + 1, height_limit)
        right_node = build_tree(right_sub, current_depth + 1, height_limit)
        return {'feature': feature, 'threshold': threshold, 'left': left_node, 'right': right_node, 'leaf_size': len(X)}

    def path_length(x: np.ndarray, node: dict, current_depth: int=0) -> float:
        if 'feature' not in node:
            return current_depth + c(node['leaf_size'])
        if x[node['feature']] <= node['threshold']:
            return path_length(x, node['left'], current_depth + 1)
        else:
            return path_length(x, node['right'], current_depth + 1)
    trees = []
    actual_sample_size = min(sample_size, n)
    height_limit = math.ceil(math.log2(actual_sample_size))
    for _ in range(n_trees):
        idx = np.random.choice(n, size=actual_sample_size, replace=False)
        X_sub = data[idx]
        tree = build_tree(X_sub, 0, height_limit)
        trees.append(tree)
    path_length_sums = np.zeros(n, dtype=float)
    for i in range(n):
        x = data[i]
        pl_sum = 0.0
        for tree in trees:
            pl_sum += path_length(x, tree, 0)
        path_length_sums[i] = pl_sum
    avg_path_lengths = path_length_sums / n_trees
    if n > 1:
        phi = 2.0 * math.log(n - 1) - 2.0 * (float(n - 1) / n)
    else:
        phi = 1e-09
    scores = np.power(2.0, -(avg_path_lengths / phi))
    outliers_count = int(n * epsilon)
    if outliers_count == 0:
        return []
    sorted_indices_desc = np.argsort(scores)[::-1]
    outlier_indices = sorted_indices_desc[:outliers_count]
    return sorted(outlier_indices.tolist())

from collections import Counter
from typing import Iterable, Tuple, List, Set, Any
def statistic_key_word(data: List[Tuple[Any, Iterable[str]]], cut_off: int | None=None) -> Tuple[List[Tuple[str, float]], Set[str]]:
    """Calculate how often each word appears across documents.

    The function returns a list with the document-frequency ratio of every word
    (sorted from highest to lowest) and a set containing only the *cut_off*
    most frequent words. When *cut_off* is ``None`` all words are placed in the
    set. Every ratio must be rounded to four decimal places.

    Args:
        data: Iterable of documents. Each document is represented by a tuple
              ``(label, words)`` where *label* is ignored and *words* is an
              iterable of strings.
        cut_off: Number of top words to include in the returned set, or
                  ``None`` to include every word.

    Returns:
        Tuple with (1) a list of (word, ratio) pairs and (2) a set of the most
        frequent words as described above.
    """
    n_docs = len(data)
    if n_docs == 0:
        return ([], set())
    doc_counts = Counter()
    for (_, words) in data:
        unique_words = set(words)
        for w in unique_words:
            doc_counts[w] += 1
    word_ratio_pairs = []
    for (word, count) in doc_counts.items():
        ratio = round(count / n_docs, 4)
        word_ratio_pairs.append((word, ratio))
    word_ratio_pairs.sort(key=lambda x: x[1], reverse=True)
    if cut_off is None:
        top_words = {w for (w, _) in word_ratio_pairs}
    elif cut_off == 0:
        top_words = set()
    else:
        top_words = {w for (w, _) in word_ratio_pairs[:cut_off]}
    return (word_ratio_pairs, top_words)

import numpy as np
def minesweeper_step(ground: list[list[float | int]], flags: list[list[bool]]) -> tuple[list[tuple[int, int]], list[tuple[int, int]]]:
    """Perform one logical deduction step on a Minesweeper board.

    The function receives the currently visible part of the board (``ground``)
    and the already placed flags (``flags``). It must return two *sorted* lists:

    1. **clicks** – every square that can be safely revealed.
    2. **new_flags** – every square that must be a bomb according to the rules
       described in the task.

    Notes:
        * ``ground`` and ``flags`` have identical dimensions.
        * Hidden squares are represented by ``np.nan`` inside ``ground``.
        * Neighbourhood is the 8-connected Moore neighbourhood.
    """
    rows = len(ground)
    cols = len(ground[0]) if rows > 0 else 0

    def neighbors(r, c):
        for dr in (-1, 0, 1):
            for dc in (-1, 0, 1):
                if dr == 0 and dc == 0:
                    continue
                (nr, nc) = (r + dr, c + dc)
                if 0 <= nr < rows and 0 <= nc < cols:
                    yield (nr, nc)
    clicks_set = set()
    flags_set = set()
    for r in range(rows):
        for c in range(cols):
            if not np.isnan(ground[r][c]) and (not flags[r][c]):
                val = int(ground[r][c])
                neighs = list(neighbors(r, c))
                hidden_unflagged = [(nr, nc) for (nr, nc) in neighs if np.isnan(ground[nr][nc]) and (not flags[nr][nc])]
                bomb_count = sum((flags[nr][nc] for (nr, nc) in neighs))
                if val == 0:
                    for sq in hidden_unflagged:
                        clicks_set.add(sq)
                elif val == 8:
                    for sq in hidden_unflagged:
                        flags_set.add(sq)
                elif bomb_count == val:
                    for sq in hidden_unflagged:
                        clicks_set.add(sq)
                elif len(hidden_unflagged) == val:
                    for sq in hidden_unflagged:
                        flags_set.add(sq)
    clicks = sorted(clicks_set)
    new_flags = sorted(flags_set)
    return (clicks, new_flags)

def maze_to_graph(maze: list[list[int]]) -> dict[tuple[int, int], list[tuple[int, int]]]:
    """Convert a binary maze into an adjacency-list graph.

    A 0 in the maze denotes an open cell, while 1 denotes a wall. Two open
    cells are adjacent if they share an edge (up, down, left, right).

    Args:
        maze: Rectangular 2-D list containing only 0s and 1s.

    Returns:
        A dictionary mapping each open cell coordinate (row, col) to a sorted
        list of neighbouring open cell coordinates. Walls are not included in
        the dictionary. If there are no open cells the function returns an
        empty dictionary.
    """
    if not maze:
        return {}
    rows = len(maze)
    cols = len(maze[0]) if rows else 0
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    graph = {}
    for r in range(rows):
        for c in range(cols):
            if maze[r][c] == 0:
                graph[r, c] = []
    for (r, c) in graph.keys():
        neighbors = []
        for (dr, dc) in directions:
            (nr, nc) = (r + dr, c + dc)
            if 0 <= nr < rows and 0 <= nc < cols and (maze[nr][nc] == 0):
                neighbors.append((nr, nc))
        graph[r, c] = sorted(neighbors)
    return graph
