from collections import Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*.

    Your task is to complete this function so that it builds N-gram count
    tables from *corpus* and then returns the total log-probability of
    *sequence* under the resulting unsmoothed language model.

    The return value must be rounded to 4 decimal places.  If any required
    count is zero you should immediately return ``float('-inf')``.
    """
    if len(sequence) < N:
        return 0.0
    if N == 1:
        corpus_unigrams = Counter(corpus)
        total_log_prob = 0.0
        corpus_length = len(corpus)
        for word in sequence:
            count = corpus_unigrams[word]
            if count == 0:
                return float('-inf')
            prob = count / corpus_length
            total_log_prob += np.log(prob)
        return round(total_log_prob, 4)
    ngram_counts = Counter()
    prefix_counts = Counter()
    for i in range(len(corpus) - N + 1):
        ngram = tuple(corpus[i:i + N])
        ngram_counts[ngram] += 1
    for i in range(len(corpus) - N + 2):
        prefix = tuple(corpus[i:i + N - 1])
        prefix_counts[prefix] += 1
    total_log_prob = 0.0
    for i in range(len(sequence) - N + 1):
        prefix = tuple(sequence[i:i + N - 1])
        ngram = tuple(sequence[i:i + N])
        ngram_count = ngram_counts[ngram]
        prefix_count = prefix_counts[prefix]
        if prefix_count == 0 or ngram_count == 0:
            return float('-inf')
        prob = ngram_count / prefix_count
        total_log_prob += np.log(prob)
    return round(total_log_prob, 4)

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or -1 on invalid input.
    """
    if not S or not A or (not B) or (not observations):
        return -1
    n = len(S)
    if len(A) != n:
        return -1
    for row in A:
        if not isinstance(row, list) or len(row) != n:
            return -1
    m = len(B[0])
    if m == 0:
        return -1
    if len(B) != n:
        return -1
    for row in B:
        if not isinstance(row, list) or len(row) != m:
            return -1
    for obs in observations:
        if not isinstance(obs, int) or obs < 0 or obs >= m:
            return -1
    S_arr = np.array(S, dtype=float)
    A_arr = np.array(A, dtype=float)
    B_arr = np.array(B, dtype=float)
    alpha = S_arr * B_arr[:, observations[0]]
    for t in range(1, len(observations)):
        alpha = alpha @ A_arr * B_arr[:, observations[t]]
    likelihood = float(np.sum(alpha))
    return round(likelihood, 4)

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    def gini_impurity(y):
        """Compute the Gini impurity for a vector of labels y."""
        if len(y) == 0:
            return 0
        (unique, counts) = np.unique(y, return_counts=True)
        probs = counts / counts.sum()
        return 1 - np.sum(probs ** 2)

    def majority_class(y):
        """Return the majority class label from y.
        In case of ties, np.argmax on bincount returns the smallest label."""
        counts = np.bincount(y)
        return int(np.argmax(counts))

    def build_tree(X, y, depth):
        if np.all(y == y[0]):
            return {'is_leaf': True, 'prediction': int(y[0])}
        if max_depth is not None and depth >= max_depth:
            return {'is_leaf': True, 'prediction': majority_class(y)}
        (n_samples, n_features) = X.shape
        parent_gini = gini_impurity(y)
        best_gain = 0.0
        best_feature = None
        best_threshold = None
        for feature in range(n_features):
            sorted_idx = np.argsort(X[:, feature])
            X_sorted = X[sorted_idx, feature]
            y_sorted = y[sorted_idx]
            for i in range(1, n_samples):
                if X_sorted[i] == X_sorted[i - 1]:
                    continue
                threshold = (X_sorted[i] + X_sorted[i - 1]) / 2.0
                left_mask = X[:, feature] < threshold
                right_mask = ~left_mask
                y_left = y[left_mask]
                y_right = y[right_mask]
                if len(y_left) == 0 or len(y_right) == 0:
                    continue
                gini_left = gini_impurity(y_left)
                gini_right = gini_impurity(y_right)
                n_left = len(y_left)
                n_right = len(y_right)
                weighted_gini = n_left / n_samples * gini_left + n_right / n_samples * gini_right
                gain = parent_gini - weighted_gini
                if gain > best_gain:
                    best_gain = gain
                    best_feature = feature
                    best_threshold = threshold
        if best_gain <= 0 or best_feature is None:
            return {'is_leaf': True, 'prediction': majority_class(y)}
        left_mask = X[:, best_feature] < best_threshold
        right_mask = ~left_mask
        if np.sum(left_mask) == 0 or np.sum(right_mask) == 0:
            return {'is_leaf': True, 'prediction': majority_class(y)}
        left_child = build_tree(X[left_mask], y[left_mask], depth + 1)
        right_child = build_tree(X[right_mask], y[right_mask], depth + 1)
        return {'is_leaf': False, 'feature': best_feature, 'threshold': best_threshold, 'left': left_child, 'right': right_child}

    def predict_single(tree, x):
        """Traverse the decision tree to predict the label for a single sample x."""
        if tree['is_leaf']:
            return tree['prediction']
        feature = tree['feature']
        threshold = tree['threshold']
        if x[feature] < threshold:
            return predict_single(tree['left'], x)
        else:
            return predict_single(tree['right'], x)
    tree = build_tree(X_train, y_train, depth=0)
    predictions = []
    for x in X_test:
        predictions.append(predict_single(tree, x))
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported_metrics = {'euclidean', 'manhattan', 'chebyshev', 'cosine'}
    if name not in supported_metrics:

        def invalid_func(_a, _b):
            return -1
        return invalid_func

    def d(vec1, vec2):
        if not isinstance(vec1, (list, tuple, np.ndarray)) or not isinstance(vec2, (list, tuple, np.ndarray)):
            return -1
        try:
            arr1 = np.array(vec1, dtype=float)
            arr2 = np.array(vec2, dtype=float)
        except:
            return -1
        if arr1.ndim != 1 or arr2.ndim != 1:
            return -1
        if arr1.size == 0 or arr2.size == 0 or arr1.size != arr2.size:
            return -1
        if name == 'cosine':
            norm1 = np.linalg.norm(arr1)
            norm2 = np.linalg.norm(arr2)
            if norm1 == 0 or norm2 == 0:
                return -1
        if name == 'euclidean':
            result = np.sqrt(np.sum((arr1 - arr2) ** 2))
        elif name == 'manhattan':
            result = np.sum(np.abs(arr1 - arr2))
        elif name == 'chebyshev':
            result = np.max(np.abs(arr1 - arr2))
        elif name == 'cosine':
            dot_product = np.dot(arr1, arr2)
            result = 1 - dot_product / (np.linalg.norm(arr1) * np.linalg.norm(arr2))
        else:
            return -1
        return round(result, 4)
    return d

import numpy as np
from itertools import product
from typing import Any, List, Tuple, Union
def action_stats(env: Any, md_action: bool, cont_action: bool) -> Tuple[List[Union[int, float]], Union[List[Tuple[int, ...]], None], int]:
    """Summarise an RL environment's action space.

    Args:
        env: Environment instance exposing an ``action_space`` attribute mimicking OpenAI Gym.
        md_action: ``True`` if the action space is multi-dimensional.
        cont_action: ``True`` if the action space is continuous (infinite number of actions).

    Returns:
        A tuple (n_actions_per_dim, action_ids, action_dim) where:
            • n_actions_per_dim: list with the number of actions in every dimension
                                  (np.inf for continuous ones),
            • action_ids: list of all discrete actions (cartesian product) or None if
                           any dimension is continuous,
            • action_dim: total number of action dimensions.
    """
    if md_action:
        n_actions_per_dim: List[Union[int, float]] = []
        contains_continuous = False
        for space in env.action_space.spaces:
            if hasattr(space, 'n'):
                n_actions_per_dim.append(space.n)
            elif hasattr(space, 'shape'):
                n_actions_per_dim.append(np.inf)
                contains_continuous = True
            else:
                n_actions_per_dim.append(np.inf)
                contains_continuous = True
        action_dim = len(n_actions_per_dim)
        if contains_continuous:
            action_ids = None
        else:
            ranges = [range(n) for n in n_actions_per_dim]
            action_ids = [tuple(a) for a in product(*ranges)]
        return (n_actions_per_dim, action_ids, action_dim)
    else:
        if cont_action:
            if hasattr(env.action_space, 'shape'):
                dim = env.action_space.shape[0]
            else:
                dim = 1
            n_actions_per_dim = [np.inf] * dim
            action_ids = None
            action_dim = dim
        else:
            if hasattr(env.action_space, 'n'):
                n = env.action_space.n
            else:
                n = 1
            n_actions_per_dim = [n]
            action_ids = [(i,) for i in range(n)]
            action_dim = 1
        return (n_actions_per_dim, action_ids, action_dim)

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def hand_value(hand: list[int]) -> int:
        total = sum(hand)
        if 1 in hand and total + 10 <= 21:
            total += 10
        return total

    def is_natural_blackjack(hand: list[int]) -> bool:
        return len(hand) == 2 and (1 in hand and 10 in hand)
    player_score = hand_value(player)
    dealer_score = hand_value(dealer)
    if player_score > 21:
        return -1.0
    if dealer_score > 21:
        return 1.0
    if player_score > dealer_score:
        if is_natural_blackjack(player):
            return 1.5
        else:
            return 1.0
    elif player_score == dealer_score:
        return 0.0
    else:
        return -1.0

import numpy as np
def sigmoid(z):
    """Compute the element-wise sigmoid (logistic) function.

    The function must work for scalar numbers, Python lists and NumPy arrays and
    must be numerically stable for very large positive or negative inputs.

    Args:
        z: A scalar (int/float) or array-like object (list or np.ndarray)
           containing numeric values.

    Returns:
        float | list: The sigmoid of the input rounded to 4 decimal places.
        If `z` is a scalar the return value is a float, otherwise it is a Python
        list with the same shape as the input.
    """

    def _sigmoid_scalar(x):
        if x >= 0:
            result = 1 / (1 + np.exp(-x))
        else:
            result = np.exp(x) / (1 + np.exp(x))
        return round(float(result), 4)
    if np.isscalar(z):
        return _sigmoid_scalar(z)
    arr = np.array(z, dtype=float)
    result = np.where(arr >= 0, 1 / (1 + np.exp(-arr)), np.exp(arr) / (1 + np.exp(arr)))
    result = np.round(result, 4)
    return result.tolist()

import numpy as np
def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    """Performs forward and backward passes of a 2-D average-pooling layer.

    The function must return a tuple (pooled_output, grad_input), both rounded
    to the nearest 4-th decimal and converted to regular Python lists using
    `tolist()`.
    """
    (N, C, H, W) = X.shape
    (p_h, p_w) = pool_shape
    (s_h, s_w) = stride
    out_H = (H - p_h) // s_h + 1
    out_W = (W - p_w) // s_w + 1
    pooled = np.zeros((N, C, out_H, out_W))
    for n in range(N):
        for c in range(C):
            for i in range(out_H):
                for j in range(out_W):
                    h_start = i * s_h
                    w_start = j * s_w
                    window = X[n, c, h_start:h_start + p_h, w_start:w_start + p_w]
                    pooled[n, c, i, j] = np.mean(window)
    grad_input = np.zeros_like(X)
    for n in range(N):
        for c in range(C):
            for i in range(out_H):
                for j in range(out_W):
                    h_start = i * s_h
                    w_start = j * s_w
                    gradient = accum_grad[n, c, i, j]
                    grad_input[n, c, h_start:h_start + p_h, w_start:w_start + p_w] += gradient / (p_h * p_w)
    pooled_rounded = np.round(pooled, 4)
    grad_input_rounded = np.round(grad_input, 4)
    return (pooled_rounded.tolist(), grad_input_rounded.tolist())

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if type(tree_a) is not type(tree_b):
        return False
    if isinstance(tree_a, Leaf):
        return np.allclose(tree_a.value, tree_b.value, atol=1e-08)
    if tree_a.feature != tree_b.feature:
        return False
    if not np.isclose(tree_a.threshold, tree_b.threshold, atol=1e-08):
        return False
    return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)

from typing import Any, Dict, List, Tuple
def to_networkx(G: Dict[str, Any]) -> Dict[Any, List[Tuple[Any, float]]]:
    """Convert a custom graph dictionary into a plain adjacency dictionary.

    Parameters
    ----------
    G : dict
        A graph represented as a dictionary with the keys:
          * 'is_directed' (bool)
          * '_V2I'        (dict mapping vertex label -> index)
          * '_G'          (list of adjacency lists, each edge is a
                           tuple (source_label, target_label, weight))

    Returns
    -------
    dict
        A dictionary where each key is a vertex label and the value is a list
        of `(neighbour, weight)` tuples.  For undirected graphs every edge
        appears once in each direction and the neighbour lists are sorted
        alphabetically.
    """
    if not G.get('_V2I'):
        return {}
    adj: Dict[Any, List[Tuple[Any, float]]] = {vertex: [] for vertex in G['_V2I'].keys()}
    is_directed = G.get('is_directed', True)
    if is_directed:
        for edge_list in G.get('_G', []):
            for (u, v, w) in edge_list:
                adj[u].append((v, w))
    else:
        seen = set()
        for edge_list in G.get('_G', []):
            for (u, v, w) in edge_list:
                if u == v:
                    canonical = (u, v, w)
                    if canonical in seen:
                        continue
                    seen.add(canonical)
                    adj[u].append((v, w))
                else:
                    if u <= v:
                        canonical = (u, v, w)
                    else:
                        canonical = (v, u, w)
                    if canonical in seen:
                        continue
                    seen.add(canonical)
                    adj[u].append((v, w))
                    adj[v].append((u, w))
    for vertex in adj:
        adj[vertex].sort(key=lambda edge: edge[0])
    return adj

import math
from collections import Counter, defaultdict
from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    """Return the total log-probability of *query* under an N-gram MLE model.

    Parameters
    ----------
    corpus : list[str]
        A list of training sentences. Each sentence is a plain string; tokens
        are assumed to be separated by white-spaces.
    query : str
        The sentence whose probability should be calculated.
    N : int
        The order of the N-gram model (N ≥ 1).

    Returns
    -------
    float
        The sum of natural logarithms of the probabilities of every
        consecutive N-gram occurring in *query*, rounded to 4 decimal places.
        If any necessary N-gram is unseen in the corpus the function returns
        float('-inf').
    """
    ngram_counts = Counter()
    prefix_counts = Counter()
    total_tokens = 0
    for sentence in corpus:
        tokens = sentence.split()
        if N > 1:
            padded_tokens = ['<bol>'] * (N - 1) + tokens + ['<eol>']
        else:
            padded_tokens = tokens
        if N == 1:
            total_tokens += len(padded_tokens)
        for i in range(len(padded_tokens) - N + 1):
            ngram = tuple(padded_tokens[i:i + N])
            ngram_counts[ngram] += 1
            if N > 1:
                prefix = ngram[:-1]
                prefix_counts[prefix] += 1
    query_tokens = query.split()
    if N > 1:
        padded_query = ['<bol>'] * (N - 1) + query_tokens + ['<eol>']
    else:
        padded_query = query_tokens
    if len(padded_query) < N:
        return float('-inf')
    log_prob_sum = 0.0
    for i in range(len(padded_query) - N + 1):
        q_ngram = tuple(padded_query[i:i + N])
        if q_ngram not in ngram_counts:
            return float('-inf')
        count_ngram = ngram_counts[q_ngram]
        if N == 1:
            prob = count_ngram / total_tokens
        else:
            prefix = q_ngram[:-1]
            if prefix_counts[prefix] == 0:
                return float('-inf')
            prob = count_ngram / prefix_counts[prefix]
        if prob == 0:
            return float('-inf')
        log_prob_sum += math.log(prob)
    return round(log_prob_sum, 4)

import numpy as np
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.
    """
    if max_freq is None:
        max_freq = fs / 2

    def hz_to_mel(f):
        return 2595 * np.log10(1 + f / 700)

    def mel_to_hz(mel):
        return 700 * (10 ** (mel / 2595) - 1)
    min_mel = hz_to_mel(min_freq)
    max_mel = hz_to_mel(max_freq)
    mel_points = np.linspace(min_mel, max_mel, n_filters + 2)
    bin_frequencies = mel_to_hz(mel_points)
    freqs = np.linspace(0, fs / 2, N // 2 + 1)
    filtbank = np.zeros((n_filters, len(freqs)))
    for i in range(1, n_filters + 1):
        f_left = bin_frequencies[i - 1]
        f_center = bin_frequencies[i]
        f_right = bin_frequencies[i + 1]
        left_slope = (freqs - f_left) / (f_center - f_left) if f_center - f_left != 0 else np.zeros_like(freqs)
        right_slope = (f_right - freqs) / (f_right - f_center) if f_right - f_center != 0 else np.zeros_like(freqs)
        filt = np.maximum(0, np.minimum(left_slope, right_slope))
        if normalize:
            filt = filt * (2 / (f_right - f_left))
        filtbank[i - 1, :] = filt
    filtbank_rounded = np.round(filtbank, 4)
    return filtbank_rounded.tolist()

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check(space: Space, composite: bool) -> bool:
        if composite:
            if isinstance(space, Tuple):
                return all((isinstance(s, Box) for s in space.spaces))
            if isinstance(space, Dict):
                return all((isinstance(s, Box) for s in space.spaces.values()))
            return False
        else:
            return isinstance(space, Box)
    cont_action = check(env.action_space, tuple_action)
    cont_obs = check(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def is_symmetric(X: list[list[int | float]]) -> bool:
    """Check whether the given 2-D array *X* is symmetric.

    A matrix is symmetric if it is square and equal to its own transpose.

    Args:
        X: Matrix represented as a list of lists containing numbers.

    Returns:
        True if the matrix is symmetric, otherwise False.
    """
    try:
        arr = np.array(X)
    except Exception:
        return False
    if arr.ndim != 2:
        return False
    (rows, cols) = arr.shape
    if rows != cols:
        return False
    return np.allclose(arr, arr.T, atol=1e-08)

import numpy as np
def env_stats(actions: list, observations: list) -> dict:
    """Compute statistics for collections of actions and observations.

    The function inspects *actions* and *observations* and returns a dictionary
    containing information about dimensionality, data type (discrete or
    continuous) and the unique values appearing in every dimension.

    Parameters
    ----------
    actions : list
        A list with the actions that were taken.  Each element is either a
        scalar or an iterable of scalars (for multi-dimensional spaces).
    observations : list
        A list with the corresponding observations.  Same structural
        requirements as *actions*.

    Returns
    -------
    dict
        A dictionary with the keys described in the task.
    """

    def is_iterable(x):
        return isinstance(x, (list, tuple, np.ndarray))

    def process_data(data):
        tuple_flag = any((is_iterable(x) for x in data))
        first = data[0]
        if is_iterable(first):
            dim = len(first)
        else:
            dim = 1
        multi_dim = dim > 1
        if dim == 1:
            dim_values = [data]
        else:
            dim_values = []
            for i in range(dim):
                dim_values.append([elem[i] for elem in data])
        return (tuple_flag, multi_dim, dim, dim_values)

    def has_noninteger_float(values):
        for x in values:
            if isinstance(x, float) or isinstance(x, np.floating):
                if not np.isclose(x, round(x)):
                    return True
        return False
    (tuple_actions, multidim_actions, action_dim, actions_dim_values) = process_data(actions)
    (tuple_observations, multidim_observations, obs_dim, obs_dim_values) = process_data(observations)
    all_action_values = []
    for sublist in actions_dim_values:
        all_action_values.extend(sublist)
    continuous_actions = has_noninteger_float(all_action_values)
    all_obs_values = []
    for sublist in obs_dim_values:
        all_obs_values.extend(sublist)
    continuous_observations = has_noninteger_float(all_obs_values)

    def unique_per_dim(dim_values):
        ids = []
        counts = []
        for vals in dim_values:
            unique_sorted = sorted(set(vals))
            ids.append(unique_sorted)
            counts.append(len(unique_sorted))
        return (ids, counts)
    (action_ids, n_actions_per_dim) = unique_per_dim(actions_dim_values)
    (obs_ids, n_obs_per_dim) = unique_per_dim(obs_dim_values)
    stats = {'tuple_actions': tuple_actions, 'tuple_observations': tuple_observations, 'multidim_actions': multidim_actions, 'multidim_observations': multidim_observations, 'continuous_actions': continuous_actions, 'continuous_observations': continuous_observations, 'n_actions_per_dim': n_actions_per_dim, 'action_dim': action_dim, 'n_obs_per_dim': n_obs_per_dim, 'obs_dim': obs_dim, 'action_ids': action_ids, 'obs_ids': obs_ids}
    return stats

import numpy as np
def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function returns the MAP predictive means for every row in
    `X_new` obtained by computing the posterior and using it to compute
    predictions. Every prediction is rounded to 4 decimal places.
    """
    if fit_intercept:
        ones_train = np.ones((X.shape[0], 1))
        X = np.hstack((ones_train, X))
        ones_new = np.ones((X_new.shape[0], 1))
        X_new = np.hstack((ones_new, X_new))
    D = X.shape[1]
    if np.isscalar(mu):
        mu_vec = np.full((D,), mu)
    else:
        mu_vec = np.array(mu).flatten()
        if mu_vec.size != D:
            raise ValueError(f'Length of mu vector ({mu_vec.size}) does not match number of features ({D}).')
    if V is None:
        V_matrix = np.eye(D)
    elif np.isscalar(V):
        V_matrix = float(V) * np.eye(D)
    else:
        V = np.array(V)
        if V.ndim == 1:
            if V.size != D:
                raise ValueError(f'Length of prior variance vector ({V.size}) does not match number of features ({D}).')
            V_matrix = np.diag(V)
        elif V.ndim == 2:
            if V.shape != (D, D):
                raise ValueError(f'Shape of prior covariance matrix {V.shape} does not match required shape ({D},{D}).')
            V_matrix = V
        else:
            raise ValueError('V must be scalar, 1-D array, or 2-D square matrix.')
    V_inv = np.linalg.inv(V_matrix)
    XtX = X.T @ X
    posterior_cov = np.linalg.inv(V_inv + XtX)
    posterior_mean = posterior_cov @ (V_inv @ mu_vec + X.T @ y)
    y_pred = X_new @ posterior_mean
    return [round(val, 4) for val in y_pred]

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in (0, 1, 2):
        return -1
    arr = np.array(x)
    sigma = 1 / (1 + np.exp(-arr))
    if order == 0:
        result = sigma
    elif order == 1:
        result = sigma * (1 - sigma)
    elif order == 2:
        result = sigma * (1 - sigma) * (1 - 2 * sigma)
    result = np.around(result, 4)
    if np.ndim(arr) == 0:
        return float(result)
    else:
        return result.tolist()

import itertools
def apriori_frequent_itemsets(transactions: list[list], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    if not transactions:
        return []
    n_transactions = len(transactions)
    trans_sets = [set(t) for t in transactions]

    def is_frequent(count):
        return count / n_transactions >= min_sup
    item_counts = {}
    for trans in trans_sets:
        for item in trans:
            item_counts[item] = item_counts.get(item, 0) + 1
    frequent_itemsets = []
    L1 = set()
    for (item, count) in item_counts.items():
        if is_frequent(count):
            L1.add(frozenset([item]))
            frequent_itemsets.append(frozenset([item]))
    prev_L = L1
    k = 2

    def generate_candidates(prev_freq, k):
        candidates = set()
        prev_list = sorted(prev_freq, key=lambda s: tuple(sorted(s)))
        for i in range(len(prev_list)):
            for j in range(i + 1, len(prev_list)):
                L1 = tuple(sorted(prev_list[i]))
                L2 = tuple(sorted(prev_list[j]))
                if L1[:-1] == L2[:-1]:
                    candidate = prev_list[i] | prev_list[j]
                    if len(candidate) == k:
                        all_subsets_frequent = True
                        for item in candidate:
                            subset = candidate - {item}
                            if subset not in prev_freq:
                                all_subsets_frequent = False
                                break
                        if all_subsets_frequent:
                            candidates.add(candidate)
                else:
                    break
        return candidates
    while prev_L:
        candidates_k = generate_candidates(prev_L, k)
        candidate_counts = {cand: 0 for cand in candidates_k}
        for trans in trans_sets:
            for cand in candidates_k:
                if cand.issubset(trans):
                    candidate_counts[cand] += 1
        current_L = set()
        for (cand, count) in candidate_counts.items():
            if is_frequent(count):
                current_L.add(cand)
                frequent_itemsets.append(cand)
        prev_L = current_L
        k += 1
    result = []
    for freq_set in frequent_itemsets:
        sorted_tuple = tuple(sorted(freq_set))
        result.append(sorted_tuple)
    result.sort(key=lambda x: (len(x), x))
    return result

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function computes:
        k(x, y) = exp(-0.5 * sum_j (((x_j - y_j)/σ_j)^2))
    where σ is either a scalar (isotropic kernel), a list of length equal to the number
    of features (anisotropic kernel), or None (default σ = sqrt(C/2)).

    It validates that input dimensions match and σ is positive. On any invalid
    input, it returns -1. Otherwise, it returns the kernel matrix as a nested list,
    with all entries rounded to 4 decimal places.
    """
    try:
        X_arr = np.array(X, dtype=float)
    except Exception:
        return -1
    if Y is None:
        Y_arr = X_arr
    else:
        try:
            Y_arr = np.array(Y, dtype=float)
        except Exception:
            return -1
    if X_arr.ndim != 2 or Y_arr.ndim != 2:
        return -1
    if X_arr.shape[1] != Y_arr.shape[1]:
        return -1
    C = X_arr.shape[1]
    if sigma is None:
        sigma_vals = np.full((C,), np.sqrt(C / 2))
    elif isinstance(sigma, (float, int)):
        if sigma <= 0:
            return -1
        sigma_vals = np.full((C,), float(sigma))
    elif isinstance(sigma, list):
        if len(sigma) != C:
            return -1
        sigma_vals = np.array(sigma, dtype=float)
        if np.any(sigma_vals <= 0):
            return -1
    else:
        return -1
    X_scaled = X_arr / sigma_vals
    Y_scaled = Y_arr / sigma_vals
    X_norm_sq = np.sum(X_scaled ** 2, axis=1).reshape(-1, 1)
    Y_norm_sq = np.sum(Y_scaled ** 2, axis=1).reshape(1, -1)
    cross_term = np.dot(X_scaled, Y_scaled.T)
    sq_dists = X_norm_sq + Y_norm_sq - 2 * cross_term
    sq_dists = np.maximum(sq_dists, 0)
    K = np.exp(-0.5 * sq_dists)
    K_rounded = np.round(K, 4)
    return K_rounded.tolist()

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if hasattr(root, 'y'):
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for child in root.child:
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    if epsilon == 0:
        return []
    np.random.seed(42)
    n_samples = data.shape[0]
    subsample_size = sample_size if sample_size < n_samples else n_samples
    height_limit = math.ceil(math.log2(subsample_size)) if subsample_size > 0 else 0

    def c(n):
        if n <= 1:
            return 0.0
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * ((n - 1) / n)

    def build_tree(indices, current_depth):
        if current_depth >= height_limit or len(indices) <= 1:
            return {'leaf': True, 'size': len(indices)}
        n_features = data.shape[1]
        feature = np.random.choice(n_features)
        feat_vals = data[indices, feature]
        min_val = feat_vals.min()
        max_val = feat_vals.max()
        if min_val == max_val:
            return {'leaf': True, 'size': len(indices)}
        threshold = np.random.uniform(min_val, max_val)
        left_mask = feat_vals < threshold
        left_indices = indices[left_mask]
        right_indices = indices[~left_mask]
        if len(left_indices) == 0 or len(right_indices) == 0:
            return {'leaf': True, 'size': len(indices)}
        left_tree = build_tree(left_indices, current_depth + 1)
        right_tree = build_tree(right_indices, current_depth + 1)
        return {'leaf': False, 'feature': feature, 'threshold': threshold, 'left': left_tree, 'right': right_tree}

    def path_length(tree, x, current_depth=0):
        if tree['leaf']:
            return current_depth + c(tree['size'])
        feature = tree['feature']
        if x[feature] < tree['threshold']:
            return path_length(tree['left'], x, current_depth + 1)
        else:
            return path_length(tree['right'], x, current_depth + 1)
    trees = []
    all_indices = np.arange(n_samples)
    for _ in range(n_trees):
        if subsample_size < n_samples:
            sample_indices = np.random.choice(n_samples, size=subsample_size, replace=False)
        else:
            sample_indices = all_indices
        trees.append(build_tree(sample_indices, 0))
    phi = 2 * math.log(subsample_size - 1) - 2 * ((subsample_size - 1) / subsample_size) if subsample_size > 1 else 0.0
    avg_path_lengths = np.zeros(n_samples)
    for i in range(n_samples):
        x = data[i]
        path_sum = 0.0
        for tree in trees:
            path_sum += path_length(tree, x)
        avg_path_lengths[i] = path_sum / n_trees
    anomaly_scores = np.zeros(n_samples)
    if phi > 0:
        anomaly_scores = 2 ** (-(avg_path_lengths / phi))
    else:
        anomaly_scores = np.zeros(n_samples)
    n_outliers = math.ceil(epsilon * n_samples)
    if n_outliers == 0:
        return []
    sorted_by_score = np.argsort(anomaly_scores)[::-1]
    outlier_indices = np.sort(sorted_by_score[:n_outliers])
    return list(outlier_indices)

import math
from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    empirical_counts = defaultdict(float)
    (n_train, n_features) = X_train.shape
    candidate_labels = set(y_train.tolist())
    for idx in range(n_train):
        row = X_train[idx]
        true_label = y_train[idx]
        for i in range(n_features):
            key = (i, row[i], true_label)
            empirical_counts[key] += 1.0
    empirical_expectation = {k: v / n_train for (k, v) in empirical_counts.items()}
    weights = {k: 0.0 for k in empirical_expectation.keys()}
    C = 0
    for idx in range(n_train):
        row = X_train[idx]
        for y in candidate_labels:
            count = 0
            for i in range(n_features):
                if (i, row[i], y) in weights:
                    count += 1
            if count > C:
                C = count
    if C < 1:
        C = 1
    for iteration in range(n_iter):
        model_expectation = defaultdict(float)
        for idx in range(n_train):
            row = X_train[idx]
            scores = {}
            for y in candidate_labels:
                s = 0.0
                for i in range(n_features):
                    key = (i, row[i], y)
                    if key in weights:
                        s += weights[key]
                scores[y] = s
            exp_scores = {y: math.exp(scores[y]) for y in candidate_labels}
            Z = sum(exp_scores.values())
            prob = {y: exp_scores[y] / Z for y in candidate_labels}
            for i in range(n_features):
                val = row[i]
                for y in candidate_labels:
                    feature_key = (i, val, y)
                    if feature_key in weights:
                        model_expectation[feature_key] += prob[y]
        for key in model_expectation:
            model_expectation[key] /= n_train
        max_delta = 0.0
        new_weights = {}
        for (key, w) in weights.items():
            emp_exp = empirical_expectation[key]
            mod_exp = model_expectation.get(key, 1e-10)
            delta = 1.0 / C * math.log(emp_exp / mod_exp)
            new_w = w + delta
            new_weights[key] = new_w
            if abs(delta) > max_delta:
                max_delta = abs(delta)
        weights = new_weights
        if max_delta < epsilon:
            break
    predictions = []
    n_test = X_test.shape[0]
    for idx in range(n_test):
        row = X_test[idx]
        best_label = None
        best_score = -float('inf')
        for y in candidate_labels:
            s = 0.0
            for i in range(n_features):
                key = (i, row[i], y)
                if key in weights:
                    s += weights[key]
            if s > best_score:
                best_score = s
                best_label = y
        predictions.append(best_label)
    return predictions
