import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse, and its
        derivative with respect to μ. Each function supports numpy scalars as well
        as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity_link(mu):
        return mu

    def identity_inv_link(eta):
        return eta

    def identity_deriv(mu):
        return np.ones_like(mu)
    identity = {'link': identity_link, 'inv_link': identity_inv_link, 'deriv': identity_deriv}

    def log_link(mu):
        return np.log(mu)

    def log_inv_link(eta):
        return np.exp(eta)

    def log_deriv(mu):
        return 1 / mu
    log = {'link': log_link, 'inv_link': log_inv_link, 'deriv': log_deriv}

    def logit_link(mu):
        return np.log(mu / (1 - mu))

    def logit_inv_link(eta):
        return 1 / (1 + np.exp(-eta))

    def logit_deriv(mu):
        return 1 / (mu * (1 - mu))
    logit = {'link': logit_link, 'inv_link': logit_inv_link, 'deriv': logit_deriv}
    return {'identity': identity, 'log': log, 'logit': logit}

import math
from collections import Counter
from itertools import combinations
from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                      itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    trans_sets = [set(t) for t in transactions]
    N = len(trans_sets)
    if N == 0:
        return {}
    min_support = math.ceil(minsup * N)
    freq_itemsets = {}
    candidate_counter = Counter()
    for t in trans_sets:
        for item in t:
            candidate_counter[item] += 1
    L1 = set()
    for (item, count) in candidate_counter.items():
        if count >= min_support:
            fs = frozenset([item])
            L1.add(fs)
            freq_itemsets[tuple(sorted(fs))] = count
    current_freq_itemsets = L1
    k = 1
    while current_freq_itemsets:
        candidates = set()
        current_freq_list = list(current_freq_itemsets)
        len_current = len(current_freq_list)
        for i in range(len_current):
            for j in range(i + 1, len_current):
                fs1 = current_freq_list[i]
                fs2 = current_freq_list[j]
                candidate = fs1.union(fs2)
                if len(candidate) == k + 1:
                    valid = True
                    for subset in combinations(candidate, k):
                        if frozenset(subset) not in current_freq_itemsets:
                            valid = False
                            break
                    if valid:
                        candidates.add(candidate)
        if not candidates:
            break
        candidate_counts = Counter()
        for t in trans_sets:
            for candidate in candidates:
                if candidate.issubset(t):
                    candidate_counts[candidate] += 1
        next_freq_itemsets = set()
        for (candidate, count) in candidate_counts.items():
            if count >= min_support:
                next_freq_itemsets.add(candidate)
                freq_itemsets[tuple(sorted(candidate))] = count
        current_freq_itemsets = next_freq_itemsets
        k += 1
    return freq_itemsets

def check_data(a, b):
    """Validate and convert the two inputs so that both are numeric.

    The function must accept integers, floats, or numeric strings.  Any string
    should be converted with `float()`; existing numeric values stay as they
    are.  The pair `(a, b)` must then be returned.
    """
    if isinstance(a, str):
        a = float(a)
    if isinstance(b, str):
        b = float(b)
    return (a, b)
def validate_input(function):

    def wrapper(a, b):
        (a, b) = check_data(a, b)
        return function(a, b)
    return wrapper
@validate_input
def sum_numbers(a, b):
    """Return the arithmetic sum of *a* and *b* after validation."""
    result = a + b
    if result == int(result):
        return int(result)
    else:
        return result

from typing import List, Optional
class Node:
    """Binary decision-tree node (provided).

    Non-leaf node: rule = (feature_index, threshold)
    Leaf node:      rule = (None, prediction_value)
    """

    def __init__(self, left: Optional['Node'], right: Optional['Node'], rule: tuple):
        self.left = left
        self.right = right
        self.feature = rule[0]
        self.threshold = rule[1]
def predict_tree(root: Optional[Node], X: List[List[float]]) -> List[float]:
    """Predict labels for a batch of samples using a binary decision tree."""
    if root is None:
        return []
    predictions = []
    for sample in X:
        node = root
        while node.left is not None or node.right is not None:
            if sample[node.feature] <= node.threshold:
                node = node.left
            else:
                node = node.right
        predictions.append(node.threshold)
    return predictions

from typing import List, Union
Number = Union[int, float]
def k_smallest(nums: List[Number], k: int) -> List[Number]:
    """Return the k smallest numbers contained in *nums*.

    The function scans the input list once, maintaining a custom
    max-heap (size at most k) implemented with a Python list. At the end,
    it returns the k smallest elements in ascending order. If *k* is invalid
    (≤0 or larger than len(nums)) it returns -1.
    """
    if k <= 0 or k > len(nums):
        return -1
    max_heap: List[Number] = []
    for num in nums:
        if len(max_heap) < k:
            max_heap.append(num)
            _move_up(max_heap, len(max_heap) - 1)
        elif num < max_heap[0]:
            max_heap[0] = num
            _move_down(max_heap, 0)
    return sorted(max_heap)

import numpy as np
from collections import Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*.

    Your task is to complete this function so that it builds N-gram count
    tables from *corpus* and then returns the total log-probability of
    *sequence* under the resulting unsmoothed language model.

    The return value must be rounded to 4 decimal places.  If any required
    count is zero you should immediately return ``float('-inf')``.
    """
    if len(sequence) < N:
        return 0.0
    if N == 1:
        corpus_unigrams = Counter(corpus)
        total_log_prob = 0.0
        corpus_length = len(corpus)
        for word in sequence:
            count = corpus_unigrams[word]
            if count == 0:
                return float('-inf')
            prob = count / corpus_length
            total_log_prob += np.log(prob)
        return round(total_log_prob, 4)
    ngram_counts = Counter()
    prefix_counts = Counter()
    for i in range(len(corpus) - N + 1):
        ngram = tuple(corpus[i:i + N])
        ngram_counts[ngram] += 1
    for i in range(len(corpus) - N + 2):
        prefix = tuple(corpus[i:i + N - 1])
        prefix_counts[prefix] += 1
    total_log_prob = 0.0
    for i in range(len(sequence) - N + 1):
        prefix = tuple(sequence[i:i + N - 1])
        ngram = tuple(sequence[i:i + N])
        ngram_count = ngram_counts[ngram]
        prefix_count = prefix_counts[prefix]
        if prefix_count == 0 or ngram_count == 0:
            return float('-inf')
        prob = ngram_count / prefix_count
        total_log_prob += np.log(prob)
    return round(total_log_prob, 4)

import numpy as np
def compute_impurity(y, criterion):
    """Compute a node's impurity.

    Args:
        y (numpy.ndarray): 1-D array with the target values.
        criterion (str): One of {"entropy", "gini", "mse"}.

    Returns:
        float: Impurity value rounded to 4 decimal places.
    """
    if y.size == 0:
        return 0.0
    if criterion == 'entropy':
        (classes, counts) = np.unique(y, return_counts=True)
        probabilities = counts / counts.sum()
        entropy = -np.sum(probabilities * np.log2(probabilities))
        return round(entropy, 4)
    elif criterion == 'gini':
        (classes, counts) = np.unique(y, return_counts=True)
        probabilities = counts / counts.sum()
        gini = 1 - np.sum(probabilities ** 2)
        return round(gini, 4)
    elif criterion == 'mse':
        mean_val = np.mean(y)
        mse = np.mean((y - mean_val) ** 2)
        return round(mse, 4)
    else:
        raise ValueError("Unsupported criterion. Choose from 'entropy', 'gini', or 'mse'.")

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or -1 on invalid input.
    """
    if not S or not A or (not B) or (not observations):
        return -1
    n = len(S)
    if len(A) != n:
        return -1
    for row in A:
        if not isinstance(row, list) or len(row) != n:
            return -1
    m = len(B[0])
    if m == 0:
        return -1
    if len(B) != n:
        return -1
    for row in B:
        if not isinstance(row, list) or len(row) != m:
            return -1
    for obs in observations:
        if not isinstance(obs, int) or obs < 0 or obs >= m:
            return -1
    S_arr = np.array(S, dtype=float)
    A_arr = np.array(A, dtype=float)
    B_arr = np.array(B, dtype=float)
    alpha = S_arr * B_arr[:, observations[0]]
    for t in range(1, len(observations)):
        alpha = alpha @ A_arr * B_arr[:, observations[t]]
    likelihood = float(np.sum(alpha))
    return round(likelihood, 4)

import numpy as np
def relu_(Z):
    """Apply the element-wise Rectified Linear Unit (ReLU) activation.

    The function keeps the container type of the input:
    scalar → scalar, list → list, NumPy array → NumPy array.

    Args:
        Z (int | float | list | numpy.ndarray): Input data – scalar, 1-D or 2-D.

    Returns:
        Same type as *Z* with all negative values clipped to 0.
    """
    if isinstance(Z, (int, float)):
        return max(0, Z)
    if isinstance(Z, np.ndarray):
        return np.maximum(Z, 0)
    if isinstance(Z, list):
        Z_arr = np.array(Z)
        relu_result = np.maximum(Z_arr, 0)
        return relu_result.tolist()
    raise TypeError('Input must be a scalar, a list, or a numpy array.')

import numpy as np
def best_arm(payoff_probs: list[float]) -> tuple[float, int]:
    """Find the arm with the highest expected reward in a Bernoulli bandit.

    Parameters
    ----------
    payoff_probs : list[float]
        A list where each element is the success probability of the
        corresponding arm. Probabilities must lie in the interval [0, 1].

    Returns
    -------
    tuple
        (max_expected_reward, best_arm_index).  If the input list is empty or
        contains an invalid probability (<0 or >1), the function returns
        (-1.0, -1).
    """
    if not payoff_probs:
        return (-1.0, -1)
    for p in payoff_probs:
        if p < 0 or p > 1:
            return (-1.0, -1)
    max_prob = np.max(payoff_probs)
    best_index = int(np.argmax(payoff_probs))
    return (float(max_prob), best_index)

def accuracy_score(y_true, y_pred):
    """Return the classification accuracy between *y_true* and *y_pred*.

    The function must:
      • return -1 if the two containers have different lengths;
      • return 0 when both containers are empty;
      • otherwise return the proportion of matching elements rounded to
        4 decimal places.

    Args:
        y_true (list | tuple): Ground-truth class labels.
        y_pred (list | tuple): Predicted class labels.

    Returns:
        float | int: The accuracy or -1 according to the rules above.
    """
    if len(y_true) != len(y_pred):
        return -1
    if len(y_true) == 0:
        return 0
    match_count = 0
    for (true_label, pred_label) in zip(y_true, y_pred):
        if true_label == pred_label:
            match_count += 1
    accuracy = match_count / len(y_true)
    return round(accuracy, 4)

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    def gini_impurity(y):
        """Compute the Gini impurity for a vector of labels y."""
        if len(y) == 0:
            return 0
        (unique, counts) = np.unique(y, return_counts=True)
        probs = counts / counts.sum()
        return 1 - np.sum(probs ** 2)

    def majority_class(y):
        """Return the majority class label from y.
        In case of ties, np.argmax on bincount returns the smallest label."""
        counts = np.bincount(y)
        return int(np.argmax(counts))

    def build_tree(X, y, depth):
        if np.all(y == y[0]):
            return {'is_leaf': True, 'prediction': int(y[0])}
        if max_depth is not None and depth >= max_depth:
            return {'is_leaf': True, 'prediction': majority_class(y)}
        (n_samples, n_features) = X.shape
        parent_gini = gini_impurity(y)
        best_gain = 0.0
        best_feature = None
        best_threshold = None
        for feature in range(n_features):
            sorted_idx = np.argsort(X[:, feature])
            X_sorted = X[sorted_idx, feature]
            y_sorted = y[sorted_idx]
            for i in range(1, n_samples):
                if X_sorted[i] == X_sorted[i - 1]:
                    continue
                threshold = (X_sorted[i] + X_sorted[i - 1]) / 2.0
                left_mask = X[:, feature] < threshold
                right_mask = ~left_mask
                y_left = y[left_mask]
                y_right = y[right_mask]
                if len(y_left) == 0 or len(y_right) == 0:
                    continue
                gini_left = gini_impurity(y_left)
                gini_right = gini_impurity(y_right)
                n_left = len(y_left)
                n_right = len(y_right)
                weighted_gini = n_left / n_samples * gini_left + n_right / n_samples * gini_right
                gain = parent_gini - weighted_gini
                if gain > best_gain:
                    best_gain = gain
                    best_feature = feature
                    best_threshold = threshold
        if best_gain <= 0 or best_feature is None:
            return {'is_leaf': True, 'prediction': majority_class(y)}
        left_mask = X[:, best_feature] < best_threshold
        right_mask = ~left_mask
        if np.sum(left_mask) == 0 or np.sum(right_mask) == 0:
            return {'is_leaf': True, 'prediction': majority_class(y)}
        left_child = build_tree(X[left_mask], y[left_mask], depth + 1)
        right_child = build_tree(X[right_mask], y[right_mask], depth + 1)
        return {'is_leaf': False, 'feature': best_feature, 'threshold': best_threshold, 'left': left_child, 'right': right_child}

    def predict_single(tree, x):
        """Traverse the decision tree to predict the label for a single sample x."""
        if tree['is_leaf']:
            return tree['prediction']
        feature = tree['feature']
        threshold = tree['threshold']
        if x[feature] < threshold:
            return predict_single(tree['left'], x)
        else:
            return predict_single(tree['right'], x)
    tree = build_tree(X_train, y_train, depth=0)
    predictions = []
    for x in X_test:
        predictions.append(predict_single(tree, x))
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported_metrics = {'euclidean', 'manhattan', 'chebyshev', 'cosine'}
    if name not in supported_metrics:

        def invalid_func(_a, _b):
            return -1
        return invalid_func

    def d(vec1, vec2):
        if not isinstance(vec1, (list, tuple, np.ndarray)) or not isinstance(vec2, (list, tuple, np.ndarray)):
            return -1
        try:
            arr1 = np.array(vec1, dtype=float)
            arr2 = np.array(vec2, dtype=float)
        except:
            return -1
        if arr1.ndim != 1 or arr2.ndim != 1:
            return -1
        if arr1.size == 0 or arr2.size == 0 or arr1.size != arr2.size:
            return -1
        if name == 'cosine':
            norm1 = np.linalg.norm(arr1)
            norm2 = np.linalg.norm(arr2)
            if norm1 == 0 or norm2 == 0:
                return -1
        if name == 'euclidean':
            result = np.sqrt(np.sum((arr1 - arr2) ** 2))
        elif name == 'manhattan':
            result = np.sum(np.abs(arr1 - arr2))
        elif name == 'chebyshev':
            result = np.max(np.abs(arr1 - arr2))
        elif name == 'cosine':
            dot_product = np.dot(arr1, arr2)
            result = 1 - dot_product / (np.linalg.norm(arr1) * np.linalg.norm(arr2))
        else:
            return -1
        return round(result, 4)
    return d

import numpy as np
def softsign(z):
    """Compute the Softsign activation function.

    Softsign(z) = z / (1 + |z|)

    The function accepts either a scalar or a list/NumPy array. For a scalar it
    returns a float; for an array-like input it returns a list of floats, with
    every element rounded to 4 decimal places.
    """
    if np.isscalar(z):
        return round(z / (1 + abs(z)), 4)
    arr = np.array(z)
    if arr.size == 0:
        return []
    softsign_values = arr / (1 + np.abs(arr))
    return np.round(softsign_values, 4).tolist()

import numpy as np
from itertools import product
from typing import Any, List, Tuple, Union
def action_stats(env: Any, md_action: bool, cont_action: bool) -> Tuple[List[Union[int, float]], Union[List[Tuple[int, ...]], None], int]:
    """Summarise an RL environment's action space.

    Args:
        env: Environment instance exposing an ``action_space`` attribute mimicking OpenAI Gym.
        md_action: ``True`` if the action space is multi-dimensional.
        cont_action: ``True`` if the action space is continuous (infinite number of actions).

    Returns:
        A tuple (n_actions_per_dim, action_ids, action_dim) where:
            • n_actions_per_dim: list with the number of actions in every dimension
                                  (np.inf for continuous ones),
            • action_ids: list of all discrete actions (cartesian product) or None if
                           any dimension is continuous,
            • action_dim: total number of action dimensions.
    """
    if md_action:
        n_actions_per_dim: List[Union[int, float]] = []
        contains_continuous = False
        for space in env.action_space.spaces:
            if hasattr(space, 'n'):
                n_actions_per_dim.append(space.n)
            elif hasattr(space, 'shape'):
                n_actions_per_dim.append(np.inf)
                contains_continuous = True
            else:
                n_actions_per_dim.append(np.inf)
                contains_continuous = True
        action_dim = len(n_actions_per_dim)
        if contains_continuous:
            action_ids = None
        else:
            ranges = [range(n) for n in n_actions_per_dim]
            action_ids = [tuple(a) for a in product(*ranges)]
        return (n_actions_per_dim, action_ids, action_dim)
    else:
        if cont_action:
            if hasattr(env.action_space, 'shape'):
                dim = env.action_space.shape[0]
            else:
                dim = 1
            n_actions_per_dim = [np.inf] * dim
            action_ids = None
            action_dim = dim
        else:
            if hasattr(env.action_space, 'n'):
                n = env.action_space.n
            else:
                n = 1
            n_actions_per_dim = [n]
            action_ids = [(i,) for i in range(n)]
            action_dim = 1
        return (n_actions_per_dim, action_ids, action_dim)

import numpy as np
import math
def num_missing(data) -> int:
    """Count missing values in a 1-D iterable.

    An element is missing if it is ``None`` or ``NaN``.

    Args:
        data: Any 1-D iterable (list, tuple, NumPy array, etc.).

    Returns:
        int: The number of missing elements.
    """
    data_list = list(data)
    missing_count = 0
    for item in data_list:
        if item is None:
            missing_count += 1
        else:
            try:
                if math.isnan(item):
                    missing_count += 1
            except TypeError:
                pass
    return int(missing_count)

import functools
from typing import Callable, Any
def doublewrap(decorator_func: Callable[..., Callable[..., Any]]) -> Callable[..., Any]:
    """Return a decorator that can be used *with* or *without* parentheses.

    Args:
        decorator_func: A callable that expects the function to decorate as
            its first positional argument followed only by *optional*
            positional or keyword arguments.

    Returns:
        A new decorator that forwards the call in the correct form so that
        ``decorator_func`` can be applied either directly or after receiving
        its own optional parameters.
    """

    @functools.wraps(decorator_func)
    def wrapper(*args, **kwargs):
        if len(args) == 1 and callable(args[0]) and (not kwargs):
            return decorator_func(args[0])
        else:

            def real_decorator(func):
                return decorator_func(func, *args, **kwargs)
            return real_decorator
    return wrapper

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def hand_value(hand: list[int]) -> int:
        total = sum(hand)
        if 1 in hand and total + 10 <= 21:
            total += 10
        return total

    def is_natural_blackjack(hand: list[int]) -> bool:
        return len(hand) == 2 and (1 in hand and 10 in hand)
    player_score = hand_value(player)
    dealer_score = hand_value(dealer)
    if player_score > 21:
        return -1.0
    if dealer_score > 21:
        return 1.0
    if player_score > dealer_score:
        if is_natural_blackjack(player):
            return 1.5
        else:
            return 1.0
    elif player_score == dealer_score:
        return 0.0
    else:
        return -1.0

import numpy as np
def sigmoid(z):
    """Compute the element-wise sigmoid (logistic) function.

    The function must work for scalar numbers, Python lists and NumPy arrays and
    must be numerically stable for very large positive or negative inputs.

    Args:
        z: A scalar (int/float) or array-like object (list or np.ndarray)
           containing numeric values.

    Returns:
        float | list: The sigmoid of the input rounded to 4 decimal places.
        If `z` is a scalar the return value is a float, otherwise it is a Python
        list with the same shape as the input.
    """

    def _sigmoid_scalar(x):
        if x >= 0:
            result = 1 / (1 + np.exp(-x))
        else:
            result = np.exp(x) / (1 + np.exp(x))
        return round(float(result), 4)
    if np.isscalar(z):
        return _sigmoid_scalar(z)
    arr = np.array(z, dtype=float)
    result = np.where(arr >= 0, 1 / (1 + np.exp(-arr)), np.exp(arr) / (1 + np.exp(arr)))
    result = np.round(result, 4)
    return result.tolist()

import numpy as np
def gaussian_naive_bayes(X_train, y_train, X_test):
    """Implement Gaussian Naive Bayes for binary classification (classes 0 and 1)."""
    eps = 1e-09
    classes = np.unique(y_train)
    n_features = X_train.shape[1]
    priors = {}
    means = {}
    variances = {}
    for c in classes:
        X_c = X_train[y_train == c]
        priors[c] = X_c.shape[0] / X_train.shape[0]
        means[c] = np.mean(X_c, axis=0)
        variances[c] = np.var(X_c, axis=0) + eps

    def log_likelihood(x, c):
        mean = means[c]
        var = variances[c]
        log_prob_features = -0.5 * np.log(2 * np.pi * var) - (x - mean) ** 2 / (2 * var)
        return np.sum(log_prob_features)
    predictions = []
    for x in X_test:
        posteriors = {}
        for c in classes:
            posteriors[c] = np.log(priors[c]) + log_likelihood(x, c)
        predicted_class = max(posteriors, key=posteriors.get)
        predictions.append(int(predicted_class))
    return predictions

import numpy as np
def create_encoding_dicts(obs_ids: list, action_ids: list, obs_encoder: dict | None=None, act_encoder: dict | None=None) -> dict:
    """Generate forward and backward mappings for observations and actions.

    Args:
        obs_ids (list): All distinct observation identifiers.
        action_ids (list): All distinct action identifiers.
        obs_encoder (dict | None, optional): Existing obs→number mapping.
