import numpy as np
def random_binary_tensor(shape: tuple, sparsity: float=0.5) -> list:
    """Generate a random binary tensor.

    The function creates a tensor of the given `shape` such that each entry
    independently is 1.0 with probability `sparsity` and 0.0 otherwise.

    The result is returned as a nested Python list (using NumPy's tolist() method).
    If either `shape` or `sparsity` is illegal (see task description),
    the function returns -1.
    """
    if not isinstance(shape, tuple) or len(shape) == 0:
        return -1
    for dim in shape:
        if not isinstance(dim, int) or dim <= 0:
            return -1
    if not isinstance(sparsity, (int, float)) or sparsity < 0 or sparsity > 1:
        return -1
    random_values = np.random.rand(*shape)
    threshold = 1 - sparsity
    binary_tensor = np.where(random_values >= threshold, 1.0, 0.0)
    return binary_tensor.tolist()

import numpy as np
def _apply_activation(x, func):
    """
    Helper function to apply an element-wise activation function and round
    numerical results to 4 decimal places while preserving the input structure.

    If x is a scalar (int or float), return a scalar.
    If x is a list, tuple or 1-D np.array, return a 1-D np.array.
    """
    if isinstance(x, (int, float)):
        return round(func(x), 4)
    else:
        arr = np.array(x, dtype=float).flatten()
        vec_func = np.vectorize(func)
        result = vec_func(arr)
        return np.round(result, 4)
def sigmoid(x):
    """Element-wise logistic sigmoid function."""

    def sig(z):
        return 1 / (1 + np.exp(-z))
    return _apply_activation(x, sig)
def tanh(x):
    """Element-wise hyperbolic tangent function."""

    def tanh_inner(z):
        return np.tanh(z)
    return _apply_activation(x, tanh_inner)
def relu(x):
    """Element-wise Rectified Linear Unit function."""

    def relu_inner(z):
        return max(0, z)
    return _apply_activation(x, relu_inner)
def leaky_relu(x, alpha: float=0.01):
    """Element-wise Leaky-ReLU function with slope *alpha* for x < 0."""

    def leaky_relu_inner(z):
        return z if z >= 0 else alpha * z
    return _apply_activation(x, leaky_relu_inner)
def softmax(x):
    """
    Computes the numerically stable softmax over a one-dimensional input.
    The output is a 1-D numpy array with each element rounded to 4 decimal places.
    """
    arr = np.array(x, dtype=float).flatten()
    shift = arr - np.max(arr)
    exps = np.exp(shift)
    sum_exps = np.sum(exps)
    softmax_result = exps / sum_exps
    return np.round(softmax_result, 4)
def get_activation(name):
    """
    Return the activation function by name.

    Args:
        name (str): One of {"sigmoid", "tanh", "relu", "leaky_relu", "softmax"}

    Returns:
        Callable: the requested activation function.

    Raises:
        ValueError: If name is not recognised.
    """
    activations = {'sigmoid': sigmoid, 'tanh': tanh, 'relu': relu, 'leaky_relu': leaky_relu, 'softmax': softmax}
    if name in activations:
        return activations[name]
    else:
        raise ValueError('Invalid activation function.')

import numpy as np
def feedforward_predict(x, weights, activations):
    """Runs a forward pass through a fully-connected neural network.

    Parameters
    ----------
    x : list[float] | numpy.ndarray
        Input feature vector (bias term **not** included).
    weights : list[list[list[float]]]
        List of weight matrices; each inner list represents one 2-D weight
        matrix where the first column contains the bias weights.
    activations : list[str]
        Activation functions per layer.  Allowed values: "sigmoid", "relu",
        "linear".

    Returns
    -------
    float | list[float]
        Network output rounded to 4 decimal places.  A scalar is returned for
        a single-neuron output layer, otherwise a list is returned.
    """
    a = np.array(x, dtype=float)

    def sigmoid(z):
        return 1 / (1 + np.exp(-z))

    def relu(z):
        return np.maximum(0, z)

    def linear(z):
        return z
    activation_map = {'sigmoid': sigmoid, 'relu': relu, 'linear': linear}
    for (W, act_name) in zip(weights, activations):
        W = np.array(W, dtype=float)
        a_with_bias = np.concatenate(([1.0], a))
        z = np.dot(W, a_with_bias)
        a = activation_map[act_name](z)
    a = np.round(a, 4)
    if a.size == 1:
        return a.item()
    else:
        return a.tolist()

import numpy as np
from itertools import combinations, permutations
def random_unweighted_graph(n_vertices: int, edge_prob: float=0.5, directed: bool=False) -> list[list[int]]:
    """Generate an un-weighted Erdős–Rényi random graph.

    The function returns the adjacency matrix (a nested list) of the graph.

    Args:
        n_vertices (int): Number of vertices in the graph (labelled 0..n-1).
        edge_prob  (float): Probability *p* that an admissible edge exists.
        directed   (bool):  Whether the graph is directed (default False).

    Returns:
        list[list[int]]: The adjacency matrix A where A[i][j] == 1 iff an edge
        from vertex i to j exists. The matrix consists of integers (0/1) only
        and has shape (n_vertices, n_vertices).
    """
    if n_vertices == 1:
        return [[0]]
    adj_matrix = [[0 for _ in range(n_vertices)] for _ in range(n_vertices)]
    if directed:
        for i in range(n_vertices):
            for j in range(n_vertices):
                if i != j:
                    if np.random.rand() <= edge_prob:
                        adj_matrix[i][j] = 1
    else:
        for (i, j) in combinations(range(n_vertices), 2):
            if np.random.rand() <= edge_prob:
                adj_matrix[i][j] = 1
                adj_matrix[j][i] = 1
    return adj_matrix

def evaluate_decision_tree(tree: dict, sample: list[float]):
    """Traverse a binary decision-tree represented by nested dictionaries.

    Args:
        tree (dict): Root node of the decision tree. Internal nodes contain
            'feature_i', 'threshold', 'true_branch', 'false_branch'. Leaf
            nodes contain only 'value'.
        sample (list[float]): Feature vector that will be classified/regressed.

    Returns:
        The value stored in the reached leaf (int, float, or str).
    """
    if 'value' in tree and len(tree) == 1:
        return tree['value']
    feature_i = tree['feature_i']
    threshold = tree['threshold']
    if sample[feature_i] <= threshold:
        return evaluate_decision_tree(tree['true_branch'], sample)
    else:
        return evaluate_decision_tree(tree['false_branch'], sample)

