def calculate_matrix_mean(matrix: list[list[float]], mode: str) -> list[float]:
    """
    Calculate the mean of a matrix either by row or by column.

    Parameters:
        matrix (list of list of float): The input matrix.
        mode (str): 'row' to compute mean across rows,
                    'column' to compute mean across columns.

    Returns:
        list of float: A list of means for each row or each column.

    Raises:
        ValueError: If mode is not 'row' or 'column'.
    """
    if mode not in ('row', 'column'):
        raise ValueError("mode must be 'row' or 'column'")
    if not matrix:
        return []
    if mode == 'row':
        means = []
        for row in matrix:
            if row:
                means.append(sum(row) / len(row))
            else:
                means.append(float('nan'))
        return means
    else:
        max_cols = max((len(row) for row in matrix), default=0)
        means = []
        for col_idx in range(max_cols):
            col_vals = [row[col_idx] for row in matrix if col_idx < len(row)]
            if col_vals:
                means.append(sum(col_vals) / len(col_vals))
            else:
                means.append(float('nan'))
        return means
assert calculate_matrix_mean([[1, 2, 3], [4, 5, 6], [7, 8, 9]], 'column') == [4.0, 5.0, 6.0]
assert calculate_matrix_mean([[1, 2, 3], [4, 5, 6], [7, 8, 9]], 'row') == [2.0, 5.0, 8.0]

