from collections import Counter
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    """
    Learn a decision tree using recursive binary splitting based
    on entropy and information gain.

    Returns a nested dictionary representing the tree:
    - If node is leaf: {'label': class_label}
    - If internal node: {'attr': attribute_name, 'nodes': {value1: subtree1, ...}}
    """

    def plurality_value(exs):
        """Return the most common target value among examples."""
        counts = Counter((e[target_attr] for e in exs))
        return counts.most_common(1)[0][0] if counts else None

    def entropy(exs):
        """Compute the entropy of the target attribute in examples."""
        total = len(exs)
        if total == 0:
            return 0
        counts = Counter((e[target_attr] for e in exs))
        ent = 0.0
        for count in counts.values():
            p = count / total
            ent -= p * math.log2(p)
        return ent

    def information_gain(exs, attr):
        """Compute information gain of splitting exs on attribute attr."""
        base_ent = entropy(exs)
        total = len(exs)
        subsets = {}
        for e in exs:
            subsets.setdefault(e[attr], []).append(e)
        subset_ent = sum((len(sub) / total * entropy(sub) for sub in subsets.values()))
        return base_ent - subset_ent

    def dt(exs, attrs, parent_exs):
        """
        The recursive decision tree learner.
        exs: current examples
        attrs: remaining attributes
        parent_exs: examples at parent node (for plurality in case exs is empty)
        """
        if not exs:
            return {'label': plurality_value(parent_exs)}
        target_vals = [e[target_attr] for e in exs]
        if target_vals.count(target_vals[0]) == len(target_vals):
            return {'label': target_vals[0]}
        if not attrs:
            return {'label': plurality_value(exs)}
        gains = [(information_gain(exs, a), a) for a in attrs]
        (_, best_attr) = max(gains, key=lambda x: x[0])
        tree = {'attr': best_attr, 'nodes': {}}
        values = set((e[best_attr] for e in exs))
        for v in values:
            subset = [e for e in exs if e[best_attr] == v]
            subtree = dt(subset, [a for a in attrs if a != best_attr], exs)
            tree['nodes'][v] = subtree
        return tree
    return dt(examples, attributes, examples)
