def calculate_brightness(img):
    """
    Calculate the average brightness of a grayscale image represented as a 2D matrix.

    Args:
        img (list of list of int/float): 2D matrix of pixel values (0–255).

    Returns:
        float: Average brightness rounded to two decimal places,
               or -1 if the input is invalid.
    """
    if not img or not isinstance(img, list):
        return -1
    row_length = None
    for row in img:
        if not isinstance(row, list) or len(row) == 0:
            return -1
        if row_length is None:
            row_length = len(row)
        elif len(row) != row_length:
            return -1
    total_pixels = len(img) * row_length
    if total_pixels == 0:
        return -1
    total_value = 0.0
    for row in img:
        for pixel in row:
            if not isinstance(pixel, (int, float)) or pixel < 0 or pixel > 255:
                return -1
            total_value += pixel
    average = total_value / total_pixels
    return round(average, 2)
assert calculate_brightness([]) == -1
assert calculate_brightness([[100, 200], [150]]) == -1
assert calculate_brightness([[100, 300]]) == -1
assert calculate_brightness([[100, -1]]) == -1
assert calculate_brightness([[128]]) == 128.0
assert calculate_brightness([[100, 200], [50, 150]]) == 125.0

