import numpy as np
def load_encoder_hparams_and_params(model_size: str='124M', models_dir: str='models'):
    """
    Dummy loader that returns:
     - a simple BPE encoder with a tiny vocab,
     - minimal hyperparameters,
     - randomly initialized model parameters (token + position embeddings,
       an empty list of transformer blocks, and a final layer norm).
    """

    class DummyBPE:

        def __init__(self):
            self.encoder_dict = {'<UNK>': 0, 'hello': 1, 'world': 2}

        def encode(self, text: str):
            tokens = text.strip().split()
            return [self.encoder_dict.get(t, self.encoder_dict['<UNK>']) for t in tokens]

        def decode(self, token_ids: list):
            rev = {v: k for (k, v) in self.encoder_dict.items()}
            return ' '.join((rev.get(tid, '<UNK>') for tid in token_ids))
    hparams = {'n_ctx': 1024, 'n_head': 12}
    params = {'wte': np.random.randn(3, 10) * 0.1, 'wpe': np.random.randn(hparams['n_ctx'], 10) * 0.1, 'blocks': [], 'ln_f': {'g': np.ones(10), 'b': np.zeros(10)}}
    encoder = DummyBPE()
    return (encoder, hparams, params)
def gelu(x):
    """Gaussian Error Linear Unit activation."""
    return 0.5 * x * (1.0 + np.tanh(np.sqrt(2.0 / np.pi) * (x + 0.044715 * x ** 3)))
def softmax(x, axis=-1):
    """Numerically stable softmax."""
    x_max = np.max(x, axis=axis, keepdims=True)
    e_x = np.exp(x - x_max)
    return e_x / np.sum(e_x, axis=axis, keepdims=True)
def layer_norm(x, g, b, eps=1e-05):
    """
    Apply LayerNorm over the last dimension of x.
    g, b are of shape (d_model,)
    """
    if x.ndim == 1:
        mu = x.mean()
        var = x.var()
        return g * (x - mu) / np.sqrt(var + eps) + b
    elif x.ndim == 2:
        mu = x.mean(axis=1, keepdims=True)
        var = x.var(axis=1, keepdims=True)
        return g * (x - mu) / np.sqrt(var + eps) + b
    else:
        raise ValueError('Unsupported tensor rank for layer_norm')
def gen_text(prompt: str, n_tokens_to_generate: int=40):
    """
    Simplified GPT-2–style autoregressive generation:
      - token + positional embeddings
      - one block of self-attention + feed-forward + residual + layer norms
      - final layer norm + linear projection to token logits
      - sample next token via softmax
    """
    (encoder, hparams, params) = load_encoder_hparams_and_params()
    token_ids = encoder.encode(prompt)
    wte = params['wte']
    wpe = params['wpe']
    ln_f = params['ln_f']
    (vocab_size, d_model) = wte.shape
    d_ff = 4 * d_model
    W1 = np.random.randn(d_model, d_ff) * 0.1
    b1 = np.zeros(d_ff)
    W2 = np.random.randn(d_ff, d_model) * 0.1
    b2 = np.zeros(d_model)
    for _ in range(n_tokens_to_generate):
        if len(token_ids) > hparams['n_ctx']:
            token_ids = token_ids[-hparams['n_ctx']:]
        seq_len = len(token_ids)
        x = wte[token_ids] + wpe[:seq_len]
        scale = np.sqrt(d_model).astype(np.float32)
        scores = x @ x.T / scale
        attn_probs = softmax(scores, axis=-1)
        attn_output = attn_probs @ x
        x = x + attn_output
        x = layer_norm(x, np.ones(d_model), np.zeros(d_model))
        ff_hidden = gelu(x @ W1 + b1)
        ff_output = ff_hidden @ W2 + b2
        x = x + ff_output
        x = layer_norm(x, np.ones(d_model), np.zeros(d_model))
        x_last = layer_norm(x[-1], ln_f['g'], ln_f['b'])
        logits = x_last @ wte.T
        probs = softmax(logits)
        next_token = np.random.choice(vocab_size, p=probs)
        token_ids.append(int(next_token))
    return encoder.decode(token_ids)
np.random.seed(42)
assert gen_text("hello", 5) == "hello hello hello <UNK> <UNK>"
