from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    N = len(transactions)
    if N == 0:
        return {}
    threshold = math.ceil(minsup * N)
    if threshold <= 0:
        return {}
    txn_sets = [set(t) for t in transactions]
    item_counts = Counter()
    for tset in txn_sets:
        item_counts.update(tset)
    L1 = {(item,): cnt for (item, cnt) in item_counts.items() if cnt >= threshold}
    if not L1:
        return {}
    frequent_itemsets: Dict[Tuple[Hashable, ...], int] = {}
    frequent_itemsets.update(L1)
    Lk_minus_1 = L1
    k = 2
    while True:
        prev_itemsets = sorted(Lk_minus_1.keys())
        Ck = set()
        len_prev = len(prev_itemsets)
        for i in range(len_prev):
            for j in range(i + 1, len_prev):
                p = prev_itemsets[i]
                q = prev_itemsets[j]
                if p[:k - 2] == q[:k - 2]:
                    candidate = tuple(sorted(p + (q[-1],)))
                    all_subsets_frequent = True
                    for subset in combinations(candidate, k - 1):
                        if subset not in Lk_minus_1:
                            all_subsets_frequent = False
                            break
                    if all_subsets_frequent:
                        Ck.add(candidate)
        if not Ck:
            break
        candidate_counts = Counter()
        for tset in txn_sets:
            for cand in Ck:
                if set(cand).issubset(tset):
                    candidate_counts[cand] += 1
        Lk = {cand: cnt for (cand, cnt) in candidate_counts.items() if cnt >= threshold}
        if not Lk:
            break
        frequent_itemsets.update(Lk)
        Lk_minus_1 = Lk
        k += 1
    return frequent_itemsets
