from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """

    class FPNode:
        __slots__ = ('name', 'count', 'parent', 'children', 'link')

        def __init__(self, name, parent):
            self.name = name
            self.count = 0
            self.parent = parent
            self.children = {}
            self.link = None

    def update_header(item, node, header_table):
        head = header_table[item][1]
        if head is None:
            header_table[item][1] = node
        else:
            while head.link:
                head = head.link
            head.link = node

    def insert_tree(items, node, header_table, count=1):
        if not items:
            return
        first = items[0]
        if first in node.children:
            child = node.children[first]
            child.count += count
        else:
            child = FPNode(first, node)
            child.count = count
            node.children[first] = child
            update_header(first, child, header_table)
        insert_tree(items[1:], child, header_table, count)
    freq = Counter()
    for tran in transactions:
        freq.update(tran)
    freq = {item: cnt for (item, cnt) in freq.items() if cnt >= min_support}
    if not freq:
        return []
    sorted_items = sorted(freq.items(), key=lambda x: (-x[1], x[0]))

    def sort_key(x):
        return (-freq[x], x)
    header_table = {item: [freq[item], None] for (item, _) in sorted_items}
    root = FPNode(None, None)
    for tran in transactions:
        filtered = [i for i in tran if i in freq]
        if not filtered:
            continue
        ordered = sorted(filtered, key=sort_key)
        insert_tree(ordered, root, header_table)
    frequent_itemsets = []

    def mine_tree(header, prefix):
        for item in sorted(header.keys()):
            new_prefix = prefix + [item]
            frequent_itemsets.append(new_prefix.copy())
            conditional_patterns = []
            node = header[item][1]
            while node:
                count = node.count
                path = []
                parent = node.parent
                while parent and parent.name is not None:
                    path.append(parent.name)
                    parent = parent.parent
                if path:
                    conditional_patterns.append((path[::-1], count))
                node = node.link
            cpb_freq = Counter()
            for (path, cnt) in conditional_patterns:
                for itm in path:
                    cpb_freq[itm] += cnt
            cond_items = {i for (i, c) in cpb_freq.items() if c >= min_support}
            if not cond_items:
                continue
            sorted_c = sorted(cond_items, key=lambda x: (-cpb_freq[x], x))
            cond_header = {i: [cpb_freq[i], None] for i in sorted_c}
            cond_root = FPNode(None, None)
            for (path, cnt) in conditional_patterns:
                filtered_path = [i for i in path if i in cond_items]
                if not filtered_path:
                    continue
                ordered_path = sorted(filtered_path, key=lambda x: (-cpb_freq[x], x))
                insert_tree(ordered_path, cond_root, cond_header, cnt)
            mine_tree(cond_header, new_prefix)
    mine_tree(header_table, [])
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
