import math
def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    if n == 0:
        return -1
    if n == 1:
        return 0 if grid[0][0] == '.' else -1
    if grid[0][0] != '.' or grid[n - 1][n - 1] != '.':
        return -1
    INF = math.inf
    fire_time = [[INF] * n for _ in range(n)]
    q = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_time[i][j] = 0
                q.append((i, j))
    while q:
        (x, y) = q.popleft()
        t0 = fire_time[x][y]
        for (dx, dy) in ((1, 0), (-1, 0), (0, 1), (0, -1)):
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n:
                if grid[nx][ny] != '#' and fire_time[nx][ny] > t0 + 1:
                    fire_time[nx][ny] = t0 + 1
                    q.append((nx, ny))
    if fire_time[0][0] <= 0 or fire_time[n - 1][n - 1] <= 0:
        return -1
    visited = [[False] * n for _ in range(n)]
    visited[0][0] = True
    q = deque([(0, 0, 0)])
    while q:
        (x, y, t) = q.popleft()
        if x == n - 1 and y == n - 1:
            return t
        for (dx, dy) in ((1, 0), (-1, 0), (0, 1), (0, -1)):
            (nx, ny) = (x + dx, y + dy)
            nt = t + 1
            if 0 <= nx < n and 0 <= ny < n:
                if not visited[nx][ny] and grid[nx][ny] == '.' and (fire_time[nx][ny] > nt):
                    visited[nx][ny] = True
                    q.append((nx, ny, nt))
    return -1
