import itertools
import itertools
def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    n_trans = len(transactions)
    if n_trans == 0:
        return []
    tsets = [set(t) for t in transactions]
    item_counts = {}
    for t in tsets:
        for item in t:
            item_counts[item] = item_counts.get(item, 0) + 1
    L1 = []
    for (item, cnt) in item_counts.items():
        if cnt / n_trans >= min_sup:
            L1.append((item,))
    L1 = sorted(L1)
    frequent_itemsets = L1.copy()
    L_prev = L1
    k = 2
    while L_prev:
        Ck = []
        len_prev = len(L_prev)
        for i in range(len_prev):
            for j in range(i + 1, len_prev):
                a = L_prev[i]
                b = L_prev[j]
                if a[:k - 2] == b[:k - 2]:
                    cand = tuple(sorted(set(a) | set(b)))
                    if len(cand) == k:
                        Ck.append(cand)
        Ck = sorted(set(Ck))
        L_prev_set = set(L_prev)
        pruned = []
        for cand in Ck:
            all_subsets_frequent = True
            for subset in itertools.combinations(cand, k - 1):
                if subset not in L_prev_set:
                    all_subsets_frequent = False
                    break
            if all_subsets_frequent:
                pruned.append(cand)
        Ck = pruned
        counts = {cand: 0 for cand in Ck}
        for t in tsets:
            for cand in Ck:
                if set(cand) <= t:
                    counts[cand] += 1
        Lk = []
        for (cand, cnt) in counts.items():
            if cnt / n_trans >= min_sup:
                Lk.append(cand)
        if not Lk:
            break
        Lk = sorted(Lk)
        frequent_itemsets.extend(Lk)
        L_prev = Lk
        k += 1
    return frequent_itemsets
