import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function follows the specifications:
    - X: N×C list of vectors
    - Y: M×C list of vectors (if None, Y = X)
    - sigma: float (isotropic), list of C floats (anisotropic), or None
             (defaults to sqrt(C/2))
    Returns:
    - Nested Python list of shape N×M of kernel values rounded to 4 decimals
    - Or -1 on invalid input
    """
    if not isinstance(X, list) or len(X) == 0:
        return -1
    N = len(X)
    try:
        C = len(X[0])
    except Exception:
        return -1
    if C == 0:
        return -1
    for row in X:
        if not isinstance(row, (list, tuple)) or len(row) != C:
            return -1
    if Y is None:
        Y = X
    if not isinstance(Y, list) or len(Y) == 0:
        return -1
    M = len(Y)
    for row in Y:
        if not isinstance(row, (list, tuple)) or len(row) != C:
            return -1
    if sigma is None:
        sigma_arr = np.full((C,), np.sqrt(C / 2.0), dtype=float)
    elif isinstance(sigma, (int, float)):
        if sigma <= 0:
            return -1
        sigma_arr = np.full((C,), float(sigma), dtype=float)
    elif isinstance(sigma, (list, tuple, np.ndarray)):
        if len(sigma) != C:
            return -1
        try:
            sigma_arr = np.array(sigma, dtype=float)
        except Exception:
            return -1
        if np.any(sigma_arr <= 0):
            return -1
    else:
        return -1
    try:
        X_np = np.array(X, dtype=float)
        Y_np = np.array(Y, dtype=float)
    except Exception:
        return -1
    Xs = X_np / sigma_arr
    Ys = Y_np / sigma_arr
    x_norm2 = np.sum(Xs * Xs, axis=1)
    y_norm2 = np.sum(Ys * Ys, axis=1)
    cross_term = Xs.dot(Ys.T)
    d2 = x_norm2[:, None] + y_norm2[None, :] - 2.0 * cross_term
    d2 = np.maximum(d2, 0.0)
    K = np.exp(-0.5 * d2)
    K_round = np.round(K, 4)
    return K_round.tolist()
