import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    if epsilon <= 0.0 or n_samples <= 1:
        return []
    sample_size_eff = min(sample_size, n_samples)
    height_limit = math.ceil(math.log2(sample_size_eff)) if sample_size_eff > 0 else 0
    _gamma = 0.5772156649

    def c(n: int) -> float:
        if n <= 1:
            return 0.0
        return 2.0 * math.log(n - 1) + _gamma - 2.0 * (n - 1) / n

    def build_tree(X: np.ndarray, current_depth: int=0) -> dict:
        n = X.shape[0]
        if current_depth >= height_limit or n <= 1:
            return {'size': n}
        f = np.random.randint(0, n_features)
        col = X[:, f]
        (min_val, max_val) = (col.min(), col.max())
        if min_val == max_val:
            return {'size': n}
        thresh = np.random.uniform(min_val, max_val)
        left_mask = col < thresh
        X_left = X[left_mask]
        X_right = X[~left_mask]
        left = build_tree(X_left, current_depth + 1)
        right = build_tree(X_right, current_depth + 1)
        return {'feature': f, 'threshold': thresh, 'left': left, 'right': right}

    def path_length(x: np.ndarray, node: dict, current_depth: int=0) -> float:
        if 'size' in node:
            return current_depth + c(node['size'])
        f = node['feature']
        if x[f] < node['threshold']:
            return path_length(x, node['left'], current_depth + 1)
        else:
            return path_length(x, node['right'], current_depth + 1)
    trees = []
    for _ in range(n_trees):
        if sample_size_eff < n_samples:
            idx = np.random.choice(n_samples, size=sample_size_eff, replace=False)
            X_sub = data[idx]
        else:
            X_sub = data.copy()
        tree = build_tree(X_sub, current_depth=0)
        trees.append(tree)
    path_sums = np.zeros(n_samples, dtype=float)
    for t in trees:
        for i in range(n_samples):
            path_sums[i] += path_length(data[i], t, 0)
    h_bar = path_sums / n_trees
    if sample_size_eff > 1:
        phi = 2.0 * math.log(sample_size_eff - 1) - 2.0 * (sample_size_eff - 1) / sample_size_eff
    else:
        return []
    scores = 2.0 ** (-h_bar / phi)
    thresh = np.quantile(scores, 1.0 - epsilon)
    outlier_idx = np.where(scores >= thresh)[0]
    return sorted(outlier_idx.tolist())
