import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary ('identity', 'log', 'logit') where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions work with numpy scalars
        as well as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity_link(mu):
        return mu

    def identity_inv(eta):
        return eta

    def identity_derivative(mu):
        return np.ones_like(mu)

    def log_link(mu):
        return np.log(mu)

    def log_inv(eta):
        return np.exp(eta)

    def log_derivative(mu):
        return 1.0 / mu

    def logit_link(mu):
        return np.log(mu / (1.0 - mu))

    def logit_inv(eta):
        return 1.0 / (1.0 + np.exp(-eta))

    def logit_derivative(mu):
        return 1.0 / (mu * (1.0 - mu))
    return {'identity': {'link': identity_link, 'inv_link': identity_inv, 'derivative': identity_derivative}, 'log': {'link': log_link, 'inv_link': log_inv, 'derivative': log_derivative}, 'logit': {'link': logit_link, 'inv_link': logit_inv, 'derivative': logit_derivative}}

import numpy as np
def softplus(x, deriv: bool=False):
    """
    Compute the SoftPlus activation or its derivative (sigmoid) 
    in a numerically stable way.

    Parameters:
    - x: scalar, list, or NumPy array of real values.
    - deriv: bool, if False return SoftPlus(x), else return sigmoid(x).

    Returns:
    - Rounded result to 4 decimal places as a Python list,
      or a single float if input was scalar.
    """
    is_scalar = np.isscalar(x)
    arr = np.array(x, dtype=np.float64)
    if deriv:
        result = 1.0 / (1.0 + np.exp(-arr))
    else:
        result = np.where(arr > 0, arr + np.log1p(np.exp(-arr)), np.log1p(np.exp(arr)))
    result = np.round(result, 4)
    if is_scalar:
        return float(result)
    else:
        return result.tolist()

import numpy as np
from collections import Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*.

    * corpus: list of word tokens for training
    * sequence: list of word tokens whose probability we compute
    * N: order of the model (>=1)

    Returns:
      - total log-probability (natural log) rounded to 4 decimals
      - float('-inf') if any required n-gram count is zero
    """
    if N < 1:
        raise ValueError('N must be at least 1')
    if N == 1:
        uni_counts = Counter(corpus)
        total_tokens = len(corpus)
        log_prob = 0.0
        for w in sequence:
            cnt = uni_counts.get(w, 0)
            if cnt == 0:
                return float('-inf')
            log_prob += np.log(cnt / total_tokens)
        return round(log_prob, 4)
    ngram_counts = Counter((tuple(corpus[i:i + N]) for i in range(len(corpus) - N + 1)))
    prefix_counts = Counter((tuple(corpus[i:i + N - 1]) for i in range(len(corpus) - (N - 1) + 1)))
    log_prob = 0.0
    for i in range(len(sequence) - N + 1):
        ngram = tuple(sequence[i:i + N])
        prefix = tuple(sequence[i:i + N - 1])
        num = ngram_counts.get(ngram, 0)
        den = prefix_counts.get(prefix, 0)
        if num == 0 or den == 0:
            return float('-inf')
        log_prob += np.log(num / den)
    return round(log_prob, 4)

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    if not isinstance(observations, list) or len(observations) == 0:
        return -1
    if not isinstance(S, list):
        return -1
    n = len(S)
    if n == 0:
        return -1
    if not (isinstance(A, list) and len(A) == n):
        return -1
    for row in A:
        if not (isinstance(row, list) and len(row) == n):
            return -1
    if not (isinstance(B, list) and len(B) == n):
        return -1
    m = None
    for row in B:
        if not isinstance(row, list):
            return -1
        if m is None:
            m = len(row)
            if m == 0:
                return -1
        elif len(row) != m:
            return -1
    for o in observations:
        if not isinstance(o, int) or o < 0 or o >= m:
            return -1
    S_arr = np.array(S, dtype=float)
    A_arr = np.array(A, dtype=float)
    B_arr = np.array(B, dtype=float)
    alpha = S_arr * B_arr[:, observations[0]]
    for t in range(1, len(observations)):
        o_t = observations[t]
        alpha = alpha @ A_arr * B_arr[:, o_t]
    likelihood = float(np.sum(alpha))
    return round(likelihood, 4)

from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result is deterministic:
      • Inside each item-set the items are sorted alphabetically.
      • The outer list is sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold, returns [].
    """

    class TreeNode:

        def __init__(self, name, count, parent):
            self.name = name
            self.count = count
            self.parent = parent
            self.children = {}
            self.node_link = None

        def increment(self, count):
            self.count += count

    def update_tree(items, node, header_table, count):
        first = items[0]
        if first in node.children:
            node.children[first].increment(count)
        else:
            new_node = TreeNode(first, count, node)
            node.children[first] = new_node
            if header_table[first][1] is None:
                header_table[first][1] = new_node
            else:
                current = header_table[first][1]
                while current.node_link:
                    current = current.node_link
                current.node_link = new_node
        if len(items) > 1:
            update_tree(items[1:], node.children[first], header_table, count)

    def create_initial_tree(transactions, min_support):
        item_counts = Counter()
        for trans in transactions:
            item_counts.update(trans)
        header_table = {item: [cnt, None] for (item, cnt) in item_counts.items() if cnt >= min_support}
        if not header_table:
            return (None, None)
        root = TreeNode(None, 1, None)
        freq_items = set(header_table.keys())
        for trans in transactions:
            filtered = [i for i in trans if i in freq_items]
            if not filtered:
                continue
            filtered.sort(key=lambda i: (-item_counts[i], i))
            update_tree(filtered, root, header_table, 1)
        return (root, header_table)

    def find_prefix_paths(base_item, node):
        cond_patterns = {}
        while node:
            prefix = []
            parent = node.parent
            while parent and parent.name is not None:
                prefix.append(parent.name)
                parent = parent.parent
            if prefix:
                key = tuple(prefix[::-1])
                cond_patterns[key] = cond_patterns.get(key, 0) + node.count
            node = node.node_link
        return cond_patterns

    def mine_tree(header_table, min_support, prefix, freq_itemsets):
        for item in sorted(header_table.keys()):
            new_prefix = prefix + [item]
            freq_itemsets.append(new_prefix)
            cond_base = find_prefix_paths(item, header_table[item][1])
            cond_header = {}
            for (path, cnt) in cond_base.items():
                for itm in path:
                    cond_header[itm] = cond_header.get(itm, 0) + cnt
            cond_header = {itm: c for (itm, c) in cond_header.items() if c >= min_support}
            if not cond_header:
                continue
            for itm in cond_header:
                cond_header[itm] = [cond_header[itm], None]
            cond_root = TreeNode(None, 1, None)
            freq_items = set(cond_header.keys())
            for (path, cnt) in cond_base.items():
                filtered = [itm for itm in path if itm in freq_items]
                if not filtered:
                    continue
                filtered.sort(key=lambda i: (-cond_header[i][0], i))
                update_tree(filtered, cond_root, cond_header, cnt)
            mine_tree(cond_header, min_support, new_prefix, freq_itemsets)
    if not transactions or min_support < 1:
        return []
    (root, header) = create_initial_tree(transactions, min_support)
    if header is None:
        return []
    all_freq_itemsets = []
    mine_tree(header, min_support, [], all_freq_itemsets)
    result = [sorted(itemset) for itemset in all_freq_itemsets]
    result.sort(key=lambda x: (len(x), x))
    return result

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    class Node:

        def __init__(self, *, predicted_class=None, feature_index=None, threshold=None, left=None, right=None):
            self.predicted_class = predicted_class
            self.feature_index = feature_index
            self.threshold = threshold
            self.left = left
            self.right = right

        def is_leaf(self):
            return self.left is None and self.right is None

        def predict_one(self, x):
            if self.is_leaf():
                return self.predicted_class
            if x[self.feature_index] <= self.threshold:
                return self.left.predict_one(x)
            else:
                return self.right.predict_one(x)

    def gini(y):
        """Compute Gini impurity for label array y."""
        if y.size == 0:
            return 0.0
        (_, counts) = np.unique(y, return_counts=True)
        probs = counts / counts.sum()
        return 1.0 - np.sum(probs ** 2)

    def majority_class(y):
        """Return the majority class label in y (smallest label on ties)."""
        if y.size == 0:
            return 0
        counts = np.bincount(y)
        return int(np.argmax(counts))

    def build_tree(X, y, depth):
        """Recursively build the CART tree."""
        if np.unique(y).size == 1:
            return Node(predicted_class=int(y[0]))
        if max_depth is not None and depth >= max_depth:
            return Node(predicted_class=majority_class(y))
        (n_samples, n_features) = X.shape
        parent_gini = gini(y)
        best_gain = 0.0
        best_idx = None
        best_thr = None
        best_splits = None
        for feature_idx in range(n_features):
            xi = X[:, feature_idx]
            sorted_idx = np.argsort(xi)
            (xi_sorted, y_sorted) = (xi[sorted_idx], y[sorted_idx])
            unique_vals = np.unique(xi_sorted)
            if unique_vals.size <= 1:
                continue
            thresholds = (unique_vals[:-1] + unique_vals[1:]) / 2.0
            for thr in thresholds:
                left_mask = xi_sorted <= thr
                y_left = y_sorted[left_mask]
                y_right = y_sorted[~left_mask]
                if y_left.size == 0 or y_right.size == 0:
                    continue
                g_left = gini(y_left)
                g_right = gini(y_right)
                w_left = y_left.size / n_samples
                w_right = y_right.size / n_samples
                gain = parent_gini - (w_left * g_left + w_right * g_right)
                if gain > best_gain:
                    best_gain = gain
                    best_idx = feature_idx
                    best_thr = thr
                    best_splits = {'left_X': X[xi <= thr], 'left_y': y[xi <= thr], 'right_X': X[xi > thr], 'right_y': y[xi > thr]}
        if best_gain == 0 or best_splits is None:
            return Node(predicted_class=majority_class(y))
        left_child = build_tree(best_splits['left_X'], best_splits['left_y'], depth + 1)
        right_child = build_tree(best_splits['right_X'], best_splits['right_y'], depth + 1)
        return Node(predicted_class=None, feature_index=best_idx, threshold=best_thr, left=left_child, right=right_child)
    root = build_tree(X_train, y_train, depth=0)
    preds = [root.predict_one(x) for x in X_test]
    return preds

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported = {'euclidean', 'manhattan', 'chebyshev', 'cosine'}

    def d(a, b):
        if name not in supported:
            return -1
        try:
            x = np.array(a, dtype=float)
            y = np.array(b, dtype=float)
        except Exception:
            return -1
        if x.ndim != 1 or y.ndim != 1:
            return -1
        if x.size == 0 or y.size == 0 or x.shape != y.shape:
            return -1
        if name == 'euclidean':
            val = np.linalg.norm(x - y)
        elif name == 'manhattan':
            val = np.sum(np.abs(x - y))
        elif name == 'chebyshev':
            val = np.max(np.abs(x - y))
        else:
            nx = np.linalg.norm(x)
            ny = np.linalg.norm(y)
            if nx == 0 or ny == 0:
                return -1
            val = 1.0 - np.dot(x, y) / (nx * ny)
        return float(np.round(val, 4))
    return d

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1.0, 0.0, 1.0 or 1.5.
    """

    def hand_value(cards: list[int]) -> int:
        total = sum(cards)
        if 1 in cards and total + 10 <= 21:
            return total + 10
        return total

    def is_natural(cards: list[int]) -> bool:
        return len(cards) == 2 and 1 in cards and (10 in cards)
    p_val = hand_value(player)
    d_val = hand_value(dealer)
    if p_val > 21:
        raw = -1
    elif d_val > 21:
        raw = 1
    elif p_val > d_val:
        raw = 1
    elif p_val < d_val:
        raw = -1
    else:
        raw = 0
    if raw == 1 and is_natural(player):
        return 1.5
    return float(raw)

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        return np.allclose(tree_a.value, tree_b.value, atol=1e-08, rtol=0)
    if isinstance(tree_a, Node) and isinstance(tree_b, Node):
        if tree_a.feature != tree_b.feature:
            return False
        if not np.isclose(tree_a.threshold, tree_b.threshold, atol=1e-08, rtol=0):
            return False
        return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)
    return False

from typing import Any, Dict, List, Tuple
def to_networkx(G: Dict[str, Any]) -> Dict[Any, List[Tuple[Any, float]]]:
    """Convert a custom graph dictionary into a plain adjacency dictionary.

    Parameters
    ----------
    G : dict
        A graph represented as a dictionary with the keys:
          * 'is_directed' (bool)
          * '_V2I'        (dict mapping vertex label -> index)
          * '_G'          (list of adjacency lists, each edge is a
                           tuple (source_label, target_label, weight))

    Returns
    -------
    dict
        A dictionary where each key is a vertex label and the value is a list
        of `(neighbour, weight)` tuples.  For undirected graphs every edge
        appears once in each direction and the neighbour lists are sorted
        alphabetically.
    """
    is_directed = G.get('is_directed', True)
    v2i = G.get('_V2I', {})
    raw_adj = G.get('_G', [])
    if not v2i:
        return {}
    adj: Dict[Any, List[Tuple[Any, float]]] = {v: [] for v in v2i.keys()}
    if is_directed:
        for edge_list in raw_adj:
            for (src, tgt, w) in edge_list:
                adj[src].append((tgt, w))
    else:
        added: set = set()
        for edge_list in raw_adj:
            for (src, tgt, w) in edge_list:
                if (src, tgt, w) not in added:
                    adj[src].append((tgt, w))
                    added.add((src, tgt, w))
                if (tgt, src, w) not in added:
                    adj[tgt].append((src, w))
                    added.add((tgt, src, w))
    for u in adj:
        adj[u].sort(key=lambda pair: pair[0])
    return adj

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries.

    Parameters
    ----------
    transactions : list[list[str]]
        A list where each element is a list of items representing one transaction.
    min_support : int
        Minimum number of transactions an item has to appear in to be kept.

    Returns
    -------
    dict
        The root node of the FP-Tree.  Each node contains two keys:
        "support" and "children" (the latter mapping item → child-node).
    """
    item_counts = Counter()
    for txn in transactions:
        for item in set(txn):
            item_counts[item] += 1
    frequent_items = {item: cnt for (item, cnt) in item_counts.items() if cnt >= min_support}
    if not frequent_items:
        return {'support': 0, 'children': {}}
    sorted_items = sorted(frequent_items.items(), key=lambda x: (-x[1], x[0]))
    item_order = {item: idx for (idx, (item, _)) in enumerate(sorted_items)}
    root = {'support': 0, 'children': {}}
    for txn in transactions:
        filtered = [it for it in set(txn) if it in item_order]
        ordered = sorted(filtered, key=lambda it: item_order[it])
        node = root
        node['support'] += 1
        for item in ordered:
            if item not in node['children']:
                node['children'][item] = {'support': 0, 'children': {}}
            node = node['children'][item]
            node['support'] += 1
    return root

import numpy as np
def linear_kernel(X: list[list[int | float]] | 'np.ndarray', Y: list[list[int | float]] | 'np.ndarray' | None=None, c0: int | float=0) -> list[list[float]] | int:
    """Return the linear kernel matrix k(x, y) = x·y + c0.

    Parameters
    ----------
    X : 2-D list or NumPy array with shape (N, C)
        Collection of N input vectors.
    Y : 2-D list or NumPy array with shape (M, C) or None, optional
        Collection of M input vectors.  If None, the function must set
        ``Y = X``.  Default is None.
    c0 : int or float, optional
        Additive constant of the kernel.  Default is 0.

    Returns
    -------
    list[list[float]] | int
        The (N, M) kernel matrix where entry (i, j) equals
        X[i]·Y[j] + c0, rounded to 4 decimals.  Return -1 when X and Y have
        incompatible shapes (different number of columns).
    """
    if isinstance(X, list):
        if len(X) == 0:
            return []
        X_arr = np.asarray(X, dtype=float)
    elif isinstance(X, np.ndarray):
        X_arr = X.astype(float, copy=False)
    else:
        return -1
    if X_arr.ndim != 2:
        return -1
    if Y is None:
        Y_arr = X_arr
    else:
        if isinstance(Y, list):
            if len(Y) == 0:
                Y_arr = np.zeros((0, X_arr.shape[1]), dtype=float)
            else:
                Y_arr = np.asarray(Y, dtype=float)
        elif isinstance(Y, np.ndarray):
            Y_arr = Y.astype(float, copy=False)
        else:
            return -1
        if Y_arr.ndim != 2:
            return -1
    (N, Cx) = X_arr.shape
    (M, Cy) = Y_arr.shape
    if Cx != Cy:
        return -1
    K = X_arr.dot(Y_arr.T) + float(c0)
    K_rounded = np.round(K, 4)
    return K_rounded.tolist()

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def _check(space: Space, is_tuple: bool) -> bool:
        if not is_tuple:
            return isinstance(space, Box)
        if isinstance(space, Tuple):
            return all((isinstance(s, Box) for s in space.spaces))
        if isinstance(space, Dict):
            return all((isinstance(s, Box) for s in space.spaces.values()))
        return False
    cont_action = _check(env.action_space, tuple_action)
    cont_obs = _check(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function returns the MAP predictive means for every row in
    `X_new`.  See the task description for full details.
    """
    if fit_intercept:
        X = np.hstack((np.ones((X.shape[0], 1)), X))
        X_new = np.hstack((np.ones((X_new.shape[0], 1)), X_new))
    (N, p) = X.shape
    if np.isscalar(mu):
        mu_vec = np.full(p, mu, dtype=float)
    else:
        mu_vec = np.array(mu, dtype=float).ravel()
        if mu_vec.shape[0] != p:
            raise ValueError(f'mu has length {mu_vec.shape[0]}, expected {p}')
    if V is None:
        V_mat = np.eye(p)
    elif np.isscalar(V):
        V_mat = float(V) * np.eye(p)
    else:
        V_arr = np.array(V, dtype=float)
        if V_arr.ndim == 1:
            if V_arr.shape[0] != p:
                raise ValueError(f'V diag has length {V_arr.shape[0]}, expected {p}')
            V_mat = np.diag(V_arr)
        elif V_arr.ndim == 2:
            if V_arr.shape != (p, p):
                raise ValueError(f'V matrix has shape {V_arr.shape}, expected ({p},{p})')
            V_mat = V_arr
        else:
            raise ValueError('V must be scalar, 1-D or 2-D array')
    V_inv = np.linalg.inv(V_mat)
    XtX = X.T @ X
    Sigma_p = np.linalg.inv(V_inv + XtX)
    rhs = V_inv @ mu_vec + X.T @ y
    mu_p = Sigma_p @ rhs
    y_pred = X_new @ mu_p
    return [round(float(val), 4) for val in y_pred]

import numpy as np
def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Compute the Root Mean Squared Logarithmic Error (RMSLE).
       Returns -1 if inputs are invalid (unequal lengths, empty, or contain negative values)."""
    if len(actual) != len(predicted) or len(actual) == 0:
        return -1
    for v in actual + predicted:
        if not isinstance(v, (int, float)) or v < 0:
            return -1
    actual_arr = np.array(actual, dtype=float)
    predicted_arr = np.array(predicted, dtype=float)
    log_actual = np.log1p(actual_arr)
    log_pred = np.log1p(predicted_arr)
    msle = np.mean((log_pred - log_actual) ** 2)
    rmsle = np.sqrt(msle)
    return round(rmsle, 4)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            list/array input returns a Python list preserving the shape.
            If order is not 0, 1, or 2, returns -1.
    """
    if order not in (0, 1, 2):
        return -1
    is_scalar = np.isscalar(x)
    arr = np.array(x, dtype=float)
    sig = 1.0 / (1.0 + np.exp(-arr))
    if order == 0:
        result = sig
    elif order == 1:
        result = sig * (1.0 - sig)
    else:
        result = sig * (1.0 - sig) * (1.0 - 2.0 * sig)
    result = np.round(result, 4)
    if is_scalar:
        return float(result)
    else:
        return result.tolist()

import itertools
import math
from collections import Counter
from typing import Hashable, List, Tuple
def apriori_frequent_itemsets(transactions: List[List[Hashable]], min_sup: float) -> List[Tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    if not 0 < min_sup <= 1:
        raise ValueError('min_sup must be in (0, 1].')
    txn_list = [set(t) for t in transactions]
    num_txns = len(txn_list)
    if num_txns == 0:
        return []
    min_count = math.ceil(min_sup * num_txns)
    item_counter = Counter()
    for txn in txn_list:
        for item in txn:
            item_counter[item] += 1
    L1 = []
    for (item, cnt) in item_counter.items():
        if cnt >= min_count:
            L1.append((item,))
    L1 = sorted(L1)
    freq_itemsets: List[Tuple] = []
    freq_itemsets.extend(L1)
    Lk_sets = {frozenset([item]) for (item,) in L1}
    k = 2
    while True:
        candidates_k = set()
        Lk_list = list(Lk_sets)
        for i in range(len(Lk_list)):
            for j in range(i + 1, len(Lk_list)):
                c = Lk_list[i] | Lk_list[j]
                if len(c) == k:
                    candidates_k.add(c)
        pruned_candidates = set()
        prev_Lk = Lk_sets
        for cand in candidates_k:
            all_subsets_frequent = True
            for subset in itertools.combinations(cand, k - 1):
                if frozenset(subset) not in prev_Lk:
                    all_subsets_frequent = False
                    break
            if all_subsets_frequent:
                pruned_candidates.add(cand)
        cand_count = Counter()
        for txn in txn_list:
            for cand in pruned_candidates:
                if cand.issubset(txn):
                    cand_count[cand] += 1
        Lk_sets = {c for (c, cnt) in cand_count.items() if cnt >= min_count}
        if not Lk_sets:
            break
        Lk_tuples = [tuple(sorted(c)) for c in Lk_sets]
        Lk_tuples.sort()
        freq_itemsets.extend(Lk_tuples)
        k += 1
    return freq_itemsets

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]] | int:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    Given:
      X: N × C list of vectors
      Y: M × C list of vectors (if None, Y = X)
      sigma: bandwidth parameter
         - single positive float (isotropic)
         - list/array of length C of positive floats (anisotropic)
         - None ⇒ default sigma_j = sqrt(C/2)

    Returns:
      -1 on invalid input, else returns the N×M kernel matrix as a nested list
      with entries rounded to 4 decimal places.
    """
    try:
        X_arr = np.array(X, dtype=float)
    except Exception:
        return -1
    if X_arr.ndim != 2:
        return -1
    (N, C) = X_arr.shape
    if Y is None:
        Y_arr = X_arr
    else:
        try:
            Y_arr = np.array(Y, dtype=float)
        except Exception:
            return -1
        if Y_arr.ndim != 2 or Y_arr.shape[1] != C:
            return -1
    M = Y_arr.shape[0]
    if sigma is None:
        sigma_arr = np.sqrt(C / 2.0)
    elif isinstance(sigma, (int, float)):
        sigma_arr = float(sigma)
        if sigma_arr <= 0:
            return -1
    elif isinstance(sigma, (list, tuple, np.ndarray)):
        if len(sigma) != C:
            return -1
        sigma_arr = np.array(sigma, dtype=float)
        if sigma_arr.ndim != 1 or np.any(sigma_arr <= 0):
            return -1
    else:
        return -1
    if np.isscalar(sigma_arr):
        Xs = X_arr / sigma_arr
        Ys = Y_arr / sigma_arr
    else:
        Xs = X_arr / sigma_arr[np.newaxis, :]
        Ys = Y_arr / sigma_arr[np.newaxis, :]
    X_sq = np.sum(Xs * Xs, axis=1).reshape(N, 1)
    Y_sq = np.sum(Ys * Ys, axis=1).reshape(1, M)
    D2 = X_sq + Y_sq - 2 * Xs.dot(Ys.T)
    D2 = np.maximum(D2, 0.0)
    K = np.exp(-0.5 * D2)
    K_rounded = np.round(K, 4)
    return K_rounded.tolist()

import numpy as np
from typing import List
from numbers import Number
def divide_on_feature(X: np.ndarray | List[list], feature_i: int, threshold) -> List[list]:
    """Split the data set *X* into two subsets using a given feature column and threshold.

    The function must create two disjoint subsets:
    1. For a numeric threshold (int or float) the first subset contains every
       sample whose value in column *feature_i* is **greater than or equal** to
       the threshold.
    2. For any other type of threshold the first subset contains every sample
       whose value in column *feature_i* is **exactly equal** to the threshold.

    The second subset always contains the remaining samples.  Both subsets must
    keep the original order of appearance in *X*.

    Args:
        X: 2-D iterable (list or ndarray) where each element is a sample.
        feature_i: Index of the feature column used for the split.
        threshold: Value that determines how the split is performed.

    Returns:
        A list of length two.  *result[0]* is the first subset, *result[1]* is
        the second subset.  Each subset is a list of lists.
    """
    is_numeric_threshold = isinstance(threshold, Number)
    left_rows = []
    right_rows = []
    for sample in X:
        try:
            value = sample[feature_i]
        except Exception as e:
            raise IndexError(f'Cannot access feature index {feature_i} in sample {sample}') from e
        if is_numeric_threshold:
            if value >= threshold:
                left_rows.append(sample)
            else:
                right_rows.append(sample)
        elif value == threshold:
            left_rows.append(sample)
        else:
            right_rows.append(sample)

    def _to_list(row):
        if isinstance(row, np.ndarray):
            return row.tolist()
        else:
            return list(row)
    left_list = [_to_list(row) for row in left_rows]
    right_list = [_to_list(row) for row in right_rows]
    return [left_list, right_list]

