import numpy as np
def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict."""
    X_train = np.array(X_train, dtype=float)
    y_train = np.array(y_train, dtype=float)
    if X_train.size == 0:
        return []

    def build_tree(X, y, depth):

        def make_leaf(vals):
            return {'is_leaf': True, 'value': float(np.mean(vals))}
        if depth >= max_depth or X.size < 2:
            return make_leaf(y)
        order = np.argsort(X)
        Xs = X[order]
        ys = y[order]
        mids = (Xs[:-1] + Xs[1:]) / 2.0
        distinct = Xs[:-1] != Xs[1:]
        if not np.any(distinct):
            return make_leaf(y)
        best_sse = np.inf
        best_split = None
        for split in mids[distinct]:
            left_mask = X <= split
            yL = y[left_mask]
            yR = y[~left_mask]
            if yL.size == 0 or yR.size == 0:
                continue
            sseL = np.sum((yL - yL.mean()) ** 2)
            sseR = np.sum((yR - yR.mean()) ** 2)
            sse = sseL + sseR
            if sse < best_sse:
                best_sse = sse
                best_split = split
        if best_split is None:
            return make_leaf(y)
        left_mask = X <= best_split
        left_node = build_tree(X[left_mask], y[left_mask], depth + 1)
        right_node = build_tree(X[~left_mask], y[~left_mask], depth + 1)
        return {'is_leaf': False, 'split': best_split, 'left': left_node, 'right': right_node}
    tree = build_tree(X_train, y_train, depth=0)

    def predict_one(x):
        node = tree
        while not node['is_leaf']:
            if x <= node['split']:
                node = node['left']
            else:
                node = node['right']
        return node['value']
    return [predict_one(float(x)) for x in X_test]

import numpy as np
import numbers
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise, rounded to 4 decimals.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def _process_list(lst):
        """Recursively process a nested list of numbers."""
        result = []
        for elem in lst:
            if isinstance(elem, list):
                result.append(_process_list(elem))
            else:
                val = float(elem)
                grad = np.round(val * (1.0 - val), 4)
                result.append(float(grad))
        return result
    if isinstance(z, np.ndarray):
        if z.ndim == 0:
            val = float(z)
            return float(np.round(val * (1.0 - val), 4))
        py_list = z.tolist()
        return _process_list(py_list)
    if isinstance(z, list):
        if len(z) == 0:
            return []
        return _process_list(z)
    if isinstance(z, numbers.Number):
        val = float(z)
        return float(np.round(val * (1.0 - val), 4))
    raise TypeError(f'Unsupported input type: {type(z)}')

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    node = root
    while not hasattr(node, 'y'):
        feat_val = features[node.label]
        if task_type == 'classification':
            next_child = None
            for child in node.child:
                if child.x == feat_val:
                    next_child = child
                    break
            if next_child is None:
                next_child = node.child[1]
            node = next_child
        elif task_type == 'regression':
            if feat_val <= node.s:
                node = node.child[0]
            else:
                node = node.child[1]
        else:
            raise ValueError(f'Unknown task_type: {task_type}')
    return node.y

import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    if epsilon <= 0:
        return []

    def c(n: int) -> float:
        if n <= 1:
            return 0.0
        return 2.0 * math.log(n - 1) + 0.5772156649 - 2.0 * (n - 1) / n
    height_limit = math.ceil(math.log2(sample_size)) if sample_size > 0 else 0

    def build_tree(idxs: np.ndarray, current_depth: int):
        size = idxs.size
        if current_depth >= height_limit or size <= 1:
            return {'leaf': True, 'size': size}
        f = np.random.randint(0, n_features)
        feat_vals = data[idxs, f]
        (lo, hi) = (feat_vals.min(), feat_vals.max())
        if lo == hi:
            return {'leaf': True, 'size': size}
        t = np.random.uniform(lo, hi)
        left_mask = feat_vals < t
        right_mask = ~left_mask
        if not np.any(left_mask) or not np.any(right_mask):
            return {'leaf': True, 'size': size}
        left_idxs = idxs[left_mask]
        right_idxs = idxs[right_mask]
        left = build_tree(left_idxs, current_depth + 1)
        right = build_tree(right_idxs, current_depth + 1)
        return {'leaf': False, 'feature': f, 'threshold': t, 'left': left, 'right': right, 'size': size}

    def path_length(x: np.ndarray, node: dict, depth: int) -> float:
        if node['leaf']:
            return depth + c(node['size'])
        if x[node['feature']] < node['threshold']:
            return path_length(x, node['left'], depth + 1)
        else:
            return path_length(x, node['right'], depth + 1)
    trees = []
    all_indices = np.arange(n_samples)
    for _ in range(n_trees):
        if sample_size >= n_samples:
            samp_idxs = all_indices.copy()
        else:
            samp_idxs = np.random.choice(all_indices, size=sample_size, replace=False)
        tree = build_tree(samp_idxs, 0)
        trees.append(tree)
    avg_path_lengths = np.zeros(n_samples, dtype=float)
    for i in range(n_samples):
        x = data[i]
        pl_sum = 0.0
        for tree in trees:
            pl_sum += path_length(x, tree, 0)
        avg_path_lengths[i] = pl_sum / n_trees
    if n_samples > 1:
        phi = 2.0 * math.log(n_samples - 1) - 2.0 * (n_samples - 1) / n_samples
    else:
        phi = 1.0
    scores = 2.0 ** (-(avg_path_lengths / phi))
    k = int(math.floor(epsilon * n_samples))
    if k <= 0:
        return []
    topk_desc = np.argsort(-scores)[:k]
    outliers = sorted(topk_desc.tolist())
    return outliers

from collections import Counter
from typing import Iterable, Tuple, List, Set, Any
def statistic_key_word(data: List[Tuple[Any, Iterable[str]]], cut_off: int | None=None) -> Tuple[List[Tuple[str, float]], Set[str]]:
    """Calculate how often each word appears across documents.

    The function returns a list with the document-frequency ratio of every word
    (sorted from highest to lowest) and a set containing only the *cut_off*
    most frequent words. When *cut_off* is ``None`` all words are placed in the
    set. Every ratio is rounded to four decimal places.

    Args:
        data: Iterable of documents. Each document is represented by a tuple
              ``(label, words)`` where *label* is ignored and *words* is an
              iterable of strings.
        cut_off: Number of top words to include in the returned set, or
                  ``None`` to include every word.

    Returns:
        Tuple with (1) a list of (word, ratio) pairs and (2) a set of the most
        frequent words as described above.
    """
    total_docs = len(data)
    if total_docs == 0:
        return ([], set())
    doc_counter: Counter[str] = Counter()
    for (_, words) in data:
        unique_words = set(words)
        doc_counter.update(unique_words)
    ratios = {word: round(count / total_docs, 4) for (word, count) in doc_counter.items()}
    sorted_word_ratios: List[Tuple[str, float]] = sorted(ratios.items(), key=lambda item: item[1], reverse=True)
    if cut_off is None:
        top_words = {word for (word, _) in sorted_word_ratios}
    else:
        top_words = {word for (word, _) in sorted_word_ratios[:cut_off]}
    return (sorted_word_ratios, top_words)
