import numpy as np
def softplus(x, deriv: bool=False):
    if isinstance(x, (int, float)):
        return np.log1p(x)
    if not isinstance(x, (list, np.ndarray)):
        raise TypeError('Input must be a scalar, list, or NumPy array.')
    if isinstance(x, list):
        return np.piecewise(x < -10, [x, x * 1 / (1 + np.exp(-x))], [x * 1 / (1 + np.exp(-x)), 0])
    if isinstance(x, np.ndarray):
        return np.piecewise(x < -10, [x, x * 1 / (1 + np.exp(-x))], [x * 1 / (1 + np.exp(-x)), 0])
    raise ValueError('Input must be a scalar, list, or NumPy array.')
