def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    if not transactions:
        return []
    item_counts = Counter()
    for transaction in transactions:
        for item in transaction:
            item_counts[item] += 1

    def build_prefix_tree(items):
        root = {}
        for item in items:
            current = root
            for char in item:
                if char not in current:
                    current[char] = {}
                current = current[char]
            current['support'] = item_counts[item]
        return root

    def mine_conditional_frequent_trees(root, current_support, current_set):
        if current_support < min_support:
            return
        if len(current_set) >= len(frequent_itemsets):
            frequent_itemsets.append(current_set)
            return
        for next_char in current_set:
            if next_char in root:
                mine_conditional_frequent_trees(root[next_char], current_support + 1, current_set + [next_char])
    root = build_prefix_tree(transactions[0])
    mine_conditional_frequent_trees(root, 0, [])
    return frequent_itemsets
