def to_networkx(G: Dict[str, Any]) -> Dict[Any, List[Tuple[Any, float]]]:
    if not G:
        return {}
    is_directed = G.get('is_directed', False)
    unique_vertices = set(G['_V2I'].keys())
    adj = {vertex: [] for vertex in unique_vertices}
    for edge in G['_G']:
        (source_label, target_label, weight) = edge
        if source_label not in unique_vertices or target_label not in unique_vertices:
            raise ValueError('Invalid edge format')
        if (source_label, target_label) not in adj[source_label]:
            adj[source_label].append((target_label, weight))
    sorted_adj = {vertex: sorted(adj[vertex]) for vertex in unique_vertices}
    return sorted_adj
