from typing import List
def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:

    def find_frequent_itemsets(frequent_items: set, transactions: List[List[int]], min_sup: float) -> List[tuple]:
        result = []
        for transaction in transactions:
            if len(transaction) == 0:
                continue
            for item in frequent_items:
                if item in transaction:
                    continue
                support = transaction.count(item)
                if support >= min_sup:
                    result.append((item, support))
        return result
    items = set()
    for transaction in transactions:
        for item in transaction:
            items.add(item)
    frequent_items = set(items)
    frequent_itemsets = find_frequent_itemsets(frequent_items, transactions, min_sup)
    for k in range(2, len(items) + 1):
        frequent_items = set()
        for transaction in transactions:
            for i in range(len(transaction)):
                for j in range(i + 1, len(transaction)):
                    item1 = transaction[i]
                    item2 = transaction[j]
                    support1 = transaction.count(item1)
                    support2 = transaction.count(item2)
                    if support1 >= min_sup and support2 >= min_sup:
                        frequent_items.add((item1, item2))
        frequent_itemsets.extend(find_frequent_itemsets(frequent_items, transactions, min_sup))
    frequent_itemsets.sort(key=lambda x: (-len(x), x[0]))
    return frequent_itemsets
