import numpy as np
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:
    if data.shape[0] == 0:
        return []
    num_samples = data.shape[0]
    cluster_labels = [-1] * num_samples
    cluster_size = [1] * num_samples

    def get_neighbors(point):
        neighbors = []
        for i in range(num_samples):
            if i != point:
                distance = np.linalg.norm(data[i] - data[point])
                if distance <= eps:
                    neighbors.append(i)
        return neighbors
    for i in range(num_samples):
        if len(get_neighbors(i)) >= min_samples:
            cluster_labels[i] = len(cluster_labels)
            cluster_size[i] += 1
    core_points = [i for (i, size) in enumerate(cluster_size) if size == len(cluster_labels)]
    for point in core_points:
        for neighbor in get_neighbors(point):
            if cluster_labels[neighbor] == -1:
                cluster_labels[neighbor] = cluster_labels[point]
                cluster_size[neighbor] += 1
    for i in range(num_samples):
        if cluster_labels[i] == -1:
            cluster_labels[i] = -1
    return cluster_labels
