import numpy as np
def kmeans(data: np.ndarray, k: int, epsilon: float=0.001, max_iter: int=2000) -> tuple[list[list[float]], list[int]]:
    if k <= 1:
        raise ValueError('Number of clusters must be at least 2.')
    centroids = data[:k]
    assignments = np.argmin(np.square(data - centroids), axis=1)
    while True:
        new_centroids = []
        for i in range(k):
            closest_centroid = np.argmin(np.square(data[i] - centroids))
            new_centroids.append(data[i])
            assignments[closest_centroid] = i
        if np.allclose(new_centroids, centroids):
            break
        centroids = new_centroids
    return (centroids, assignments)
