def build_tree(items: list[str], counts: list[int], parents: list[int]) -> dict:
    if not items or not counts or (not parents):
        return {}
    tree = {}
    for (i, parent) in enumerate(parents):
        if parent == -1:
            continue
        child = {}
        child['item'] = items[i]
        child['count'] = counts[i]
        if len(tree) == 0:
            tree[parent] = child
        else:
            tree[parent]['children'].append(child)
    return tree
