import numpy as np
def random_binary_tensor(shape: tuple[int, ...], sparsity: float=0.5, seed: int | None=None):
    """
    Generate a NumPy tensor filled with 0.0s and 1.0s.

    Parameters:
    shape (tuple[int, ...]): The shape of the tensor to generate.
    sparsity (float): The probability of generating a 1.0.
    seed (int | None): A seed for reproducibility.

    Returns:
    numpy.ndarray: A NumPy array containing only the float values 0.0 and 1.0.
    """
    if sparsity < 0 or sparsity > 1:
        return -1
    if seed is not None:
        np.random.seed(seed)
    tensor = np.zeros(shape)
    for i in range(tensor.shape[0]):
        for j in range(tensor.shape[1]):
            if np.random.uniform() < sparsity:
                tensor[i, j] = 1.0
            else:
                tensor[i, j] = 0.0
    return tensor

from typing import List, Optional
class Node:
    """Binary decision-tree node (provided).

    Non-leaf node:   rule = (feature_index, threshold)
    Leaf node:       rule = (None, prediction_value)
    """

    def __init__(self, left: Optional['Node'], right: Optional['Node'], rule: tuple):
        self.left = left
        self.right = right
        self.feature = rule[0]
        self.threshold = rule[1]
def predict_tree(root: Optional[Node], X: List[List[float]]) -> List[float]:
    """Predicts the class labels or regression values for each sample in the input dataset."""
    predictions = []
    if root is None:
        return predictions
    current_node = root
    while current_node is not None:
        if current_node.left is None and current_node.right is None:
            predictions.append(current_node.threshold)
        elif X[current_node.feature] <= current_node.threshold:
            current_node = current_node.left
        else:
            current_node = current_node.right
    return predictions

import numpy as np
def tanh_activation(x, derivative: bool=False) -> list[float]:
    """Compute tanh or its derivative element-wise.

    Args:
        x: A 1-D list or NumPy array of numbers.
        derivative: If True, return the gradient of tanh. Otherwise return tanh.

    Returns:
        Python list with each element rounded to 4 decimals.
    """
    if not isinstance(x, (list, np.ndarray)):
        raise TypeError('Input must be a list or NumPy array.')
    tanh_values = np.tanh(x)
    if derivative:
        return tanh_values * (1 - tanh_values ** 2)
    return tanh_values.tolist()

