import numpy as np
def compute_impurity(y, criterion):
    """Compute a node's impurity.

    Args:
        y (numpy.ndarray): 1-D array with the target values.
        criterion (str): One of {"entropy", "gini", "mse"}.

    Returns:
        float: Impurity value rounded to 4 decimal places.
    """
    if len(y) == 0:
        return 0.0
    majority_class = np.argmax(np.bincount(y))
    counts = np.bincount(y, weights=y == majority_class)
    if criterion == 'entropy':
        entropy = -np.sum(counts * np.log(counts))
    elif criterion == 'gini':
        gini = 1 - np.sum(counts * np.power(counts, -1))
    elif criterion == 'mse':
        mse = np.mean((y - majority_class) ** 2)
    else:
        raise ValueError('Invalid criterion')
    return entropy + gini * counts[0] / len(y)

import numpy as np
def backward_prob(A: list[list[float]], B: list[list[float]], pi: list[float], obs: list[int]) -> float:
    if not A or not B or (not pi) or (not obs):
        return 0.0
    N = len(pi)
    beta = np.zeros((N, len(obs)))
    beta[0, :] = pi
    for t in range(1, len(obs)):
        for i in range(N):
            for j in range(N):
                beta[t, i] += A[i][j] * B[j, obs[t - 1]] * beta[t - 1, j]
    beta[-1, :] += pi * B[-1, obs[-1]]
    total_probability = np.sum(beta[-1, :])
    return round(total_probability, 6)
