import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    if len(S) != len(A) or len(S) != len(B) or len(observations) != len(A):
        raise ValueError('Input dimensions must be equal.')
    n = len(S)
    m = len(B[0])
    forward_probabilities = np.zeros((n, m))
    forward_probabilities[0] = S
    for observation in observations:
        if observation < 0 or observation >= m:
            return -1
        current_state_probabilities = forward_probabilities[0]
        for i in range(n):
            current_state_probabilities[i] *= A[i][observation]
        for j in range(m):
            current_state_probabilities[j] += B[current_state_probabilities[j]][observation]
        forward_probabilities[observation] = current_state_probabilities.sum()
    return round(forward_probabilities[-1], 4)
