def kmeans(X: np.ndarray, K: int, max_iter: int=100, random_state: int | None=None) -> list[list[float]]:
    if random_state is None:
        random_state = np.random.randint(0, 10000)
    if random_state is None:
        centroids = np.random.rand(K, X.shape[1])
    else:
        centroids = X[:K]
    for _ in range(max_iter):
        cluster_assignments = np.argmin(np.linalg.norm(X - centroids, axis=1), axis=1)
        new_centroids = np.zeros((K, X.shape[1]))
        for i in range(K):
            new_centroids[i] = np.mean(X[cluster_assignments == i], axis=0)
        if np.allclose(centroids, new_centroids):
            break
        centroids = new_centroids
    return [list(centroid) for centroid in sorted(centroids, key=lambda x: x[0], reverse=True)]
