def outcome_probs(transitions: List[Tuple[Any, Any, Any, Any]], state: Any, action: Any) -> List[List[float]]:
    next_states = set()
    for (state, action, reward, next_state) in transitions:
        next_states.add(next_state)
    probabilities = {next_state: 0 for next_state in next_states}
    for (state, action, reward, next_state) in transitions:
        if next_state == state:
            probabilities[next_state] += reward
    probabilities_rounded = {next_state: round(probability, 4) for (next_state, probability) in probabilities.items()}
    probabilities_sorted = sorted(probabilities_rounded.items(), key=lambda x: x[0])
    return probabilities_sorted
