from heapq import heappush, heappop
from typing import List, Tuple, Union
def find_shortest_path(grid: List[List[int]]) -> Union[int, List[Tuple[int, int]]]:
    if not grid or not grid[0]:
        return -1
    (rows, cols) = (len(grid), len(grid[0]))
    (start_row, start_col) = (0, 0)
    (goal_row, goal_col) = (rows - 1, cols - 1)
    pq = [(0, (start_row, start_col))]
    visited = set()
    while pq:
        (current_cost, (current_row, current_col)) = heappop(pq)
        if current_row == goal_row and current_col == goal_col:
            return [(current_row, current_col)]
        for (dr, dc) in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            (new_row, new_col) = (current_row + dr, current_col + dc)
            if 0 <= new_row < rows and 0 <= new_col < cols and ((new_row, new_col) not in visited):
                new_cost = current_cost + 1
                heappush(pq, (new_cost, (new_row, new_col)))
    return -1

import numpy as np
def generate_window(window: str, N: int, coefficients: list[float] | None=None) -> list[float]:
    if not isinstance(N, int) or N <= 0:
        raise ValueError('N must be a positive integer.')
    if window == 'hamming':
        return [0.54 - 0.46 * np.cos(2 * np.pi * i / (N - 1)) for i in range(N)]
    elif window == 'hann':
        return [0.5 - 0.5 * np.cos(2 * np.pi * i / (N - 1)) for i in range(N)]
    elif window == 'blackman_harris':
        (a0, a1, a2, a3) = (0.35875, 0.48829, 0.14128, 0.01168)
        return [a0 - a1 * np.cos(2 * np.pi * i / (N - 1)) + a2 * np.cos(4 * np.pi * i / (N - 1)) - a3 * np.cos(6 * np.pi * i / (N - 1)) for i in range(N)]
    elif window == 'generalized_cosine':
        coefficients = coefficients or [0.35875, 0.48829, 0.14128, 0.01168]
        return [np.sum(coef * np.cos(2 * np.pi * i / (N - 1))) for i in range(N)]
    else:
        raise ValueError(f'Unsupported window: {window}')

