import numpy as np
def pam_clustering(X: np.ndarray, k: int) -> list[int]:
    medoids = X[:k]
    while True:
        closest_non_medoids = []
        for sample in X:
            for medoid in medoids:
                if sample != medoid:
                    dist = np.linalg.norm(sample - medoid)
                    closest_non_medoids.append((dist, sample))
        closest_non_medoids.sort()
        if len(closest_non_medoids) == 0:
            break
        closest_non_medoid = closest_non_medoids.pop(0)
        medoids.append(closest_non_medoid[1])
    labels = [i for i in range(k)]
    for i in range(len(X)):
        labels[i] = np.argmin([np.linalg.norm(X[i] - medoid) for medoid in medoids])
    return labels
