import numpy as np
def analyze_bandit(payoffs: list[list[int | float]], payoff_probs: list[list[float]]) -> tuple[list[float], int, float]:
    if len(payoffs) != len(payoff_probs):
        return -1
    if not np.allclose(np.sum(payoff_probs, axis=1), 1, atol=1e-08):
        return -1
    expected_values = []
    best_arm = None
    best_ev = float('-inf')
    for (i, payoffs_row) in enumerate(payoffs):
        expected_payoff = np.mean(payoffs_row)
        expected_values.append(expected_payoff)
        if expected_payoff > best_ev:
            best_arm = i
            best_ev = expected_payoff
    return (expected_values, best_arm, best_ev)
