import math
def ucb1_select(ev_estimates: list[float], pull_counts: list[int], current_step: int, C: float=1.0) -> int:
    if not pull_counts:
        return 0
    best_arm_index = 0
    max_score = float('-inf')
    for (arm_index, count) in enumerate(pull_counts):
        if count == 0:
            continue
        score = ev_estimates[arm_index] + C * math.sqrt(2 * math.log(current_step + 1) / count)
        if score > max_score:
            max_score = score
            best_arm_index = arm_index
    return best_arm_index

import numpy as np
def gaussian_bandit_oracle(payoff_dists: list[tuple[float, float]], payoff_probs: list[float]) -> tuple[float, int] | int:
    if len(payoff_dists) != len(payoff_probs):
        return -1
    if any((not isinstance(val, tuple) or len(val) != 2 for val in payoff_dists)):
        return -1
    if any((not isinstance(val, float) or val <= 0 for val in payoff_probs)):
        return -1
    expected_rewards = []
    for (mu_k, sigma_k2) in payoff_dists:
        expected_reward = np.exp(mu_k * np.random.normal(0, sigma_k2))
        expected_rewards.append(expected_reward)
    best_ev = max(expected_rewards)
    best_arm = expected_rewards.index(best_ev)
    best_ev_rounded = round(best_ev, 4)
    best_arm_rounded = round(best_arm, 4)
    return (best_ev_rounded, best_arm_rounded)
