import numpy as np
def sigmoid(x):
    """Returns the sigmoid function element-wise."""
    return 1 / (1 + np.exp(-x))
def tanh(x):
    """Returns the hyperbolic tangent function element-wise."""
    return np.tanh(x)
def relu(x):
    """Returns the ReLU function element-wise."""
    return np.maximum(0, x)
def leaky_relu(x, alpha: float=0.01):
    """Returns the Leaky-ReLU function element-wise with slope *alpha* for x<0."""
    return np.maximum(0, x * (1 - alpha))
def softmax(x):
    """Computes the softmax function on a one-dimensional array."""
    exp_x = np.exp(x)
    sum_exp_x = np.sum(exp_x)
    if sum_exp_x == 0:
        return np.zeros_like(x)
    return exp_x / sum_exp_x
def get_activation(name):
    """Return the activation function by *name*.

    Args:
        name (str): One of {"sigmoid", "tanh", "relu", "leaky_relu", "softmax"}

    Returns:
        Callable: the requested activation function.

    Raises:
        ValueError: If *name* is not recognised.
    """
    if name not in ['sigmoid', 'tanh', 'relu', 'leaky_relu', 'softmax']:
        raise ValueError('Invalid activation function.')
    if name == 'softmax':
        return softmax
    elif name == 'sigmoid':
        return sigmoid
    elif name == 'tanh':
        return tanh
    elif name == 'relu':
        return relu
    elif name == 'leaky_relu':
        return leaky_relu
    else:
        raise ValueError('Invalid activation function.')

import numpy as np
def train_decision_stump(X: np.ndarray, y: np.ndarray, sample_weights: np.ndarray) -> dict:

    def get_best_threshold(X, y, sample_weights):
        best_threshold = None
        best_polarity = None
        min_error = float('inf')
        for i in range(X.shape[1]):
            for threshold in np.linspace(0, X[:, i].max() + 1, num=100):
                predictions = np.sign(X[:, i] - threshold)
                weighted_error = np.sum(predictions != y) / sample_weights.sum()
                if weighted_error < min_error:
                    min_error = weighted_error
                    best_threshold = threshold
                    best_polarity = predictions[1]
        return {'feature_index': i, 'threshold': best_threshold, 'polarity': best_polarity, 'weighted_error': min_error}
    tree = {}
    for feature_index in range(X.shape[1]):
        best_threshold = get_best_threshold(X[:, feature_index], y, sample_weights)
        if best_threshold is not None:
            tree[feature_index] = {'threshold': best_threshold, 'left': {}, 'right': {}}
        if best_threshold > 0:
            left_tree = train_decision_stump(X[:, feature_index], y, sample_weights)
            tree[feature_index]['left'] = left_tree
        if best_threshold < 0:
            right_tree = train_decision_stump(X[:, feature_index], y, sample_weights)
            tree[feature_index]['right'] = right_tree
    return tree
