import numpy as np
def pca_transform(X: np.ndarray, n_components: int) -> list[list[float]]:
    if not 1 <= n_components <= X.shape[1]:
        return -1
    Σ = np.cov(X, rowvar=False)
    (eigenvalues, eigenvectors) = np.linalg.eig(Σ)
    (sorted_eigenvalues, sorted_eigenvectors) = np.sort(eigenvalues, axis=0, reverse=True)
    principal_components = sorted_eigenvectors[:n_components]
    transformed_X = X @ principal_components
    return transformed_X.tolist()

