import math
from collections import Counter, defaultdict
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    tokens = query.split()
    padded_tokens = []
    for token in tokens:
        padded_tokens.extend([f'<bol>{token}</bol>', f'<eol>'])
    ngram_counts = defaultdict(int)
    for i in range(len(padded_tokens) - N + 1):
        ngram = tuple(padded_tokens[i:i + N])
        ngram_counts[ngram] += 1
    total_count = sum(ngram_counts.values())
    if total_count == 0:
        return float('-inf')
    mle_probabilities = []
    for (ngram, count) in ngram_counts.items():
        log_prob = math.log(count / total_count)
        mle_probabilities.append(log_prob)
    total_log_prob = sum(mle_probabilities)
    return round(total_log_prob, 4)
