def noam_learning_rate(step: int, model_dim: int=512, scale_factor: float=1.0, warmup_steps: int=4000) -> float:
    """Return the learning-rate given by the Noam schedule.

    The function must follow the formula described in the task description and
    round the result to 9 decimal places.
    """
    if step < warmup_steps:
        return 0.0
    else:
        learning_rate = scale_factor * model_dim ** (-0.5) * min(step ** (-0.5), step * warmup_steps ** (-1.5))
        return round(learning_rate, 9)

import numpy as np
def get_initializer(name: str):
    """Return a weight-initialisation callable associated with *name*.

    The callable must have the signature
        initializer(shape: tuple[int, ...], scale: float = 1.0) -> np.ndarray
    and create a numpy array filled according to the requested strategy.

    Supported strategies (case-insensitive):
        - "zeros"
        - "ones"
        - "normal"
        - "glorot_uniform"

    If *name* is not recognised the function must raise `ValueError`.
    """
    if name.lower() not in ['zeros', 'ones', 'normal', 'glorot_uniform']:
        raise ValueError('Unsupported initialisation strategy')
    if name.lower() == 'zeros':
        return np.zeros(shape)
    elif name.lower() == 'ones':
        return np.ones(shape)
    elif name.lower() == 'normal':
        return np.random.normal(loc=0, scale=1, size=shape)
    elif name.lower() == 'glorot_uniform':
        limit = scale * np.sqrt(6 / (shape[0] + shape[1]))
        weights = np.random.uniform(low=-limit, high=limit, size=shape)
        return weights
    else:
        raise ValueError('Unsupported initialisation strategy')

import heapq
def huffman_encode(tokens: list[str]) -> dict[str, str]:
    if not tokens:
        return {}
    min_heap = []
    for token in tokens:
        heapq.heappush(min_heap, (-len(token), token))
    while len(min_heap) > 1:
        (freq1, token1) = heapq.heappop(min_heap)
        (freq2, token2) = heapq.heappop(min_heap)
        parent_key = token1 + token2
        parent = {'key': parent_key, 'freq': freq1 + freq2}
        heapq.heappush(min_heap, (parent['freq'], parent['key']))
    code_dict = {}
    current_node = min_heap[0]['key']
    while current_node:
        if current_node in code_dict:
            break
        code_dict[current_node] = '0' if current_node % 2 == 0 else '1'
        current_node = heapq.heappop(min_heap)[1]
    return code_dict
